INSERT INTO `conteudo` VALUES (12, 'CIC lança sua nova marca comemorativa aos 90 Anos', '2009-03-12', '\n\n<p align="left">\n	<span style="FONT-SIZE: 10pt"></span></p>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt">\n		<img height="97" alt="" src="/site/img/banco/CIC 90Anos.jpg" width="340" align="top" border="0" /></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt">O CIC inicia a comemoração de seus 90 anos de cara nova. Desenvolvida pelo empresário e design gráfico Umehara Parente, a nova logomarca do CIC apresenta um conceito moderno e arrojado, características inerentes&nbsp;da entidade. Dentro desta parceris, o novo conceito&nbsp;abrange&nbsp;todas as peças gráficas,&nbsp;o novo cenário para o programa Ciclo de debates e o design do site, este, brilhantemente executado pelo parceiro Grupo Fortes.</span></div>', '', 'EP', '', '');
INSERT INTO `conteudo` VALUES (13, 'Tasso inaugura novo cenário do programa Ciclo de Debates', '2009-03-01', '\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt">\n		<img style="MARGIN-BOTTOM: 10px" height="223" alt="" src="/site/img/banco/Fotos Tasso.jpg" width="340" align="top" border="0" /></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt">Para&nbsp;inaugurar o novo cenário do programa Ciclo de Debaes, o CIC&nbsp;recebeu seu ex-presidente e&nbsp;Senador da República Tasso Jereissati. Em uma entrevista descontraida, Tasso abordou o cenário econômico atual.</span></div>', '', 'EP', '', '');
INSERT INTO `conteudo` VALUES (16, 'Presidente da Allianz Seguros no Brasil fala sobre Crise Financeira', '2009-03-23', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt">\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Verdana">\n		\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt" align="justify">\n			<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; COLOR: windowtext; mso-bidi-font-size: 11.0pt; mso-bidi-font-style: normal">O presidente da Allianz Seguros no Brasil, Max Thiermann, estará na Casa da Indústria, para um almoço com os associados do Centro Industrial do Ceará, nesta terça-feira, 24, às 12:45, no Salão VIP  cobertura. Na ocasião, ele falará sobre a Crise Financeira Internacional.</span></div>\n		\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt" align="justify">\n			<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; COLOR: windowtext; mso-bidi-font-size: 11.0pt; mso-bidi-font-style: normal">\n				<o:p>&nbsp; \n					\n<div class="MsoNormal" style="MARGIN: 0cm 0cm 0pt" align="justify">\n						<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt">\n							<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; COLOR: windowtext; mso-bidi-font-size: 11.0pt; mso-bidi-font-style: normal">A Allianz Seguros no Brasil (ex AGF Seguros do Brasil) atua no país há 105 anos, mantém 60 filiais e 1400 funcionários. Pertence ao Grupo Allianz SE, um dos líderes mundiais em seguros e o maior da Europa, com 180 mil funcionários que atendem cerca de 80 milhões de clientes em mais de 70 países. Além de oferecer produtos e serviços, a Allianz também se destaca na área de pesquisa de grandes riscos, estudos de sustentabilidade e nos investimentos em fontes renováveis de energia.</span></span>\n						<o:p>\n							<o:p>\n								<o:p>\n									<o:p>\n										<o:p>\n											<o:p>\n												<o:p>\n													<o:p>\n														<o:p>\n															<o:p>\n																<o:p>\n																	<o:p>\n																		<o:p>\n																			<o:p>\n																				<o:p>\n																					<o:p>\n																						<o:p>\n																							<o:p>\n																								<o:p>\n																									<o:p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></p></div></p></span>\n			<o:p>\n				<o:p>\n					<o:p>\n						<o:p>\n							<o:p>\n								<o:p>\n									<o:p>\n										<o:p>\n											<o:p>\n												<o:p></p></p></p></p></p></p></p></p></p></p></div></span></span>', '', 'EP', '', '');
INSERT INTO `conteudo` VALUES (17, 'Pela modernização', '2009-03-27', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O presidente do Tribunal de Justiça do Estado do Ceará, Ernani Barreira, é o convidado do Ciclo de Debates do último domingo de março. Ao lado do diretor do Centro Industrial do Ceará, Maia Júnior, e do jornalista Luís Sérgio Santos, ele falará sobre a modernização administrativa do Poder Judiciário. O programa irá ao ar às 19 horas, pela TVC.</span></p>', '', 'CE', 'Diário do Nordeste - Coluna Lêda Maria', 'http://diariodonordeste.globo.com/materia.asp?codigo=625674');
INSERT INTO `conteudo` VALUES (19, 'Deve o Senado ter suas atribuições revistas?', '2009-03-29', '\n\n<p>Assunto da semana&nbsp; </p>\n\n<p>Deve o Senado ter suas atribuições revistas? </p>\n\n<p>Cresce o clamor para que o Senado adote maior racionalidade administrativa interna. Mas, está de volta também a discussão sobre a hipertrofia do próprio papel da instituição. Especialistas defendem que o Senado deve-se ater à representação de cada Estado-membro dentro da Federação, à discussão do orçamento, à nomeação do presidente do Banco Central e à política externa, deixando de ser câmara revisora e de sobrepor-se à Câmara dos Deputados (detentora da representação popular). Outra exigência é que o tempo de mandato do senador coincida com o do deputado federal. </p>\n\n<p>REFORMA<br />\n	&nbsp;<br />\n	"As sucessivas crises do Congresso Nacional, indicam o esgotamento do modelo de representação brasileiro e a necessidade urgente de sua reforma. Extinto fosse o Senado Federal não faria hoje a menor falta, a instituição faz algum tempo que não dá ao País uma contribuição relevante. Espaço privilegiado de atuação de caciques brasileiros, assistimos àqueles que deveriam representar moral e eticamente seus Estados, serem sistemática e repetidamente envolvidos em escândalos. E tudo estar a indicar que o que veio a tona é apenas a porção mais visível de um iceberg, que por seu tamanho, é capaz de lançar em profundidade abissal o conceito de uma instituição que naufraga a olhos vistos". </p>\n\n<p>FRANCISCO MOREIRA RIBEIRO <br />\n	Cientista Político. Professor do Nudoc-UFC e Unifor </p>\n\n<p>AUTORITARISMO<br />\n	&nbsp;<br />\n	"A proposta de redução das atribuições do Senado tem viés autoritário. Exemplo notório de esfacelamento do Senado, de terríveis consequências para a vida nacional, deu-se no Estado Novo, com a Constituição de 1937 e a substituição do Senado pelo "Conselho Federal", sob o jugo do Executivo. O Senado é instituição que decorre da forma federativa de estado, cláusula pétrea da Carta Magna de 1988. A crise de credibilidade por que passa não justifica o surgimento de falsas soluções". </p>\n\n<p>REPRESENTAÇÃO <br />\n	HÉLIO LEITÃO <br />\n	Presidente da OAB-CE </p>\n\n<p>REPRESENTAÇÃO </p>\n\n<p>"Vivemos hoje na política uma crise de representação. O escândalo que envolve o Senado Federal é apenas um exemplo do que ocorre em todas as instituições que representam os mais altos poderes do país. Atribuições dos parlamentares e tempo de mandato são análises pontuais. É preciso olhar os problemas que envolvem o Congresso Nacional (não podemos excluir a Câmara dos Deputados) como um todo. Hoje, os interesses políticos deram lugar aos ganhos particulares. E o mais grave: a sociedade, embora indignada, não mais se manifesta contra os descalabros cometidos pelos parlamentares e nem parece estar atenta na escolha por representantes que tenham verdadeiro espírito público e que poderiam corrigir as imperfeições das instituições que representam". <br />\n	ROBINSON DE CASTRO E SILVA <br />\n	Presidente do Centro Industrial do Ceará - CIC </p>\n\n<p>EQUILÍBRIO<br />\n	&nbsp;<br />\n	"Os atuais "escândalos" que afetam a imagem do Senado não podem importar o amesquinhamento das atribuições daquela Casa. Ela é imprescindível para que se mantenha, inclusive quanto à produção de leis e de emendas à Constituição, o equilíbrio entre as unidades federadas. Se se mantiver apenas a Câmara dos Deputados, São Paulo, Rio e Minas dominarão a vontade legislativa. Deve-se manter a duração atual dos mandatos, reduzindo-se a quantidade de senadores (dois por Estado) e de deputados federais".<br />\n	VALMIR PONTES FILHO <br />\n	Professor de Direito </p>\n\n<p>AGILIZAÇÃO<br />\n	&nbsp;<br />\n	"O Brasil é um país que tem pressa. Grandes questões pedem soluções urgentes, até porque são gritantes os problemas em alguns setores fundamentais para o crescimento do País e para o resgate de sua dívida social. Ao mesmo tempo, é notória a lentidão com que os poderes públicos trabalham. A máquina administrativa é grande e emperrada. Assim, tudo o que puder ser feito para agilizar a pauta do Senado, sem comprometimento de suas atribuições constitucionais, será muito bem recebido pela sociedade brasileira". <br />\n	JESUALDO PEREIRA FARIAS <br />\n	Reitor da UFC </p>\n\n<p>PRESSÃO<br />\n	&nbsp;<br />\n	"Implementar ações racionalizadoras no processo administrativo, rever as atribuições do Senado, reduzir o número de senadores, alterar o tempo do mandato para coincidir com o deputado federal e acabar com a figura do suplente ("senador" sem voto), tudo isso é necessário, porém insuficiente. A existência do Senado só é validada se tiver como objetivo maior a preocupação com a Virtude e com a ética na política. Só assim poderá contribuir para a conquista da felicidade e do bem estar para todos os brasileiros. Entretanto, isto só ocorrerá com forte pressão popular".<br />\n	ALBERTO TEIXEIRA <br />\n	Escola de Governantes</p>', '', 'CE', 'O Povo - Opinião - Assunto da Semana', 'http://www.opovo.com.br/opovo/opiniao/866081.html');
INSERT INTO `conteudo` VALUES (20, 'Telinha', '2009-03-29', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">TELINHA</p>\n	\n<p align="justify">Quem pinta no Ciclo de Debates da TVC, esta noite (às 7), é o presidente do Tribunal de Justiça-CE, Ernani Barreira, abordando a modernização do poder Judiciário. Do programa (reprise às 22 horas, do dia 3) tomam parte o jornalista Luís Sérgio Santos e o diretor do CIC, Maia Júnior. </p></span>', '', 'CE', 'O Povo - Caderno Vida & Arte - Coluna Sônia Pinheiro', 'http://www.opovo.com.br/opovo/colunas/soniapinheiro/');
INSERT INTO `conteudo` VALUES (21, 'Vamos ensinar a pescar', '2009-03-30', '\n\n<p align="justify"><a href="http://www.cic.com.br/admin/conteudo/l">\n		<span style="FONT-SIZE: 12pt; COLOR: #0000ff; FONT-FAMILY: Arial">Vamos ensinar a pescar</span> \n		<span style="FONT-SIZE: 12pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 12pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span>\n			<span style="FONT-SIZE: 12pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></span></a></p>\n\n<div align="justify">\n	\n<p>\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Marcondes rosa de sousa<br />\n			</span></p></div>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No Ceará (é bordão em meus artigos), economia e política alternam-se, como nossos rios, em cheias e secas. Exemplos recentes entre nós, a União pelo Ceará, com Virgílio Távora, a superar as velhas rixas entre PSD e UDN. Em 1984, Celso Furtado, voltado do exílio político, dava-nos conta, nos Encontros Culturais da UFC de que os ciclos, no País, vêm e se vão de 15 a 18 anos, daí nascendo, o "projeto das mudanças" liderado pelos à época "jovens empresários do CIC", à frente Tasso Jereissati. Agora, um desses "jovens empresários", por alguns chamado "o Samurai do Cambeba" e hoje a levar à frente, na presidência da Transpetro, os sonhos do Barão de Mauá, em nossa indústria naval, adverte-nos para a chegada de novo ciclo  político e econômico - entre nós. Sérgio Machado aponta-nos estratégicas presenças de cearenses na vida política e social, no cenário nacional, que poderiam, sob o diapasão do olhar mais alto, re/unir, num "Partido do Ceará", forças nossas hoje em desperdício, nos poderes executivo, legislativo e judiciário do País. <br />\n		<br />\n		Em \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O POVO</span>, Sérgio Machado nos diz que sua intenção "é explicitar as características do nosso mercado e caminhos do treinamento e do crédito. Não vamos dar o peixe, mas ensinar a pescar". De minha parte, sou partidário, sim, do "dar de comer a quem tem fome". Mas insisto: "... uma esmola a um homem que é são ou lhe mata de vergonha ou vicia o cidadão" (Zé Dantas e Luiz Gonzaga). E, não mais que Pero Vaz de Caminha, nessa jornada, ecoa-me a lição que, no Auditório Castelo Branco (UFC), deixou-nos Celso Furtado: "Quando o projeto social dá prioridade à efetiva melhoria das condições de vida da maioria da população, o crescimento se metamorfoseia em desenvolvimento". <br />\n		<br />\n		Esse, o horizonte de um desenvolvimento sustentável. É a esperança de todos nós! <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Marcondes rosa de sousa</span> - Professor da UFC e da UECE </span>\n	<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"><a href="mailto:marcondesrosa@gmail.com">marcondesrosa@gmail.com</a></span></p>', '', 'CE', 'jornal O Povo - Opinião', 'http://www.opovo.com.br/opovo/opiniao/866362.html');
INSERT INTO `conteudo` VALUES (22, 'Produção industrial caiu 17% desde agravamento da crise', '2009-04-01', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">RIO - A produção industrial brasileira caiu 16,9% entre setembro e fevereiro. Setembro foi quando a crise econômica global agravou-se e também o mês em que a indústria brasileira bateu recorde histórico.<br />\n		<br />\n		Os dados são do Instituto Brasileiro de Geografia e Estatística (IBGE), que divulgou hoje a Produção Industrial Mensal - Produção Física (PIM-PF) de fevereiro.<br />\n		</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com o coordenador de indústria do IBGE, Silvio Sales, o resultado mostra uma recuperação no primeiro bimestre, uma vez que em dezembro esse tombo era de 20,1%.<br />\n		<br />\n		A recuperação é fruto de dois meses de alta na margem, apesar das sucessivas baixas frente aos resultados de 2008. Em janeiro a produção industrial cresceu 2,1% frente a dezembro, enquanto em fevereiro a alta foi de 1,8% na comparação com janeiro.<br />\n		<br />\n		"A recuperação na margem não foi suficiente para compensar as quedas dos meses anteriores. Excetuando-se os resultados da ponta, houve queda da produção nos outros tipos de comparação " , disse Sales.<br />\n		<br />\n		O recuo de 17,2% da produção industrial no primeiro bimestre em relação aos dois primeiros meses de 2008 provocou a primeira queda do acumulado pela indústria em 12 meses desde setembro de 2002, quando o acumulado em 12 meses diminuiu 0,4%. A produção industrial fechou fevereiro com baixa acumulada em 12 meses de 1%.<br />\n		<br />\n		Das quatro categorias de uso analisadas pelo IBGE, apenas os bens de capital se mantêm no terreno positivo entre março de 2008 e fevereiro de 2009, com aumento de 8,1% na produção na comparação com os 12 meses imediatamente anteriores.<br />\n		<br />\n		Segundo Sales, esse resultado é fruto da demora da produção de bens de capital a apontar trajetória de queda logo nos primeiros meses da crise financeira internacional.<br />\n		<br />\n		" A queda do acumulado em 12 meses é menor porque ainda reflete um pouco da situação existente antes da crise internacional " , ressaltou o economista.<br />\n		<br />\n		As maiores quedas da produção em 12 meses foram registradas por bens intermediários e bens de consumo duráveis, ambos com baixa de 3,2% frente aos 12 meses imediatamente anteriores. Os bens de consumo semiduráveis e não-duráveis tiveram baixa de apenas 0,1% nesta comparação. <br />\n		<br />\n		(Rafael Rosas | Valor Online) </span></p>', '', 'EP', 'Uol - Economia - Úlitmas Notícias', 'http://economia.uol.com.br/ultnot/valor/2009/04/01/ult1913u104394.jhtm');
INSERT INTO `conteudo` VALUES (23, 'G20 deve aprovar US$ 1 trilhão para socorrer economia, diz Mantega ', '2009-04-01', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">01/04/2009 - 16h08 </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">G20 deve aprovar US$ 1 trilhão para socorrer economia, diz Mantega </p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">da \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Agência Brasil</span><br />\n		da \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Efe</span>, em Londres </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O ministro da Fazenda, Guido Mantega, disse nesta quarta-feira que o G20 (grupo que reúne as principais potências e países emergentes) deve aprovar na reunião de amanhã, em Londres, a criação de um fundo internacional de US$ 1 trilhão para socorrer a economia mundial. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Desse total, segundo o ministro, US$ 200 bilhões devem ser empregados em financiamentos para o comércio internacional. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com Mantega, o Japão deve emprestar US$ 100 bilhões para o fundo. A mesma quantia deve ser repassada pela União Europeia. Já a Noruega deve destinar US$ 48 bilhões. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O ministro fez a declaração durante viagem de trem da comitiva presidencial de Paris (França) a Londres (Inglaterra), sob o Canal da Mancha. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">FMI</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Durante o percurso, Lula também afirmou que o Brasil está </span><a href="http://www1.folha.uol.com.br/folha/dinheiro/ult91u544049.shtml">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">disposto a reforçar o FMI</span>\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">(Fundo Monetário Internacional). "Se for necessário dar dinheiro (ao FMI) e se isso não diminuir nossas reservas, não vemos problema algum", disse. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Consultado sobre a nova linha de crédito de US$ 47 bilhões que o </span><a href="http://www1.folha.uol.com.br/folha/dinheiro/ult91u544021.shtml">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">México solicitou ao Fundo</span>\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, o presidente esclareceu que o "Brasil não precisa [de uma linha de crédito do FMI] porque tem reservas suficientes." </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Mantega acrescentou que é preciso discutir ainda a quem o FMI emprestará dinheiro e sob que condições o Brasil concederá esses fundos. O Brasil acha que esses recursos devem ser concedidos preferencialmente aos países emergentes. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Estratégia comum</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Lula se encontrou com o presidente da França, Nicolas Sarkozy, antes de deixar o país rumo ao Reino Unido. Eles </span><a href="http://www1.folha.uol.com.br/folha/dinheiro/ult91u544029.shtml">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">afinaram o discurso para o G20</span>\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">e disseram o encontro de amanhã, entre chefes de Estado, tem a responsabilidade de dar uma resposta efetiva à atual crise econômica. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Sarkozy, que nos últimos dias ameaçou não assinar a declaração final do encontro caso não haja uma proposta objetiva de mudança no sistema financeiro mundial, disse que vê sintonia entre as expectativas dos dois países para a reunião. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Nós queremos que o mundo avance, e se unirmos nossas vozes, se avançarmos juntos, somos mais fortes", disse o presidente francês. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O presidente brasileiro defendeu a reforma das instituições multilaterais, como o FMI, e criticou a atual desregulação do sistema financeiro mundial. E ressaltou a importância de os investimentos no sistema financeiro estarem vinculados à produção e à geração de emprego e renda.</span></p>', '', 'EP', 'Folha On line', 'http://www1.folha.uol.com.br/folha/dinheiro/ult91u544110.shtml');
INSERT INTO `conteudo` VALUES (24, 'China dobra participação nas exportações do Brasil e ajuda balança comercial', '2009-04-01', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">da \n			<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha Online</span>, em Brasília </span></p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">da \n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha Online</span>, em Brasília </span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A China dobrou a sua participação nas exportações brasileiras no primeiro trimestre de 2009. Segundo dados do Ministério do Desenvolvimento, o país asiático respondeu por 11% das vendas do Brasil para o exterior. No mesmo período 2008, a participação era de 5,4%. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Nos três primeiros meses do ano, as exportações para a China aumentaram 63%, para US$ 3,4 bilhões. Em um momento de desaceleração do comércio mundial, devido à crise econômica, esse movimento foi o inverso do registrado em relação a outros países. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Nesse período, o Brasil vendeu 38% a menos para os EUA, 44% menos para a Argentina e teve queda de 22% nos embarques para a União Européia. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Embora tenha vendido mais para a China, o Brasil comprou menos produtos do seu principal parceiro comercial asiático. Houve uma queda de 13% nas importações de produtos chineses, para US$ 3,6 bilhões. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Considerando apenas o mês de março, o Brasil voltou a registrar superávit comercial com a China. No mês passado, o valor das exportações superou o valor das importações em US$ 508 milhões. É a primeira vez que isso acontece desde setembro de 2008. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para se ter uma idéia dessa virada, em março de 2008, as importações da China superaram as exportações em US$ 610 milhões. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Sem a China, as exportações seriam menores e poderia até haver déficit [na balança brasileira]", disse o secretário de Comércio Exterior, Welber Barral. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Trimestre</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O Ministério do Desenvolvimento prevê uma queda de 20% nas exportações em 2009 na comparação com o ano passado. Se confirmada, essa será a primeira queda nas exportações desde 1999. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No primeiro trimestre, as exportações brasileiras registraram recuo de quase 20% em relação ao mesmo intervalo no ano passado, para US$ 31,177 bilhões. No período, o superávit da balança comercial ficou em US$ 3,012 bilhões. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A queda nas exportações brasileiras no primeiro trimestre ficou praticamente no mesmo nível de países como EUA (-21,5%), China (-21,1%) e Índia (-18,9%). Tiveram resultados piores a Coreia do Sul (-25,1%), Argentina (-30,4%), México (-31,5%) e Rússia (-43%).</span></p>', '', 'EP', 'Folha On line - Dinheiro', 'http://www1.folha.uol.com.br/folha/dinheiro/ult91u544080.shtml');
INSERT INTO `conteudo` VALUES (25, 'Governo prevê exportação 20% menor em 2009, de US$ 160 bilhões ', '2009-04-01', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">BRASÍLIA - As exportações brasileiras deverão recuar 20% em 2009 por causa da crise global e fechar ao redor de US$ 160 bilhões. Será o primeiro recuo anual das exportações em dez anos, segundo informou o Ministério do Desenvolvimento, Indústria e Comércio Exterior (Mdic).<br />\n		<br />\n		As vendas externas passaram a registrar trajetória crescente e consecutiva desde 1999. Apesar da queda, o governo ainda vê a situação brasileira bem melhor do que a de vários parceiros comerciais, beneficiado por alta nas compras da Ásia e África.<br />\n		"Ainda não é a meta, é apenas uma previsão", explicou o secretário de Comércio Exterior, Welber Barral. "A meta de exportações é sempre algo que esperamos conquistar a mais do que a previsão", continuou ele.<br />\n		<br />\n		Barral destacou que o Mdic tem tido maior taxa de acerto sobre as metas do que as previsões do mercado financeiro, por exemplo, ou de outros órgãos do próprio governo. A previsão de agora, por exemplo, é de retração de 20% sobre o montante apurado em 2008, quando as exportações subiram 23% e atingiram US$ 197,9 bilhões.<br />\n		<br />\n		O secretário lembrou que ano passado, o Mdic fixou inicialmente uma meta de US$ 172 bilhões, passou a US$ 180 bilhões e acabou em US$ 200 bilhões, bem próximo ao registrado. A previsão de US$ 160 bilhões para este ano se aproxima do que prevê o BC, cuja revisão nas projeções em março situou as exportações em US$ 158 bilhões.<br />\n		<br />\n		Dados do Mdic mostraram hoje que o país fechou o primeiro trimestre com um saldo comercial de US$ 3,012 bilhões, 9,1% superior ao registrado no mesmo intervalo de 2008 (US$ 2,761 bilhões). Barral destacou que a queda na atividade interna e a alta na cotação do dólar contribuiram para redução nas importações. As compras no exterior recuaram mais do que as exportações.<br />\n		</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No trimestre, as importações caíram 21,6% ante janeiro a março de 2008, somando US$ 28,165 bilhões. E as exportações acumularam US$ 31,177 bilhões, com queda de 19,4%.<br />\n		<br />\n		Barral comentou que os básicos (commodities) "salvaram" as exportações, com alta de 1,7% no trimestre, enquanto manufaturados recuaram 29,1% e semimanufatruados tiveram baixa de 22,9%, sempre na comparação com mesmo periodo de 2008.<br />\n		<br />\n		"Mercados como países asiáticos e da África fizeram a diferença ao comprarem mais do Brasil", citou Barral. E tais compras foram basicamente de commodities.<br />\n		<br />\n		A China, por exemplo, aumentou em 62,7% as importações brasileiras no trimestre, comprando mais minério de ferro, soja, aviões e açúcar. Tanto que em março o Brasil registrou superávit de US$ 508 milhões com a China, o que não ocorria desde setembro de 2008, quando as vendas brasileiras foram maiores que as importações chinesas em US$ 69 milhões.<br />\n		<br />\n		Barral afirmou ainda que, a despeito da crise global e da falta acentuada de crédito para o comércio mundial, "no Brasil a tendência é se repetir o que ocorreu no início do ano, com queda menor das exportações na comparação com outros países".<br />\n		<br />\n		Ele citou ainda que, enquanto as exportações brasileiras recuaram 19,4% no primeiro trimestre do ano sobre o mesmo período de 2008, as vendas externas da Rússia cederam 43%; as do México recuaram 31,5%; na Argentina a queda foi de 30,4%; nos Estados Unidos caíram 21,5% e na Coreia do Sul foram 25,1% menores.<br />\n		<br />\n		(Azelma Rodrigues | Valor Online)</span></p>', '', 'EP', 'Uol - Valor On line', 'http://economia.uol.com.br/ultnot/valor/2009/04/01/ult1913u104420.jhtm');
INSERT INTO `conteudo` VALUES (26, 'Tasso paga avião fretado com dinheiro do Senado ', '2009-04-02', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Desde 2005, quase R$ 500 mil foram usados pelo tucano, que diz ter autorização especial<br />\n			<br />\n			Ex-presidente do PSDB, que tem avião próprio, afirma que aproveita a verba não utilizada de passagens aéreas para fretar jatos</span></p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">FERNANDO RODRIGUES</span><br />\n			\n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">FÁBIO ZANINI</span></span> \n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></span></span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA SUCURSAL DE BRASÍLIA</span> </span></span></p>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O senador Tasso Jereissati (PSDB-CE) tem o hábito de usar parte de sua verba oficial de passagens aéreas para fretar jatinhos que são pagos com recursos do Senado. O ato da direção da Casa que regula o benefício não permite esse tipo de procedimento, mas o tucano diz ter obtido autorização especial para fazer as suas viagens.<br />\n		Entre 2005 e 2007, Tasso gastou R$ 335 mil. Depois, as despesas foram publicadas sem registro de seu nome. De lá para cá, foram mais R$ 134 mil, totalizando R$ 469 mil, segundo o Siafi (sistema de acompanhamento do Orçamento).</span></div>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<div align="justify"><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O senador confirmou à \n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span> que foi usuário de jatinhos fretados e bancados com o dinheiro do Senado nos últimos quatro anos, mas enviou documentos em que assume gastos menores: R$ 358 mil. Tasso tem o seu próprio avião, um jato Citation. Ele afirma que recorre a fretamentos quando o seu está indisponível.<br />\n			Ele diz que a autorização foi obtida após o envio de ofícios para o então diretor-geral da Casa, Agaciel Maia. As brechas foram autorizadas pessoalmente pelo primeiro-secretário da Casa entre 2005 e 2008, Efraim Morais (DEM-PB), sem consulta à Mesa Diretora.</span></div>\n	\n<div align="justify"><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Há dois meses, o Senado enfrenta acusações em série contra congressistas e diretores. Agaciel foi o primeiro a cair, após a \n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span> revelar que ele mora numa casa de alto valor não declarada em Brasília. O jornal também mostrou que servidores receberam hora extra em janeiro, quando a Casa estava em recesso.<br />\n			Tasso diz que aproveita o saldo de passagens não usadas para fretar jatos. Por mês, ele tem direito a R$ 21.230, o que daria para voar nove vezes entre Brasília e Fortaleza, pela tarifa mais cara da TAM (R$ 2.379).</span></div>\n	\n<div align="justify"><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O senador afirma que em 2005 e 2006 o uso de jatos fretados foi alto (há nove registros de pagamento) em parte porque na época ele presidia o PSDB. Admite, assim, ter usado a verba de passagens do Senado para viagens partidárias.</span></div>\n	\n<div align="justify"><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Foram pelo menos 16 pagamentos feitos pelo Senado desde 2005. A ONG Contas Abertas, especialista em analisar o Orçamento, fez pesquisa em todas essas despesas. Tasso só aluga jatinhos da empresa TAM. Nem sempre há a identificação dos trechos voados nem se os valores pagos se referem a uma ou a mais viagens.</span></div></span>', '\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Apesar de ele ser do Ceará, em três oportunidades os pagamentos do Senado foram para que o tucano viajasse no trecho "São Paulo-Rio-São Paulo". </span></div>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Não há uma tabela de preços para os chamados voos executivos no mercado. As empresas costumam fazer preços especiais para viajantes frequentes. Também depende do número de assentos do aparelho escolhido. Em geral, um voo de ida e volta de São Paulo ao Rio varia de R$ 15 mil a R$ 25 mil.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Não é conhecido o uso que todos os 81 senadores fazem de suas cotas de passagens aéreas -cinco por mês. É expressamente proibido dar dinheiro para os senadores viajarem aos seus Estados. O ato que normatizou as passagens, de 1988, determina que fica "extinta a ajuda de custo paga aos senadores para transporte aéreo".</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Há uma coincidência no caso de Tasso usar jatinhos pagos pelo Senado a partir de 2005. Foi nesse ano que o senador comprou seu jato, cuja cotação à época era de US$ 3 milhões.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Segundo Tasso, o aluguel de jatinhos fretados ocorre porque, às vezes, o seu está em revisão. O tucano nega que possa ter usado o dinheiro de sua verba de passagens para comprar combustível para seu avião. As notas fiscais que apresenta são sempre de fretamento de aeronaves da empresa TAM.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Outra coincidência é o fato de o nome de Tasso ter sumido dos controles do Siafi nas ordens de pagamento de jatinhos fretados no período em que começaram a surgir rumores de que ele comprava combustível de avião com as verbas de bilhetes aéreos do Senado. O tucano nega ter pedido que seu nome não aparecesse.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em 2005, 2006 e em parte de 2007, o sistema orçamentário sempre menciona da seguinte forma os desembolsos a favor da TAM: "Pagamento da NF [nota fiscal] ref. ao fretamento de uma aeronave pelo senador Tasso Jereissati".</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A partir de julho de 2007, a descrição muda: "Pagamento da NF ref. ao fretamento de aeronave pelo senador". Não aparece mais o nome do congressista -mas trata-se de Tasso, como o próprio tucano reconheceu ontem à \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span>.</span></div>\n<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">\n		\n<p align="justify">outro lado</p></span>\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">\n			<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Tucano diz que Senado liberou uso de verba</span> \n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial"></span>\n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial"></span></span></p>\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">\n		\n<p align="justify">DA SUCURSAL DE BRASÍLIA</p></span>\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial"></span></p>\n	\n<div align="justify">\n		<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">O senador Tasso Jereissati justificou o uso de parte de sua verba de passagens em 2005 e 2006 dizendo que fretou jatos para compromissos como presidente do PSDB, cargo que ocupava à época.</span></div>\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">\n		\n<div align="justify"><br />\n			"Às vezes é difícil separar a agenda de compromissos partidários dos compromissos de senador. Ocorria de eu sair de Brasília, passar em São Paulo para um evento partidário e seguir depois para Fortaleza", afirma ele.</div>\n		\n<div align="justify"><br />\n			Tasso diz que sempre teve autorização da Casa para aproveitar o saldo das passagens não utilizadas. "Eu só sabia que era legal. Sabia que tinha um saldo e pedia o fretamento."</div>\n		\n<div align="justify"><br />\n			O tucano diz que raramente utiliza todo o crédito para passagens do Senado a que tem direito mensalmente (R$ 21.230). "Às vezes tenho uma emergência e meu avião, por algum motivo, está indisponível. Eu recorro a um fretamento", disse. "Moro longe, num Estado de fora do eixo Rio-São Paulo, com uma frequencia menor de voos comerciais."</div>\n		\n<div align="justify"><br />\n			Tasso disse que mesmo assim usa eventualmente voos de carreira. "Na semana passada usei um."</div>\n		\n<div align="justify"><br />\n			Segundo o diretor-geral do Senado, Alexandre Gazineo, o ato da Mesa Diretora de 1988 que estabelece as cotas de passagens aéreas é "omisso" quanto à possibilidade de fretamento. Assim, poderia haver autorização ou não.</div>\n		\n<div align="justify"><br />\n			Segundo ele, a decisão de autorizar a conversão das passagens em crédito para fretamento é do primeiro-secretário da Mesa Diretora -Efraim Morais (DEM-PB), no período em que Tasso fez uso desse expediente. A cada pedido abre-se um processo, de acordo com ele.</div>\n		\n<div align="justify"><br />\n			Morais confirmou à \n			<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Folha</span> que deu as autorizações. "É um procedimento legal. Se for para uso pessoal do senador, não vejo problema", afirma.</div></span></span>', 'EP', 'jornal Folha de São Paulo', 'http://www1.folha.uol.com.br/fsp/brasil/fc0204200902.htm');
INSERT INTO `conteudo` VALUES (27, 'CIC TEM AGENDA DE PESO - 02/04/2009', '2009-04-02', '\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A agenda do Centro Industrial do Ceará (CIC), a entidade de pensamento empresarial que a partir dos anos 1980 projetou nomes como Tasso Jereissati, Sergio Machado, Beni Veras, Assis Machado, Lauro Fiúza, Jorge Parente e Lima Matos, fechou uma agenda de peso para comemorar os 90 anos de fundação. O presidente Robinson de Castro confirmou como primeiro convidado o senador Jarbas Vasconcelos (PMDB-PE). Ele fala no dia 4 de maio sobre "Espírito Público". No dia 25 de maio vem Cláudio de Moura e Castro tratar de "Educação: Um olhar para o desenvolvimento". <br />\n		<br />\n		O economista Raul Velloso, referência em contas públicas, dá palestra sobre "Gestão das Contas Públicas: A Visão político-administrativa", no dia 15 de junho. Fechando o programa, o ex-presidente Fernando Henrique Cardoso apresenta "Cenários Político-econômicos: Nacional e Internacional", no dia 30 de julho. </span></p>', '', 'CE', 'Jornal O Povo - Economia - Col, Vertical S/A (Jocélio Leal)', 'http://www.opovo.com.br/opovo/colunas/verticalsa/');
INSERT INTO `conteudo` VALUES (28, 'Indústria tem a pior queda desde 1994', '2009-04-02', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Sergio Lamucci, de São Paulo<br />\n		</p>\n	\n<p align="justify">Entre setembro e dezembro, a produção industrial caiu 20,1%, taxa nunca atingida nas crises anteriores, segundo a Abdib</p>\n	\n<p align="justify">Nos últimos 15 anos, nenhuma das cinco crises que afetaram o Brasil teve um impacto sobre a indústria tão grande como a atual. Entre setembro e dezembro, a produção industrial recuou 20,1%, taxa nunca antes atingida nas crises anteriores, segundo a Associação Brasileira da Infraestrutura e Indústrias de Base (Abdib). Anteriormente, a pior retração foi decorrente do colapso do México, quando a indústria caiu 12% entre dezembro de 1994 - o início da crise - e o fundo do poço, alcançado em setembro de 1995.</p>\n	\n<p align="justify">O estudo realizado pela entidade pesquisou ainda o tempo que a indústria demorou para voltar ao patamar de produção anterior à crise. Nas últimas cinco crises, também foi a mexicana que se prolongou por mais tempo - foram 33 meses até o setor recuperar o padrão perdido.</p>\n	\n<p align="justify">Em fevereiro, a produção industrial caiu 17% em relação ao mesmo mês do ano passado, um tombo quase idêntico aos 17,2% de janeiro. O resultado foi 1,8% melhor que o de janeiro, na série com ajuste sazonal.</p></span>', '', 'EP', 'Jornal Valor Econômico', 'http://www.valoronline.com.br/ValorImpresso/MateriaImpresso.aspx?codmateria=5497102&dtmateria=2009-4-2&codcategoria=89&Tp=1');
INSERT INTO `conteudo` VALUES (29, 'Ciclo de Debates: Governança Urbana, Mobilidade e Plano Diretor ', '2009-04-02', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">PLANO DIRETOR DE FORTALEZA</p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Governança Urbana, Mobilidade e Plano Diretor será o tema de discussão do próximo Ciclo de Debates, que terá a participação do arquiteto Fausto Nilo, da professora da Universidade Federal do Ceará (UFC), Cleide Bernal, que é doutora em Planejamento Urbano e Regional, e do engenheiro civil e diretor do Centro Industrial do Ceará, Marcelo Cavalcante. O programa irá ao ar neste domingo, 05, às 19 horas, pela TVC, com reprise na sexta-feira, 10 de abril, às 22 horas.</p></span>', '', 'EP', '', '');
INSERT INTO `conteudo` VALUES (30, 'Para corregedor do Senado, usar verba para fretar avião não é proibido', '2009-04-02', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Claudia Andrade<br />\n		Do UOL Notícias<br />\n		Em Brasília </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O corregedor do Senado Federal, senador Romeu Tuma (PTB-SP), afirmou nesta quinta-feira (2) que não haverá investigação sobre o suposto pagamento de avião fretado com verba oficial pelo senador Tasso Jereissati (PSDB-CE). A informação foi publicada hoje pelo jornal </span><a href="http://www1.folha.uol.com.br/folha/brasil/ult96u544494.shtml">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">Folha de S.Paulo</span>\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">. <br />\n		</span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Tasso paga avião fretado com dinheiro do Senado </p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Reportagem do jornal Folha de S. Paulo desta quinta-feira denuncia que o senador Tasso Jereissati (PSDB-CE) tem o hábito de usar parte de sua verba oficial de passagens aéreas para fretar jatinhos que são pagos com recursos do Senado.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Ele tem liberdade de usar (a verba). Ele está apresentando comprovante de uso. Se ele fez uso do avião, é permitido que ele faça. Ele tem R$ 15 mil para usar em vários segmentos", afirmou Tuma. "Não é proibido. Não há impedimento para isso. Precisa de uma regra mais clara para evitar especulação e distorção, porque as regras hoje são um pouco abertas", acrescentou o corregedor.<br />\n		<br />\n		Se não há uma regra que impede o uso de recursos do Senado em fretamento de aviões, o caso do senador Jereissati resvalaria na questão ética? Para Tuma, esse \'é um problema pessoal\'. "Aí é um problema pessoal. Questão ética é pessoal. Se não há impedimento, o cidadão é que decide se pode ou não. Cada um de nós sabe o que representa a legalidade ou a imoralidade e decidimos em cima disso. Enquanto na regra não há impedimento, depende do parlamentar fazer o que acha correto ou não."<br />\n		<br />\n		Para Tuma, o uso do dinheiro público só seria indevido caso não houvesse justificativa por parte do parlamentar. "Ele comprovando que precisava de uma viagem urgente de avião, eu não vejo nenhuma razão para uma apuração. Ele vai ter que justificar a despesa." <br />\n		</span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Grupo de discussão</p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Você acha que os políticos devem ter seus gastos cobertos pelos cofres públicos?</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O corregedor defende a necessidade de se estabelecer regras para as várias situações que envolvem o uso de dinheiro público pelos parlamentares. "Os líderes dos partidos acham que temos que harmonizar a casa, definir, escrever e assinar o que pode e o que não pode ser feito. É importante você ter uma regra estabelecida."<br />\n		<br />\n		"Quando tiver uma regra, aí sim, segue-se aquela regra e quem desrespeitar vai ter que responder pelo ato", resumiu Tuma. </span></p>', '', 'EP', 'Uol Notícias', 'http://noticias.uol.com.br/politica/2009/04/02/ult5773u958.jhtm');
INSERT INTO `conteudo` VALUES (31, 'Indústria se arrasta pelo fundo do poço, afirma consultoria', '2009-04-03', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O resultado da indústria de fevereiro, divulgado nesta semana, indica que a atividade do setor segue no fundo do poço. A análise é da consultoria MB Associados, que já projeta uma retração de 4,5% na atividade industrial deste ano comparada com a de 2008.<br />\n		Em fevereiro, a produção industrial se retraiu em 17% em relação ao mesmo mês do ano passado, repetindo o resultado de janeiro. Nos dois primeiros meses do ano, no entanto, a expansão ante o mês anterior foi de cerca de 2%.<br />\n		Segundo Sergio Vale, economista-chefe da MB, os dados não indicam uma recuperação, mas um indício de que a indústria vai demorar para atingir os patamares de produção que tinha antes da crise.<br />\n		Para ele, o desempenho ruim do setor vai refletir em um resultado negativo do PIB do país nos três primeiros meses do ano. A MB aguarda os dados de março para revisar sua projeção, mas já espera uma queda de cerca de 3% no PIB do primeiro trimestre em relação ao mesmo período de 2008.<br />\n		Para a MB, esse resultado ruim dará o tom para o PIB do ano todo. "A tendência, com isso, é de fato termos PIB negativo neste ano", informa a consultoria em relatório.<br />\n		Vale ressalta, no entanto, que a possibilidade de o crescimento da economia ficar abaixo de zero neste ano será negada pelo governo "veementemente até o fim", por se tratar de um ano pré-eleitoral. Hoje, a projeção do governo é de crescimento de 2% da economia em 2009.<br />\n		"Não tem a mínima condição de o país atingir esse número [projeção oficial]", afirma Vale.<br />\n		A contração da economia brasileira promoverá aumento do desemprego, desaceleração do crescimento da renda e expansão menor de pessoas para a classe média, segundo Vale.<br />\n		Para ele, a classe média sofrerá mais que a de baixa renda com a retração da economia, já que sente mais forte o impacto do desemprego. A população de baixa renda também conta com a proteção do salário mínimo e de programas de complementação de rendimentos do governo, como o Bolsa Família, para não perder renda.<br />\n		"Veremos uma situação em que a classe baixa se sustentará, mas a média não", afirma.</span></p>', '', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0304200901.htm');
INSERT INTO `conteudo` VALUES (32, 'Presidente \'Teflon\' do Brasil é afetado pela crise econômica', '2009-04-03', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 24pt; FONT-FAMILY: Arial">\n	\n<p>\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 12pt; FONT-FAMILY: Arial"></span></p>\n	\n<p align="justify">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ele já foi chamado de presidente "Teflon", o presidente mais popular do mundo, um líder que sobreviveu a escândalo atrás de escândalo apenas para ver seu índice de aprovação subir a novas alturas.<br />\n			<br />\n			Mas com a forte desaceleração industrial e alta do desemprego, o presidente do Brasil, Luiz Inácio Lula da Silva, pode estar exibindo algumas rachaduras em sua armadura aparentemente impenetrável, dizem os analistas.<br />\n			</span></p>\n	\n<p align="justify">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ao participar do encontro de cúpula do Grupo dos 20 em Londres, nesta semana, ele enfrenta crescentes desafios à sua elevada popularidade doméstica e talvez ao seu status como um dos principais defensores dos países em desenvolvimento.<br />\n			<br />\n			Pelo menos três pesquisas mostram uma recente queda em seus índices estelares de aprovação e uma onda de controvérsia em relação a comentários feitos na semana passada, quando atribuiu a crise econômica a "gente branca com olhos azuis", sinais de que Lula afinal é mortal, disseram os analistas.<br />\n			<br />\n			"A perspectiva de que os ganhos econômicos e sociais do Brasil possam ser minados pela crise global poderia minimizar dois mandatos presidenciais enormemente bem-sucedidos", disse Julia E. Sweig, diretora para Estudos Latino-Americanos do Conselho de Relações Exteriores.</span></p></span>', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 24pt; FONT-FAMILY: Arial">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 12pt; FONT-FAMILY: Arial">\n		\n<p>\n			<span style="FONT-SIZE: 10pt">Lula ainda conta com um apoio invejável em casa, mas se a tempestade econômica prosseguir, ela poderia minar ainda mais esse apoio e atrapalhar sua capacidade de transferir a presidência em 2010 para sua sucessora escolhida, Dilma Rousseff, a ministra da Casa Civil, disseram os analistas.<br />\n				</span></p>\n		\n<p align="justify">\n			<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">No encontro de cúpula nesta semana, Lula buscará estabelecer o Brasil como potência mundial, mas "o truque em Londres será atingir as notas substantivas certas, estabelecendo a liderança brasileira sem colher muito desdém daqueles ao seu redor", disse Sweig.<br />\n				<br />\n				Na semana passada, Lula colheu muito desdém. A crise econômica, ele disse, foi causada "por comportamentos irracionais de gente branca com olhos azuis, que antes pareciam saber de tudo, e, agora, demonstram não saber de nada".<br />\n				<br />\n				Os comentários poderiam ter causado apenas uma leve agitação na presença de outros líderes latino-americanos, que rotineiramente criticam os banqueiros nos Estados Unidos e na Europa. Mas ditos em Brasília com o primeiro-ministro do Reino Unido, Gordon Brown, ao seu lado, os comentários de Lula causaram irritação tanto em casa quanto no exterior.<br />\n				<br />\n				"Aquela era uma grande chance de Lula mostrar que é a voz de uma liderança razoável, considerada", disse Amaury de Souza, um analista político que comanda a MCM Consultores Associados, no Rio. "Agora ele pode ser acusado de racismo, o que enfraquece enormemente as posições do Brasil na política externa."<br />\n				<br />\n				Alguns analistas sugeriram que o presidente brasileiro, como faz com frequência, podia estar fazendo seus comentários para os trabalhadores pobres, a base de seu apoio. Mas os comentários tocaram uma ferida aberta entre as elites do Brasil, que se ressentem da popularidade de Lula, um ex-metalúrgico com escolaridade apenas até a quarta série e que trabalhou em uma fábrica de automóveis.<br />\n				<br />\n				Não foi a primeira vez que ele exibiu sua raiva em relação à crise. Após passar grande parte da década desenvolvendo sua economia, domando a inflação e poupando mais de US$ 200 bilhões durante o boom dos commodities, o Brasil era visto como um exemplo na economia mundial.<br />\n				<br />\n				"O desejo do Brasil de ser considerado uma das grandes potências econômicas faz parte desta frustração de Lula", disse Johanna Mendelson-Forman, associada sênior do programa para Américas do Centro para Estudos Estratégicos e Internacionais. "É como se ele dissesse: \'Agora que cheguei a potência econômica, o mundo quebra\'."<br />\n				<br />\n				Enquanto a crise econômica se desdobrava nos Estados Unidos e na Europa no final do ano passado, Lula minimizava a possibilidade de contágio em seu país.<br />\n				<br />\n				"Lá, a crise é uma tsunami", ele disse em outubro. "Aqui, se ela chegar, vai chegar uma marolinha que não dá nem para esquiar".<br />\n				<br />\n				Mas agora a economia do Brasil está sofrendo. Seu produto interno bruto caiu em 3,6% no último trimestre de 2008, a pior queda entre os países latino-americanos. O país perdeu 654.946 empregos em dezembro de 2008 e mais 101.748 empregos em janeiro, segundo o Ministério do Trabalho.<br />\n				<br />\n				O impacto está exercendo um preço político. Em março, Lula sofreu sua primeira queda de popularidade em mais de um ano.<br />\n				<br />\n				Seu índice de aprovação em algumas pesquisas ainda é impressionante, pairando acima de 70%, mas poderia cair abaixo de 50% se o declínio econômico prosseguir, disse David Fleischer, um professor de ciência política da Universidade de Brasília.<br />\n				<br />\n				A equipe de Lula tem impressionado alguns economistas com seus esforços para tentar impedir um maior vazamento no barco econômico. E apesar da recente queda na popularidade, poucos descartam Lula, um dos políticos mais resistentes do Brasil. Em 2006, ele se recuperou de um escândalo para conquistar a reeleição para o segundo mandato. Então o Supremo Tribunal acusou dois importantes funcionários do governo de corrupção em 2007, mas isso não afetou a popularidade do presidente, que continuou subindo.<br />\n				<br />\n				Mesmo no encontro de cúpula em Londres, sua estatura pareceu segui-lo.<br />\n				<br />\n				Um órgão de imprensa brasileiro mostrou o vídeo do presidente Obama apertando a mão de Lula. "Esse é o cara", disse Obama. "Eu amo esse cara. Ele é o político mais popular da Terra."<br />\n				<br />\n				</span>\n			<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Tradução: George El Khouri Andolfato</span></p></span></span>', 'EP', 'Uol - Úlitmas Notícias - The New York Times', 'http://noticias.uol.com.br/midiaglobal/nytimes/2009/04/03/ult574u9269.jhtm');
INSERT INTO `conteudo` VALUES (33, 'Em dia de euforia, Bovespa retomou os 43 mil pontos e dólar caiu ', '2009-04-03', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Em dia de euforia, Bovespa retomou os 43 mil pontos e dólar caiu </p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Valor Online</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">SÃO PAULO - Apoiados na crescente percepção de que o pior da crise já teria ficado para trás, os investidores mantiveram o tom otimista dos últimos dias na quinta-feira e seguiram atuando na ponta compradora da Bolsa de Valores de São Paulo (Bovespa), que retomou os 43 mil pontos, e continuaram se desfazendo de dólares. Apoiados na mesma crença, os agentes realizaram lucros no mercado de juros futuros. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A melhora de ambiente, que parece se consolidar, começou na segunda semana de março com bancos americanos indicando lucratividade no primeiro bimestre do ano, ganhou fôlego com a ampliação das medidas para estimular o crédito, tomadas pelo Federal Reserve (Fed), banco central americano, e ganhou corpo depois que o Tesouro dos Estados Unidos detalhou o plano de saneamento dos bancos. Entremeando esses acontecimentos, saíram alguns indicadores econômicos melhores do que o estimado.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ontem, dois novos fatores se somaram aos anteriores, promovendo um aumento no apetite por risco, que resultou em uma onda de valorização nas bolsas e nas commodities. A reunião do G-20, em Londres, foi acompanhada com grande entusiasmo pelo mercado, pois mostrou a coordenação dos países no combate à crise.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O outro ponto envolve as alterações nas normas contábeis americanas, que beneficiará os bancos no registro de seus ativos ilíquidos. De forma simplificada, diz o sócio da Global Financial Advisor, Miguel Daoud, a mudança permite que os bancos fixem o valor dos ativos em suas carteiras de acordo com o que eles acham que vale. " O papel pode não ter valor no mercado, mas, dentro do banco, ele vale alguma coisa ainda. " </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Segundo Daoud, com isso, as revisões para baixo nos ativos dos bancos chegam ao fim, permitindo que eles retomem os empréstimos, algo fundamental para o restabelecimento da atividade no lado real da economia. " Existe risco nesse tipo precificação, mas a crise é sem precedente e essa é uma decisão melhor que a nacionalização dos bancos " , afirmou Daoud.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De posse dessas informações, os investidores já começaram a antecipar a recuperação da economia global. No entanto, alerta Daoud, essa foi a visão ontem; não dá para saber se, de hoje em diante, o mercado continuará enxergando a mesma coisa.</span>\n	<span style="FONT-SIZE: 12pt; FONT-FAMILY: Arial">&nbsp;</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Na Bovespa, as compras foram acentuadas desde o começo do pregão, com o índice testando 44.286 pontos na máxima do dia. De acordo com o superintende da Banif Corretora, Raffi Dokuzian, a impressão é de que quem não comprou está correndo atrás do prejuízo.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Com 59 dos 65 papéis que compõem o Ibovespa em alta, o índice fechou o dia com valorização de 4,19%, aos 43.736 pontos, maior pontuação desde 3 de outubro. O giro financeiro foi elevado, somando de R$ 5,89 bilhões, o maior do ano. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em Wall Street, as compras perderam um pouco de força no final do dia, mas, ainda assim, o Dow Jones teve alta de 2,79%, para 7.978 pontos. O S &amp; P 500 ganhou 2,87%, a 834 pontos. O Nasdaq Composite aumentou 3,29%, a 1.602 pontos.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Com a aversão ao risco caindo, também diminui a necessidade posições compradas em dólar, o que os investidores estrangeiros parecem estar fazendo. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ao fim do pregão, a moeda americana era negociada a R$ 2,233 na compra e R$ 2,235 na venda, queda de 1,97%. Tal preço é o menor desde 6 de janeiro, quando a moeda fechou a R$ 2,18.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Na roda de " pronto " da Bolsa de Mercadorias e Futuros (BM &amp; F), a moeda também perdeu 1,97%, encerrando a R$ 2,235. O giro financeiro somou US$ 169 milhões. No interbancário, o movimento passou de US$ 3,4 bilhões.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O operador de mercados futuros da Terra Futuros, Daniel Negrisolo, acredita que a tendência de curto prazo, salvo alguma notícia catastrófica, é que essa recuperação no preço dos ativos continue, o que também vale para o real. Para Negrisolo, a banda de oscilação do dólar fica entre R$ 2,20 e R$ 2,35.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Nos juros futuros, com os agentes trabalhando com a possibilidade de retomada da atividade econômica, automaticamente cai a necessidade de vultosos estímulos monetários. Por isso, a diminuição das posições vendidas em taxa futura.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">" Todo mundo conhece o conservadorismo do nosso Banco Central. Se ele perceber sinais de melhora na economia, pode diminuir o ritmo de corte de juros " , resume o vice-presidente de tesouraria do banco WestLB, Ures Folchini.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Depois de três dias perdendo prêmio de risco, o contrato de Depósito Interfinanceiro (DI) com o vencimento para janeiro de 2010, o mais líquido, fechou com alta de 0,09 ponto, a 9,79%, na Bolsa de Mercadorias e Futuros (BM &amp; F). O vencimento para janeiro de 2011 ganhou 0,08 ponto, a 10,39%. E janeiro 2012 apontava 10,81%, ganho de 0,11 ponto.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Na ponta curta, o DI para maio subiu 0,01 ponto, para 11,09%. O contrato para junho ganhou 0,04 ponto, a 10,54%. E julho de 2009 acumulou 0,04 ponto, projetando 10,26%.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Até as 16h15, antes do ajuste final de posições, foram negociados 666.700 contratos, equivalentes a R$ 61,24 bilhões (US$ 26,74 bilhões). O vencimento para janeiro de 2010 foi o mais negociado, com 360.670 contratos, equivalentes a R$ 33,64 bilhões (US$ 14,69 bilhões).</span></p>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">(Eduardo Campos | Valor Online)</p></span>', 'EP', 'Valor On Line - Finanças', 'http://www.valoronline.com.br/ValorOnLine/MateriaCompleta.aspx?tit=Em+dia+de+euforia+Bovespa+retomou+os+43+mil+pontos+e+dolar+caiu&codMateria=5499448&dtMateria=03+04+2009&codCategoria=212');
INSERT INTO `conteudo` VALUES (34, 'Educação - Curso capacita alunos para fiscalizar gestores públicos ', '2009-04-05', '\n\n<p>Cerca de 200 pessoas serão capacitadas para monitorar gestores da Ibiapaba. O curso Controle Social das Contas Públicas será realizado em Guaraciaba com uso dos fascículos encartados no O POVO </p>\n\n<p>Quais os rumos do dinheiro de impostos que todos pagam? É a partir desse questionamento que o curso Controle Social das Contas Públicas será realizado em Guaraciaba do Norte, a partir do dia 16 de abril, no Teatro João Barreto. O evento, organizado pelo Centro Industrial do Ceará (CIC), em convênio com a Universidade Vale do Acaraú (UVA) e Instituto Vale do Acaraú (IVA), vai utilizar os mesmos fascículos do curso homônimo promovido pela Fundação Demócrito Rocha e Tribunal de Contas do Município, que é encartado semanalmente no O POVO. </p>\n\n<p>"A região da Ibiapaba é carente de informação sobre a fiscalização da gestão pública. Queremos aproximar o assunto e criar uma rede de monitoramento das ações públicas", afirma Francisco Baltazar, coordenador do evento. O curso, destinado aos estudantes universitários e interessados no assunto, pretende alcançar cerca de 200 pessoas dos nove municípios que englobam a serra da Ibiapaba. As inscrições podem ser realizadas até o primeiro dia do evento. </p>\n\n<p>A solenidade de abertura contará com a presença do presidente do TCM, Ernesto Sabóia, que falará sobre as estratégias para diminuir a distância entre o cidadão e as informações técnicas das contas públicas. </p>\n\n<p>O curso também terá a participação do reitor da Universidade do Parlamento, Francisco Caminha, do pró-reitor do curso de extensão da UVA, Leuman Gomes, e do jornalista Plínio Bortolotti, editor institucional do O POVO. "Será uma oportunidade para tirar dúvidas e discutir com personalidades e autores dos fascículos", acrescenta Baltazar. </p>\n\n<p>Para a coordenadora do projeto, Eloísa Vidal, a iniciativa de realizar o curso em Guaraciaba do Norte deve ser imitada por outros municípios. "Itapajé também se interessou em realizar um curso presencial. O evento utiliza o máximo possível de informações contidas nos fascículos e serve para divulgar, aprofundar e socializar os conhecimentos sobre a importância do controle social nas contas públicas", afirma Vidal. </p>\n\n<p>Amanhã será encartado no O POVO o terceiro fascículo O papel do controle externo - TCU, TCE e TCM, escrito por Ubiratan Aguiar. Além do material impresso, o aluno pode acompanhar as aulas pela TV O POVO e rádio O POVO/CBN. </p>\n\n<p>Ao final do curso, os participantes realizarão um exame online e, caso aprovados com média 6,0 ou superior, receberão certificados. </p>\n\n<p>As inscrições podem ser feitas pela Internet (<a href="http://www.controlesocial.fdr.com.br">www.controlesocial.fdr.com.br</a>) ou no Teatro João Barreto, em Guaraciaba do Norte. Mais informações: 0800 280 2210</p>\n\n<p>&nbsp;<br />\n	&nbsp;<br />\n	PROGRAMA </p>\n\n<p>CONTROLE SOCIAL DAS CONTAS PÚBLICAS <br />\n	Local: Teatro João Barreto - Guaraciaba do Norte </p>\n\n<p>DIA 16 DE ABRIL, DAS 19H ÀS 21H <br />\n	Em debate: Fascículos 1 e 2 </p>\n\n<p>DIA 8 DE MAIO, DAS 19H ÀS 21H <br />\n	Em debate: Fascículos 3, 4 e 5 </p>\n\n<p>DIA 22 DE MAIO, AS 19H ÀS 21H <br />\n	Em debate: Fascículos 6, 7 e 8 </p>\n\n<p>DIA 5 DE JUNHO, DAS 19H ÀS 21H <br />\n	Em debate: Fascículos 9 e 10 </p>\n\n<p>FASCÍCULOS E AUTORES<br />\n	> 1º FASCÍCULO. "Fundamentos filosóficos da transparência e do controle popular": Oscar dAlva - procurador de Justiça, professor de Direito da Universidade de Fortaleza (Unifor) <br />\n	> 2° FASCÍCULO. "Jornalismo e controle social": Plínio Bortolotti - jornalista, editor institucional do Grupo de Comunicação O POVO e ex-ombudsman do O POVO <br />\n	> 3° FASCÍCULO. "O papel do controle externo - TCU, TCE e TCM": Ubiratan Aguiar - presidente do Tribunal de Contas da União (TCU) <br />\n	> 4° FASCÍCULO. "A transparência e controle social na Carta Maior": Judicael Sudário de Pinho - juiz do Trabalho, diretor do Fórum Autran Nunes (TRT, 7ª Região), ex-coordenador do curso de Direito da Universidade de Fortaleza (Unifor) <br />\n	> 5° FASCÍCULO. "A impunidade no Brasil": Djalma Pinto - professor da Escola Superior de Advocacia do Ceará, ex-procurador-geral do Estado <br />\n	> 6° FASCÍCULO. "A informática como instrumento auxiliar do controle social": Vasco Furtado - professor de Informática da Universidade de Fortaleza (Unifor) e consultor da Empresa de Tecnologia da Informação do Ceará (Etice) <br />\n	> 7° FASCÍCULO. "A ação parlamentar na fiscalização da administração pública": Heitor Férrer - deputado estadual e médico <br />\n	> 8° FASCÍCULO. "O papel e a função do Ministério Público de servir o cidadão e a comunidade": Socorro França - procuradora-geral de Justiça do Estado <br />\n	> 9° FASCÍCULO. "O papel do controle interno": Aloísio Carvalho - secretário estadual da Controladoria e Ouvidoria Geral, ex-secretário de Finanças de Fortaleza <br />\n	> 10° FASCÍCULO. "Educação fiscal e controle social": Mauro Filho - secretário da Fazenda do Estado e deputado estadual licenciado. </p>', '\n\n<p>&nbsp;</p>', 'CE', 'Jornal O Povo - Ceará', 'http://www.opovo.com.br/edicoesant/');
INSERT INTO `conteudo` VALUES (35, 'BIRTHDAY', '2009-04-06', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">>> Ainda cumprimentadíssimo pela troca de idade o presidente do CIC -Centro Industrial do Ceará-, Robinson de Castro e Silva. <br />\n		</span></p>', '', 'CE', 'Jornal O Povo - Col. Sônia Pinheiro', 'http://www.opovo.com.br/opovo/colunas/soniapinheiro/');
INSERT INTO `conteudo` VALUES (36, 'Maioria das empresas planeja cortar custos', '2009-04-06', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">São Paulo, 6 de Abril de 2009 - Os empresários preparam um forte ajuste para adaptar suas operações ao quadro de desaceleração econômica e enfrentam um processo de revisão de modelo de </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430876,860,10,8/l">\n		<span style="COLOR: #000000">\n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial; TEXT-DECORATION: underline">negócios</span> \n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">. De acordo com pesquisa realizada pela Ernst &amp; Young, o corte de custos está nos planos de 87% dos líderes empresariais ouvidos pela consultoria, 53% pretendem reduzir investimentos e 47% preveem diminuir o número de vagas nos próximos 12 meses. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">A compressão da estrutura das operações a um patamar mais baixo revela uma contradição em relação às projeções relativas ao desempenho das vendas e faturamento. A sondagem, realizada com 15 empresas com faturamento anual de até R$ 1,5 bilhão, mostra que 53% confiam que serão capazes de manter ou melhorar os resultados neste ano. "As contradições demonstram que ainda há dúvidas e os empresários não sabem exatamente qual o contorno da crise e seu impacto", afirma Carlos Miranda, sócio da Ernst &amp; Young. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">A dificuldade de delinear o alcance e de saber a exata extensão da turbulência econômica são componentes que atuam na formação das expectativas. "Esses são componentes que podem mexer com a confiança do empresário e nos planos de </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430876,860,10,8/l">\n		<span style="COLOR: #000000">\n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial; TEXT-DECORATION: underline">ação</span> \n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">", assinala. A medida que a crise se define, as empresas começam a dimensionar os cortes. </span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">A pesquisa mostrou que as medidas para reduzir o ritmo e o peso da folha de pagamentos convivem com a perspectiva de sustentar parte da força de trabalho que será útil para a empresa quando ocorrer uma retomada. A adoção alternativas para evitar as demissões estão entre as prioridades de 100% dos líderes empresariais. A concessão de férias antecipadas, programas de treinamento e revisão dos níveis de estoque integram a lista de recursos adotados pelas companhias. O congelamento de vagas, gerenciamento da reposição de pessoal, adiamento de projetos e aumento da força de vendas completam o conjunto de medidas apontadas pelos empresários para reduzir o volume de cortes de mão-de-obra. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">"O empreendedor tem metas agressivas, defende as pessoas que trabalham com ele, mas sabe que há uma pressão externa", comenta Miranda. A decisão de cortar pessoas e custos está relacionada ao tempo que durar a crise. Segundo ele, os empresários, mesmo que não cresçam, planejam mudar o mix de produtos e começam a promover um rearranjo interno e a olhar outros mercados. "Há uma revisão no modelo de negócio." </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">A abertura de capital está fora dos planos de 53% dos entrevistados. Uma parcela das empresas (13%) diz ter abandonado os planos de abrir o capital com a crise. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Se existe dificuldade para se saber o tamanho da crise, há uma forte convergência em relação a sua duração. Para 73% dos executivos, a desaceleração econômica deve se manter pelo prazo de um a dois anos. "Essa é a média mais comum e confortável", avalia. Uma parcela de 13% da amostra se mostra mais otimista e espera uma reação em menos de um ano. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Os empresários não subestimam os efeitos da crise internacional e 60% esperaram um impacto severo na economia brasileira e 33% acreditam que os danos serão baixos para a atividade do País. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">A redução das taxas de juros básicos, adoção de políticas de crédito, cortes de gastos governamentais e expansão dos </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430876,860,10,8/l">\n		<span style="COLOR: #000000">\n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial; TEXT-DECORATION: underline">investimentos</span> \n			<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial"></span></span></a>\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">em infraestrutura são pontos de convergência entre os empresários. "No Brasil, há um aspecto efetivo e psicológico da crise. Muitos acreditam que o papel do empresariado e do governo pode ajudar a mudar um pouco a visão pessimista", diz. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Estas ações deveriam ser incrementadas por incentivo ao consumo interno, desoneração fiscal, gerenciamento da crise e flexibilização dos contratos de mão de obra. A redução do tamanho do Estado na economia, por meio de privatizações, e do custo Brasil foram considerados fatores importantes para ampliar o nível de atividade. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Para Miranda, se o governo " sinalizar que está fazendo seu dever de casa" o humor dos empresários pode começar a mudar. As publicações dos balanços e o comportamento da Bolsa de Valores terão efeito também nas expectativas. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">Do ponto de vista de gestão, a criatividade, estímulo ao diálogo interno, objetividade, simplicidade e capacidade de reter talentos aparecem como características principais. A transparência e a ética foram citadas também como elementos importantes em um período de turbulência.</span></p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430876,860,10,8');
INSERT INTO `conteudo` VALUES (37, 'Cortes no orçamento podem ser revistos', '2009-04-06', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">São Paulo, 6 de Abril de 2009 - Por mais doloroso que possa se apresentar, o ajuste no orçamento Geral da União -- contingenciando R$ 21,6 bilhões -- anunciado semana passada foi absolutamente necessário, garantem economistas. Mas economistas com especialização em finanças públicas questionam o cálculo do orçamento, baseado na premissa de um crescimento do Produto Interno Bruto (PIB) de 2% em 2009; e o engessamento da atuação do estado na área, devido à obrigações com o custeio, com repasses constitucionais à Saúde e à Educação e com o pagamento da dívida pública. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"A meta de resultado primário prevista no orçamento enviado ao Congresso em 2008 é inconsistente com o cenário de inflação mais baixa e crescimento do PIB inferior em curso", diz Amaury Bier, ex-secretário executivo do Ministério da Fazenda no governo Fernando Henrique e sócio da Gávea Investimento. Ou seja, as receitas em 2009 tenderão a ser bem menores do que as inicialmente previstas em 2008. Para Bier, portanto, seria desejável que o corte dos gastos do governo fosse muito maior. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"É bom lembrar que estados, municípios e estatais terão resultados piores este ano, e o que interessa nas contas públicas é a meta consolidada, levando em conta todas essas variáveis". Bier acredita que a revisão da estimativa de crescimento do PIB em 2009 feita pelo governo de 4,5% para 2% falha por ser muito otimista. O ex-secretário, ao contrário, trabalha com estimativas de um recuo do PIB em 1% este ano.</span>\n	<span style="FONT-SIZE: 10pt"></span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Bier pondera que, em função da demanda reprimida no País, "daria para gastar até três vezes o orçamento, dadas as carências sociais, em infraestrutura e em outras áreas". Ele admite a extrema dificuldade de se fazer cortes nesse contexto. Tarefa que se torna ainda mais complexa devido ao "pequeno espaço de manobra que sobra para o governo atuar". De um lado, lembra, houve nos anos recentes uma "expansão generosa" dos gastos correntes, principalmente em função dos aumentos dados ao funcionalismo e às regras de indexação do salário mínimo, as quais onerariam a </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430877,860,10,9/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">Previdência</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">. "O que sobra para se cortar é exatamente o que não é recomendável suprimir, ou seja, os investimentos". </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para o ex-secretário do Ministério da Fazenda, é muito provável que o governo se veja obrigado a divulgar novo reajuste no Orçamento já no próximo bimestre, quando, devido à inflexibilidade de boa parte do orçamento, até as obras do Programa de Aceleração do Crescimento (PAC) correrão o risco de serem sacrificadas -- seja por contingenciamento, seja por meio de adiamentos. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com Bier, outras alternativas para a adequação do orçamento à nova realidade estariam nos recursos do Fundo Soberano e dos Projetos Pilotos de Investimento (PPIs). "Esses últimos são investimentos prioritários que não são computados como despesas", explica. Na </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430877,860,10,9/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">prática</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, os PPIs são gastos que podem ser abatidos da meta de Resultado Primário do Governo Federal. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Nem todas as análises são tão críticas. O professor Adriano Biava, da Faculdade de Economia, Administração e Contabilidade da Universidade de São Paulo, acredita que o contingenciamento proposto pelo governo é significativo. "17% do orçamento já é um grande corte", afirma. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para Biava, a grande questão sobre o contingenciamento está desfocada. "O que importa é saber se a carga tributária de cerca de 35% do PIB está bem dimensionada para as necessidades do País", comenta. "Para alguns, ela é absurda. Para outros, um País como o Brasil, que necessita de um estado com maior presença, de forma a gerar mais serviços à população carente, não tem como fugir de uma tributação mais pesada", argumenta. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Outro problema levantado pelo professor é o fato de, na redução de estimativa de gastos, a área financeira ser preservada, ao contrário do que ocorreu com diversos ministérios, notadamente o da Cidades -- que detinha uma participação de 6,5 % do orçamento, caindo para 4,9% -- e a área da Defesa, que participava do bolo com 7,4% e cuja fatia minguou para 6,6%. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O Brasil, independente do cenário </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430877,860,10,9/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">econômico</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, tem pago religiosamente a sua dívida, sem sequer questioná-la", diz ele. "Exceto por alguns espasmos no governo Sarney, todos os governos pagaram a dívida sem discutir o seu montante, os prazos e condições de pagamento", critica. Segundo ele, a tabela divulgada pelo Ministério do Planejamento com os valores a serem destinados a cada área do governo não deixa claro, ainda, qual será a economia a ser feita com a queda da taxa de juros ao longo do ano. "Os números divulgados, somando os encargos e operações, não refletem o peso da dívida sobre o orçamento público", avalia. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Outro professor, Francisco Vignole, da Fundação Getúlio Vargas, vê o compromisso com o pagamento da dívida um mérito. "O resultado primário desse governo mostra o seu esforço fiscal", diz. Ele próprio ex-secretário de Finanças dos municípios de Diadema e Santo André (ambos em SP) em administrações petistas, Vignole lembra que a gestão Lula conseguiu diminuir a dívida interna de um patamar de 56% do PIB, em 2003, para os atuais 36%. Segundo o economista, o governo tem agido seriamente, também, levando em conta os repasses constitucionais para Saúde e Educação. "O cobertor está curto", frisa. "Assim, é natural que algumas áreas sejam priorizadas em detrimento de outras", diz. Para o economista, a distribuição do ônus não é uma decisão técnica. "Ao contrário, é essencialmente política". E, neste caso, a decisão estaria correta ao preservar as áreas sociais e as voltadas ao investimento em infraestrutura, que minimizariam os efeitos da crise.</span></p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2430877,860,10,9');
INSERT INTO `conteudo` VALUES (38, 'Atraso em reformas trava país, diz Banco Mundial ', '2009-04-07', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Para organismo, Brasil perdeu chance de reduzir burocracia e melhorar infraestrutura<br />\n		Órgão atribui expansão recente à bonança mundial e credita adiamento de reformas à mudança no comando da Fazenda</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">GUSTAVO PATU</span><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA SUCURSAL DE BRASÍLIA </span><br />\n	<br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Relatório do Bird (Banco Mundial) afirma que o Brasil deixou de dar prioridade às reformas econômicas na gestão do ministro da Fazenda, Guido Mantega, durante o período de auge da prosperidade mundial -e agora, passada a bonança, dificilmente voltará a conseguir um crescimento elevado na ausência dessa agenda.<br />\n		Trata-se de uma referência a um grupo de medidas e projetos apresentados ainda no primeiro mandato do presidente Luiz Inácio Lula da Silva, com apoio dos organismos financeiros internacionais, para aumentar o potencial de expansão da economia do país.<br />\n		Pretendia-se, no que se convencionou chamar de reformas microeconômicas, simplificar tributos, reduzir empecilhos burocráticos, enfrentar gargalos na infraestrutura, elevar a competição no setor bancário e estimular os investimentos em inovação tecnológica, entre outras metas a serem atingidas em etapas.<br />\n		</span></p>', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Na visão do Banco Mundial, a agenda começou a ser relegada a segundo plano com a substituição de Antonio Palocci Filho por Mantega, em 2006.<br />\n		"Vários fatores reduziram o comprometimento do governo com o programa de reforma microeconômica acertado com o banco. Primeiro, a saída de várias autoridades-chave no Ministério da Fazenda, incluindo o ministro."<br />\n		Não por acaso, o progresso do governo brasileiro na condução das reformas, que merecia o adjetivo "satisfatório" até aquele ano, foi rebaixado para "moderadamente satisfatório". No quesito "Melhora do ambiente de negócios", a nota cai para "moderadamente insatisfatório" no relatório.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Empréstimos</span><br />\n		O Bird pretendia fazer três operações de empréstimos ao Brasil para auxiliar no avanço da agenda. A primeira, de US$ 505 milhões, foi integralmente cumprida; da segunda, só foi liberado um quarto dos US$ 600 milhões esperados; a terceira foi cancelada em comum acordo com o governo.<br />\n		O segundo motivo, diz o documento, foi a tão esperada retomada do crescimento econômico brasileiro, que "levou a uma perda de interesse em alguns aspectos da agenda". As taxas de expansão do Produto Interno Bruto finalmente ultrapassaram os 5% em 2007 e 2008 -"mais como resultado da alta dos preços dos produtos primários de exportação do que pela melhora na competitividade", assinala o Bird.<br />\n		Tanto o bom desempenho brasileiro era fruto do excepcional momento do comércio global, argumenta-se, que foi bruscamente interrompido quando o agravamento da crise financeira no mundo desenvolvido paralisou o crédito e reduziu a demanda por petróleo e produtos agrícolas.<br />\n		"É improvável que o Brasil vá ser capaz de sustentar um elevado crescimento econômico na ausência de mais reformas para melhorar sua competitividade, particularmente se as condições externas permanecerem desfavoráveis com demanda mais baixa por commodities e custos financeiros maiores", avalia-se. Para este ano, analistas consultados pelo Banco Central estimam retração de 0,19% no PIB.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Matriz ideológica</span><br />\n		Uma das principais matrizes ideológicas do pensamento econômico liberal, o Banco Mundial teve influência crescente na agenda do Brasil e da América Latina a partir da década passada. Ao lado do FMI (Fundo Monetário Internacional), ajudou a propagar medidas como privatizações, contenção de gastos públicos, abertura comercial e financeira, no que foi batizado de Consenso de Washington.<br />\n		Esse ideário foi eficaz para controlar a inflação e regularizar os pagamentos da dívida externa, mas não para acelerar o crescimento. Entrou em cena, então, a segunda geração de reformas, não mais voltadas para grandezas como o gasto público ou o fluxo de capitais, mas para o aperfeiçoamento de leis e das instituições -daí serem chamadas de microeconômicas.<br />\n		A agenda foi adotada como bandeira da gestão de Palocci, quando foram aprovadas, por exemplo, a Lei de Falências e a regulamentação das Parcerias Público-Privadas.</p>\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; COLOR: #000080; FONT-FAMILY: Arial">\n		\n<div align="justify">outro lado</div>\n		\n<div align="justify">\n			<span style="FONT-WEIGHT: bold; FONT-SIZE: 12pt; FONT-FAMILY: Arial">Governo contesta relatório e diz que adotou medidas</span>\n			<span style="FONT-SIZE: 12pt; FONT-FAMILY: Arial"></span>\n			<span style="FONT-SIZE: 12pt; FONT-FAMILY: Arial"></span></div></span>\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 12pt; FONT-FAMILY: Arial">Governo contesta relatório e diz que adotou medidas</span>\n	<span style="FONT-SIZE: 12pt; FONT-FAMILY: Arial"></span>\n	<span style="FONT-SIZE: 12pt; FONT-FAMILY: Arial"></span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n		\n<p align="justify">DA SUCURSAL DE BRASÍLIA</p></span>\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n	\n<p align="justify">Em correspondência ao Bird (Banco Mundial), o governo Lula contestou a avaliação de que as reformas deixaram de ser prioritárias e a metodologia do organismo para a avaliação da competitividade do país.<br />\n		"O governo brasileiro discorda da opinião expressada no documento com respeito à perda de interesse nas reformas microeconômicas", diz o texto, reproduzido no relatório do Bird. Como argumento, são citados o Plano Nacional de Dragagem, a Lei do Gás e o decreto com diretrizes para o desenvolvimento dos portos, todos no segundo mandato de Lula, além da nova tentativa de reforma tributária.<br />\n		Procurado ontem pela \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span>, o Ministério da Fazenda não havia comentado o documento do Banco Mundial até a conclusão desta edição.</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0704200907.htm');
INSERT INTO `conteudo` VALUES (39, 'Indústria sofre quedas recordes no 1º bimestre', '2009-04-08', '\n\n<p align="justify">\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">EDUARDO CUCOLO</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 8pt; FONT-FAMILY: Arial">DA FOLHA ONLINE, EM BRASÍLIA </span><br />\n	<br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A indústria brasileira fechou o primeiro bimestre do ano com queda recorde de faturamento, emprego e horas trabalhadas na produção, de acordo com a pesquisa mensal do setor realizada pela CNI (Confederação Nacional da Indústria).<br />\n		Diante dos números negativos, a confederação afasta a possibilidade de recuperação ainda no primeiro semestre deste ano e já fala em evitar um "desastre" ao final de 2009.<br />\n		Os principais indicadores da pesquisa mostraram queda de 10,9% no faturamento do setor, recuo de 7,8% nas horas trabalhadas e 0,9% no emprego. Todos os resultados são os piores da série histórica da entidade, iniciada em 2003.<br />\n		"Não vou dizer que o ano está perdido, pois pode haver uma recuperação no segundo semestre. A situação não está em processo de agravamento, mas está em um nível deprimido, que, se mantido até o fim do ano, será um desastre", disse o economista-chefe da CNI, Flávio Castelo Branco.<br />\n		De acordo com o economista, a indústria precisa crescer 9,3% nos próximos dez meses para fechar o ano no "zero a zero".</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A pesquisa apontou aumento do faturamento e das horas trabalhadas entre janeiro e fevereiro, mas isso se deve à base fraca de comparação. A massa salarial teve o menor avanço da série histórica. "Os impactos da redução da atividade industrial se manifestam fortemente no mercado de trabalho. Temos, até fevereiro, quatro meses seguidos de queda. E esse enfraquecimento no mercado começa a se refletir no rendimento", diz Castelo Branco.<br />\n		As indústrias mais afetadas no começo deste ano pela queda no faturamento foram as de metalurgia básica (-42,6%) e madeira (-23,7%), que aparecem entre os setores mais prejudicados por conta da queda nas exportações.<br />\n		O setor automotivo registrou queda de 10% no faturamento. Também caíram horas trabalhadas e emprego no bimestre. Na comparação com janeiro, o setor teve recuperação, devido ao aumento das vendas por causa da redução do IPI (Imposto sobre Produtos Industrializados).</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0804200904.htm');
INSERT INTO `conteudo` VALUES (40, 'DEFLAÇÃO - IGP-DI tem sua maior queda desde 1995', '2009-04-08', '\n<span style="FONT-SIZE: 10pt; COLOR: #000000; FONT-FAMILY: Arial">\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; COLOR: #000000">O IGP-DI (Índice Geral de Preços - Disponibilidade Interna) de março registrou a maior deflação desde setembro de 1995, com taxa negativa de 0,84%. Em fevereiro, o índice havia recuado 0,13%.<br />\n			O resultado decorre da forte retração dos preços no atacado, segundo a FGV. Na esteira da forte queda da demanda mundial e dos preços das commodities metálicas e agrícolas, cederam com força em março tanto os preços agrícolas como os industriais no atacado.<br />\n			"Não há mais dúvidas: a desaceleração brusca da atividade econômica surte efeitos devastadores sobre os preços no atacado, que até meados de 2008 apresentavam aceleração expressiva", diz a consultoria Rosenberg e Associados.<br />\n			Mas no varejo houve alta de preços. O IPC (Índice de Preços ao Consumidor) subiu 0,61% em março, com destaque para a alta, pontual, de alimentos.</span></p></span>', '', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0804200911.htm');
INSERT INTO `conteudo` VALUES (44, 'SENADOR JARBAS VASCONCELOS VIRÁ A FORTALEZA', '2009-04-08', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p>O senador peemedebista Jarbas Vasconcelos estará em Fortaleza no próximo mês de maio. Ele virá a convite do Centro Industrial do Ceará (CIC), quando falará sobre "Espírito Público". A palestra será no auditório da Casa da Indústria,&nbsp;dia 4, segunda-feira.</p>\n	\n<p>Em evidência na mídia nacional desde as suas declarações à revista Veja, em fevereiro último, quando acusou o PMDB de estar "sem bandeiras, sem propostas, sem norte" e boa parte dos seus filiados "quer mesmo é a corrupção", o senador pernambucano ocupou o plenário, no mês passado, para mais uma revelação explosiva: estaria sendo alvo de espionagem por parte de integrantes do seu próprio partido.</p>\n	\n<p>Aos 66 anos, dos quais 43 dedicados à politica, Jarbas Vasconcelos já exerceu, por dois mandatos, os cargos de governador de Pernambuco e prefeito do Recife e foi um dos fundadores do PMDB.</p>\n	\n<p>\n		<span style="FONT-WEIGHT: bold; FONT-STYLE: italic">Grandes nomes</span></p>\n	\n<p>A palestra do senador Jarbas Vasconcelos integra a programação do CIC pela comemoração dos 90 anos de sua fundação. Ao longo deste semestre, grandes nomes do cenário político, econômico e social do país virão a Fortaleza para debater temas atuais e de interessa da sociedade. </p></span>', '', 'EP', '', '');
INSERT INTO `conteudo` VALUES (45, 'IPCA desacelera em março para o menor patamar desde setembro de 2007', '2009-04-08', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A inflação oficial do Brasil, medida pelo </span><a href="http://economia.uol.com.br/ultnot/2007/09/14/ult5365u3.jhtm">\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">IPCA</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline"></span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, atingiu 0,2% em março, abaixo dos 0,55% registrados em fevereiro, no menor patamar desde setembro de 2007, quando havia marcado 0,18%, informou nesta quarta-feira o Instituto Brasileiro de Geografia e Estatística .<br />\n		<br />\n		O número de março foi inferior ao esperado por analistas. Segundo o último boletim Focus divulgado na segunda-feira pelo Banco Central, o mercado esperava que o indicador marcasse 0,25%.<br />\n		</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No primeiro trimestre, o IPCA acumulou 1,23%, inferior aos 1,52% registrados em igual período de 2008. Nos últimos 12 meses, a taxa está em 5,61%, também abaixo dos 5,9% registrados nos 12 meses imediatamente anteriores.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A principal contribuição para o índice em março veio do grupo Educação, "pois os efeitos da alta sazonal verificados no início do ano foram absorvidos no índice anterior, quando o grupo registrou alta de 4,77%", disse o IBGE em nota.<br />\n		<br />\n		Entre os demais grupos pesquisados pelo IBGE, a maior alta ficou com Vestuário (0,7%), seguida por Artigos de residência (0,48%). Em contrapartida, as quedas ficaram com Educação (-0,37%) e Transporte (-0,07%).<br />\n		<br />\n		Por região, Curitiba e Recife registraram os resultados mais elevados, com 0,39% e 0,37%, respectivamente, enquanto Salvador e Rio de Janeiro mostraram relativa estabilidade de preços com 0,02% e 0,03% cada um.<br />\n		<br />\n		O IPCA é o indicador oficial de preços do país, usado pelo governo para definir suas metas de inflação. Para 2009, o objetivo é que o índice marque alta de 4,5% nos preços, sendo tolerados até dois pontos percentuais para mais ou para menos.</span></p>', 'EP', 'Uol - Economia - Úlitmas Notícias', 'http://economia.uol.com.br/ultnot/2009/04/08/ult4294u2454.jhtm');
INSERT INTO `conteudo` VALUES (46, 'Governo quer desonerar folha de pagamento', '2009-04-08', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p>Em troca da redução de tributos, as empresas que aderissem à proposta teriam que garantir a manutenção de vagas<br />\n		<br />\n		Lula discute medidas com sindicalistas, que seriam "fiscais" do programa; corte de jornada sem redução no salário também é estudado</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">KENNEDY ALENCAR</span><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">LEANDRA PERES</span></span> \n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA SUCURSAL DE BRASÍLIA</span> </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O presidente Luiz Inácio Lula da Silva discute hoje com representantes das centrais sindicais proposta para reduzir encargos trabalhistas para empresários que aceitem não demitir na crise, incluindo o recolhimento menor do FGTS (Fundo de Garantia do Tempo de Serviço) dos trabalhadores.<br />\n		A \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span> apurou que os sindicalistas devem receber o esboço da MP por meio da qual o governo propõe também que as empresas reduzam em até 20% a jornada de trabalho sem cortar salários. Para compensar a empresa, o governo aceita diminuir de cerca de 30% para algo em torno de 19% o recolhimento de parte dos tributos cobrados sobre a folha salarial.<br />\n		O corte proposto é de 40% sobre a contribuição patronal ao INSS (20% sobre a folha), a contribuição para o Sistema S (3,1%) e o recolhimento mensal ao FGTS (8%).<br />\n		A adesão ao programa será opcional. A medida, que deverá ser temporária, é justificada pelo governo como uma forma de incentivar acordos de redução de jornada sem cortes de salários e evitar mais demissões. A suspensão dos cortes, porém, não valerá para trabalhadores temporários e programas de demissão voluntária.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Desde novembro, quando a crise se agravou, já foram fechados 798 mil vagas, segundo o Ministério do Trabalho. A piora na economia também custou pontos na popularidade de Lula. A mais recente pesquisa Datafolha mostrou queda de cinco pontos percentuais, para 65% de aprovação.<br />\n		O outro argumento do governo é que a proposta não traria perdas à União. O gasto com a queda na arrecadação da Previdência seria mais que compensado, segundo as projeções feitas, pela economia com o seguro-desemprego. A lógica é que, se o trabalhador for demitido, o governo terá que gastar com o seguro-desemprego por até sete meses no caso de quem estava empregado em setores mais afetados pela crise.<br />\n		Em vez dessa despesa, as empresas pagariam menos imposto. Uma dúvida é se os trabalhadores teriam de abrir mão de parte de seu FGTS em troca de emprego e salário, pelo menos enquanto durar o acordo.<br />\n		O governo conta com os sindicatos para evitar fraudes ou demissões desnecessárias. Como a negociação teria que passar pelo crivo de cada categoria, há o entendimento de que isso funcionaria como fiscalização.<br />\n		Além da contribuição para a Previdência, o Sistema S e o FGTS, a empresas no Brasil arcam, na folha de pagamento, com 2,5% do salário-educação, 0,5% do seguro de acidentes de trabalho e 0,2% a entidades como o Incra. Incluído o recolhimento ao FGTS, os gastos das empresas com a folha de pagamento chegam a 34,3% além do salário pago ao empregado.<br />\n		A contribuição ao INSS põe o Brasil entre os países que têm a tributação mais elevada sobre os salários, e a desoneração da folha é reivindicação histórica dos empresários. No México, por exemplo, o empregador recolhe 10,6% em contribuições sociais. O Brasil se compara a países como Suécia (24,5%), Espanha (23,2%) e Itália (24,3%).<br />\n		Propostas para diminuir essa carga não são novas. O governo já discutiu o assunto várias vezes. O projeto, hoje abandonado, de reforma tributária previa redução da contribuição patronal. O compromisso do governo era de queda escalonada de 20% a 14%. A dificuldade, no entanto, sempre foi o custo fiscal da medida. Para cada ponto percentual de redução no pagamento do INSS, o governo deixaria de arrecadar R$ 3 bilhões.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0804200902.htm');
INSERT INTO `conteudo` VALUES (47, 'TELINHA', '2009-04-10', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A convite do Centro Industrial do Ceará, o presidente da Allianz Seguros do Brasil, Max Thiermann, tomará parte do programa Ciclo de Debates, domingo, às 19 horas, na TVC. Thiermann abordará a crise econômica internacional e o mercado de seguros. Como debatedores atuarão: Celso Sakuraba e o vice-presidente do CIC, Ivo Machado. </span></p>', '', 'CE', 'Jornal O Povo - Col. Sônia Pinheiro', 'http://www.opovo.com.br/opovo/colunas/soniapinheiro/');
INSERT INTO `conteudo` VALUES (48, 'O INCENDIÁRIO', '2009-04-10', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O senador Jarbas Vasconcelos, aquele que na revista Veja detonou muita corrupção envolvendo o seu PMDB, dará palestra em Fortaleza dia 4 de maio. Às 8h30min, na Fiec, dentro do programa comemorativo dos 90 anos do CIC. <br />\n		</span></p>', '', 'CE', 'Jornal O Povo - Cidade - Col. Vertical', 'http://www.opovo.com.br/opovo/colunas/vertical/');
INSERT INTO `conteudo` VALUES (49, 'Governo estuda novos aumentos de impostos', '2009-04-10', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O governo está analisando uma nova rodada de aumento de carga tributária de alguns setores para desonerar outros que pretende estimular em meio à crise econômica. Há rumores de que o próximo a pagar tributos mais altos para compensar benefícios concedidos a outros setores será a indústria de cosméticos, além de bebidas.<br />\n		A medida seria aplicada para estimular alguns setores em meio à crise sem prejudicar a arrecadação fiscal. A receita do governo caiu 27% em fevereiro ante o mesmo mês de 2008.<br />\n		A ação já foi feita no final de março, quando o governo aumentou o IPI (Imposto sobre Produtos Industrializados) do cigarro. A carga maior sobre o produto veio para compensar a redução do imposto a materiais de construção e a prorrogação do benefício do IPI reduzido aos veículos.<br />\n		De Portugal, o presidente da Abihpec (associação da indústria de cosméticos), João Carlos Basilio da Silva, falou com a Folha. Ele disse estar "chocado" com a perspectiva de os impostos do setor subirem e que o governo não entrou em contato com a entidade para discutir o aumento. "A nossa perspectiva era de redução de impostos, e não de aumento."</span><br />\n	</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com ele, o setor já é punido com altas cargas tributárias e não há espaço para elevações. A incidência de impostos no setor varia de 40% do preço de fábrica, para produtos de higiene oral, a 94%, para tintas de cabelo, por exemplo, afirma Silva.<br />\n		Hoje, a expectativa da Abihpec é que o setor registre um crescimento real de 5% neste ano -três pontos percentuais acima da projeção do governo de expansão do PIB.<br />\n		Se esse aumento for concretizado, Silva considera que o setor não terá condições de atingir sua meta de crescimento. "Se o governo aumentar o imposto, vamos ter desemprego no setor", afirma.<br />\n		Silva disse também que, se o governo elevar a carga tributária dos cosméticos, a Abihpec vai reagir. "Não é uma medida de bom senso aumentar impostos de um setor em meio à crise. Isso pune injustamente um segmento em benefício de outro. O cosmético não é supérfluo, é uma necessidade", afirma o presidente da entidade.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">BOLSO</span><br />\n		O faturamento real do comércio no Estado do Rio de Janeiro recuou 0,6% em fevereiro na comparação com o mesmo mês de 2008, de acordo com levantamento da Fecomércio-RJ. Segundo a entidade, os grupos mais dependentes de financiamentos foram os mais prejudicados. O faturamento do grupo de bens duráveis caiu 2,7%. O comércio automotivo teve uma queda menos acentuada, de apenas 1%, em razão da redução do IPI, que favoreceu o faturamento das concessionárias de veículos.</span></p>', 'EP', 'Jornal Folha de São Paulo - Dinheiro - Col. Mercado Aberto', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1004200901.htm');
INSERT INTO `conteudo` VALUES (50, 'Emprego industrial recua pelo 5º mês', '2009-04-10', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">Na comparação com fevereiro de 2008, nível de vagas no setor teve a maior queda desde o início da série, em 2001</span></p>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">Crescimento de 1,9% na folha de pagamento das empresas, segundo o IBGE, é consequência dos gastos relacionados às demissões</span><br />\n		<br />\n		\n		<span style="FONT-SIZE: 8pt">\n			<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">JANAINA LAGE</span><br />\n			</span>\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			<span style="FONT-SIZE: 8pt">DA SUCURSAL DO RIO</span> </span><br />\n		<br />\n		</span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O nível de emprego industrial registrou queda de 1,3% em fevereiro na comparação com janeiro. O resultado representa o quinto mês seguido de taxas negativas. Segundo especialistas, os dados de fevereiro indicam que 2009 será um ano de corte de vagas no setor.<br />\n		Ante fevereiro de 2008, o emprego industrial teve queda de 4,2%. Trata-se do pior resultado desde o início da série histórica, em 2001. "Essa queda reflete os efeitos da crise e a base alta de comparação. Em fevereiro de 2008, a economia estava aquecida e a indústria estava na fase de contratações", diz Ariadne Vitoriano, analista da consultoria Tendências.<br />\n		Para Isabella Nunes, do IBGE, os resultados mostram que a reação da indústria no começo do ano não surtiu efeito sobre o mercado de trabalho.<br />\n		"O movimento de corte de vagas atingiu 13 dos 14 locais pesquisados. As maiores pressões vieram de São Paulo, Minas Gerais e Centro-Oeste. A crise atingiu a maior parte dos setores, principalmente meios de transporte e máquinas e equipamentos." Somente em São Paulo, a queda no nível de emprego industrial foi de 3,6% em relação a igual mês de 2008.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com cálculos do Iedi (Instituto de Estudos para o Desenvolvimento Industrial), desde outubro o nível de emprego na indústria caiu 5,2%. "Se a política econômica do governo brasileiro não agir de forma mais rápida e eficaz para a recuperação do crédito e do ânimo dos consumidores, o emprego industrial no país poderá sofrer, infelizmente, forte revés em 2009", diz o Iedi.<br />\n		Em fevereiro, houve reação da folha de pagamento, com alta de 1,9% em relação a janeiro, após quatro meses seguidos de queda nesse tipo de comparação. Apesar disso, Nunes avalia que o aumento está mais relacionado ao pagamento de demissões e indenizações do que a uma melhora do mercado.<br />\n		O indicador de horas pagas teve a maior queda desde o início da série, de 5,7% em relação a fevereiro de 2008. Na comparação com janeiro, o recuo foi de 0,4%.<br />\n		Tradicionalmente, o primeiro passo para a ampliação da mão de obra é o aumento do número de horas pagas. O empresário tende a pagar mais horas extras antes de optar pela contratação de funcionários.</span> \n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n<span style="FONT-SIZE: 10pt; COLOR: #000080; FONT-FAMILY: Arial">\n	\n<div align="justify">TRABALHO\n		<span style="FONT-SIZE: 10pt; COLOR: #000080; FONT-FAMILY: Arial">&nbsp;</span></div></span>\n<span style="FONT-SIZE: 10pt; COLOR: #000080; FONT-FAMILY: Arial">&nbsp;</span>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #000080; FONT-FAMILY: Arial"></span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">PAÍS VOLTOU A CRIAR VAGAS FORMAIS EM MARÇO, DIZ LUPI</span><br />\n	</div>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A indústria continuou demitindo mais trabalhadores do que contratando em março. Segundo o ministro do Trabalho, Carlos Lupi, a velocidade desse movimento foi menor do que em fevereiro, quando a indústria demitiu mais de 56 mil pessoas. Os dados do emprego formal em março, a serem divulgados na semana que vem, devem apontar saldo positivo de criação de vagas, disse ele. Em fevereiro, haviam sido criados 9.000 empregos no país, após perda acumulada de 797,5 mil postos de novembro a janeiro. Lupi também afirmou que os pedidos de seguro-desemprego voltaram à média histórica.</span></div>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1004200918.htm');
INSERT INTO `conteudo` VALUES (51, 'Indústria de base quer compensação tributária', '2009-04-11', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O crescente desequilíbrio da balança comercial brasileira de bens de capital provocado pela crise aumentou a pressa do setor por medidas de desoneração tributária. Paulo Godoy, presidente da Abdib (Associação Brasileira da Infraestrutura e Indústrias de Base), defende que os créditos tributários acumulados pelas indústrias de bens de capital possam ser usados para pagar quaisquer tributos e contribuições federais.<br />\n		De acordo com Godoy, o pedido está sendo feito ao governo desde a primeira reunião do comitê de monitoramento da crise. Desde o início das atividades do grupo, outros setores conseguiram benefícios concretos, como o automotivo e a construção civil.<br />\n		No entanto, segundo o presidente da Abdib, não há uma sinalização de que o pedido do setor de bens de capital será atendido pelo governo.<br />\n		A possibilidade de compensar créditos tributários ganha importância para o setor porque poderá preservar o caixa das empresas em um momento de escassez e encarecimento de crédito, afirma Godoy.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A comparação entre os dados do primeiro trimestre deste ano e do ano passado indica uma reversão da balança comercial de bens de capital sob encomenda.<br />\n		Antes, as exportações superavam as importações e o resultado foi de um saldo de US$ 157 milhões nos negócios feitos entre janeiro e março de 2008.<br />\n		Com a crise econômica internacional, as importações subiram e as exportações despencaram. O quadro se reverteu para um déficit acima de US$ 500 milhões nos três primeiros meses deste ano.<br />\n		Para Godoy, a compensação tributária não resolverá esse desequilíbrio, mas é uma medida que ajuda o setor a enfrentar a crise econômica sem se desestruturar.<br />\n		Segundo ele, a desativação da indústria de base brasileira por uma questão de conjuntura e por uma falta de competitividade com os produtos chineses pode prejudicar toda a infraestrutura industrial do país.<br />\n		Godoy afirma que o Brasil será muito punido se a indústria de base for desestruturada, pois é um setor que exige grandes investimentos para ser reorganizado.</span></p>', 'EP', 'Jornal Folha de São Paulo - Dinheiro - Col. Mercado Aberto', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1104200901.htm');
INSERT INTO `conteudo` VALUES (52, 'Empresas "congelam" aumentos a executivos', '2009-04-11', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Crise leva acionistas a serem austeros em pagamento a diretorias e conselhos Vivo, Petrobras, Bradesco, Usiminas e TIM reduziram ou mantiveram montante destinado a gastos com seus executivos neste ano </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 8pt">\n		<span style="FONT-SIZE: 8pt; FONT-FAMILY: Arial">SAMANTHA LIMA</span><br />\n		</span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n		<span style="FONT-SIZE: 8pt">DA SUCURSAL DO RIO</span> </span><br />\n	<br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em tempos de crise e de questionamentos sobre a remuneração de executivos, empresas têm mostrado cautela na fixação da remuneração de seus conselheiros e diretores para 2009. Levantamento da \n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span> mostra que, em cinco grandes companhias de capital aberto, seus acionistas mantiveram ou reduziram os recursos totais máximos para tais pagamentos, de seis que já definiram os valores deste ano.<br />\n		Uma das primeiras a sair na frente na redução dos salários dos executivos foi a holding da operadora Vivo, que a controla. O teto baixou 28%, de R$ 5,047 milhões para R$ 3,92 milhões entre 2008 e 2009. A empresa foi seguida pela TIM Participações, que cortou 41%, de R$ 11 milhões para R$ 6,5 milhões, e a Petrobras, que reduziu o máximo em 16%, de R$ 9,8 milhões para R$ 8,2 milhões.<br />\n		</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Mantiveram o mesmo patamar de 2008 em pagamentos a seus administradores a siderúrgica Usiminas (R$ 24 milhões) e o Bradesco (R$ 170 milhões). "A decisão de reduzir ou manter seus salários é uma demonstração de austeridade preventiva em relação à crise e vai ser seguida por outras companhias", diz o professor Haroldo Mota, da Fundação Dom Cabral, especialista em governança corporativa.<br />\n		Das seis companhias verificadas, a Light, concessionária de energia do Rio de Janeiro, foi a única que elevou o pagamento de seus executivos. Foi aprovado em assembleia com acionistas o pagamento global de R$ 1,9 milhão. O valor é 30% superior ao R$ 1,47 aprovado em 2008. A reportagem tentou entrar em contato com a empresa, mas não localizou ninguém para comentar.<br />\n		Especialistas acreditam que poderá haver outros casos como o da Light. "Diante da crise, a empresa pode estabelecer metas mais ambiciosas do que o normal e estimular os executivos com um prêmio a mais. Mas a política recessiva deverá ser a regra", diz Edison Garcia, superintendente da Amec (Associação dos Investidores do Mercado de Capitais).<br />\n		A estrutura de administração das companhias abertas compreende um grupo de conselheiros, que representam os acionistas, e uma diretoria executiva. Os conselheiros, que não são funcionários mas são remunerados, reunem-se periodicamente para avaliar decisões estratégicas e nomear os diretores e o presidente que estarão no dia a dia da empresa.<br />\n		A lei determina que as companhias abertas tornem público o valor total pagos a seus executivos e conselheiros ao divulgarem seus resultados financeiros anuais -sem detalhar pagamentos individuais.<br />\n		O valor estimado para pagamento dos administradores é apresentado nas assembleias de acionistas realizadas pelas empresas para apresentação das contas, que ocorrem até o próximo dia 30. Isso significa que a política de remuneração dos executivos é decidida a cada ano. A maioria das empresas ainda não realizou a assembleia neste ano. Vale e Braskem são duas das companhias que definem a remuneração dos executivos na próxima semana.<br />\n		Embora tivesse fixado, em 2008, o teto de R$ 9,8 milhões para pagar seus administradores, a Petrobras informou ter gasto R$ 8,2 milhões -valor que serviu de base para estabelecer o salário deste ano.</span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 12pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Usiminas se antecipa à CVM e já detalha salário da diretoria</p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">DA SUCURSAL DO RIO</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A Usiminas tomou uma decisão inédita no país, ao decidir informar, a partir deste ano, os valores que pagou individualmente a seus 8 executivos, 11 conselheiros de administração e 4 conselheiros fiscais.<br />\n		As informações constam do relatório anual de administração de 2008, divulgado em março. O documento informa, por exemplo, que o presidente Marco Antônio Castelo Branco, contratado em maio, recebeu R$ 770 mil em honorários em 2008, mais R$ 1,8 milhão em bônus e R$ 65 mil em previdência e seguro. Outro R$ 1,4 milhão referente a 2008 serão pagos ao longo deste ano.<br />\n		Wilson Brumer, presidente do conselho do grupo, recebeu R$ 970 mil em 2008. "A política está em sintonia com a tendência internacional de transparência e com os novos valores da marca Usiminas", diz André Andrade, assessor de relações institucionais da Usiminas.<br />\n		No mundo, a questão está em discussão depois que executivos de empresas em dificuldades que tiveram ajuda de governos não se constrangeram em receber bônus milionários.<br />\n		No Brasil, a CVM analisa as propostas feitas para mudança nas regras de divulgação. As novas normas devem entrar em vigor em 2010. Alegando segurança, as companhias defendem que seja divulgado só o valor global pago a conselheiros e diretores, como é feito hoje.<br />\n		"Se não abrir individualmente, ao menos que se separe por grupos, entre conselheiros e diretores e, em cada grupo, em quantas vezes o maior salário supera o menor. Isso ajuda a avaliar melhor a gestão", diz Lucy Souza, presidente da Apimec (associação de analistas e profissionais de mercado).<br />\n		"Não é bisbilhotice. O investidor tem direito de saber quanto sacrifica de seu lucro para remunerar executivos e se condiz com os resultados", diz Edison Garcia, superintendente da Associação dos Investidores do Mercado de Capitais.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1104200909.htm');
INSERT INTO `conteudo` VALUES (53, 'A crise em 4 visões', '2009-04-12', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Benedito Sverberi</span></p>\n\n<p align="justify">\n	\n<table dir="ltr" cellspacing="0" cellpadding="4" width="200" border="0">\n		\n<tbody>\n			\n<tr>\n				\n<td valign="center" bgcolor="#ffffff">\n					\n<p>\n						<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">VEJA TAMBÉM </span></p></td>\n			</tr>\n			\n<tr>\n				\n<td valign="center" bgcolor="#ffffff">\n					\n<p>\n						<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Exclusivo on-line</span></p>\n					\n<p><a href="http://www.cic.com.br/admin/conteudo/debate.shtml">\n							<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">Íntegra </span>\n							<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a></p></td>\n			</tr>\n		</tbody>\n	</table></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A crise internacional atingiu a atividade econômica brasileira numa velocidade inédita. Mas o país segue em uma situação privilegiada para recuperar o crescimento tão logo a fase mais intensa do choque externo se dissipe. Para acelerar a retomada, o governo ainda dispõe de armas de alto impacto, entre elas o estímulo dos investimentos públicos e privados, reduzindo os gastos com o custeio da máquina e da burocracia. Em essência, foi essa a avaliação dos quatro participantes de um debate promovido pelo Centro de Liderança Pública com o intuito de discutir o alcance da crise e como contorná-la. Foram quatro visões complementares, de observadores vindos de diferentes áreas de atuação. A seguir, os principais trechos da discussão.</span></p>\n\n<p align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">JOSÉ ROBERTO MENDONÇA DE BARROS - Economista, sócio da consultoria MB Associados, foi secretário de Política Econômica do Ministério da Fazenda </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Este será o ano da política monetária. Vamos entrar, depois de décadas, na esperada fase de juros de um dígito. Isso implica uma enorme e positiva mudança estrutural, mas que impõe desafios." </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Contágio no Brasil  "Os dados do PIB (produto interno bruto) no quarto trimestre de 2008 e do emprego, entre outros, mostraram que a reação das famílias e das empresas brasileiras à crise foi muito rápida. No caso do setor corporativo, a deterioração do crédito no exterior foi transmitida instantaneamente para dentro do país. Esta é a primeira crise on-line que o mundo está vivendo. A crise da integração total."</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ajuste  "No Brasil, o grosso do ajuste às novas condições já foi feito, especialmente na indústria. O ritmo de retração da economia passará a ser mais lento. Tivemos um degrau e agora vamos cair devagarzinho pelo que se pode chamar de efeitos secundários do primeiro ajuste."</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Governo  "Infelizmente, a resposta do governo tem sido muito modesta. Não há nenhuma perspectiva de melhoria do sistema tributário, até mesmo porque, é bom lembrar, a arrecadação já começou a cair. Vejo com muita preocupação e ceticismo a capacidade de avançar na melhoria da infraestrutura. Também na área de tecnologia, existe pouca disposição em avançar."</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">JOSÉ ROBERTO MENDONÇA DE BARROS - Economista, sócio da consultoria MB Associados, foi secretário de Política Econômica do Ministério da Fazenda </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Este será o ano da política monetária. Vamos entrar, depois de décadas, na esperada fase de juros de um dígito. Isso implica uma enorme e positiva mudança estrutural, mas que impõe desafios." </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Contágio no Brasil  "Os dados do PIB (produto interno bruto) no quarto trimestre de 2008 e do emprego, entre outros, mostraram que a reação das famílias e das empresas brasileiras à crise foi muito rápida. No caso do setor corporativo, a deterioração do crédito no exterior foi transmitida instantaneamente para dentro do país. Esta é a primeira crise on-line que o mundo está vivendo. A crise da integração total."</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ajuste  "No Brasil, o grosso do ajuste às novas condições já foi feito, especialmente na indústria. O ritmo de retração da economia passará a ser mais lento. Tivemos um degrau e agora vamos cair devagarzinho pelo que se pode chamar de efeitos secundários do primeiro ajuste."</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Governo  "Infelizmente, a resposta do governo tem sido muito modesta. Não há nenhuma perspectiva de melhoria do sistema tributário, até mesmo porque, é bom lembrar, a arrecadação já começou a cair. Vejo com muita preocupação e ceticismo a capacidade de avançar na melhoria da infraestrutura. Também na área de tecnologia, existe pouca disposição em avançar."</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Limitações fiscais  "A política fiscal poderia desempenhar um importante papel na indução do crescimento e na coordenação da recuperação. Entretanto, há muito pouco que possa ser feito no âmbito do governo federal, porque o bônus da arrecadação elevada dos últimos anos foi quase totalmente dissipado em empregar pessoas e em gastos de custeio."</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Propostas  "As políticas públicas deveriam buscar, de um lado, amenizar os efeitos sociais da crise e, de outro, desenvolver e pôr em prática um conjunto de medidas que aumente a competitividade do país. Muitas obras de infraestrutura estão hoje bloqueadas simplesmente pela incapacidade de algumas empresas em apresentar garantias. Essa é uma deficiência que acho factível que o governo consiga reduzir. Na área regulatória, há muito que se pode fazer e, aqui, o foco deve ser resolver suas próprias dubiedades. O Executivo também poderia desenhar medida de apoio a novas empresas, especialmente aquelas de base tecnológica." </span></p>\n\n<p align="justify"><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">PEDRO PARENTE - Engenheiro, vice-presidente do grupo RBS, foi ministro da Casa Civil e do Planejamento</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O governo tem um papel fundamental, que é coordenar expectativas. É preciso ficar claro que as medidas têm de ser adotadas no prazo certo, com objetividade, sem um discurso político por trás delas".</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Otimismo  "Há razões para certa visão positiva a respeito da nossa situação. Em primeiro lugar, não temos uma crise bancária, como nos Estados Unidos. A situação do sistema bancário brasileiro é extraordinária. No lado fiscal, tivemos um efeito estatístico, em função do recálculo do PIB, que foi positivo para o tamanho da nossa dívida. Outro fato extremamente importante foi a desdolarização da dívida pública. Assim, a recente desvalorização cambial não nos afetou como em crises anteriores. As reservas internacionais não tiveram uma queda relevante. Continuam lá no nível dos 200 bilhões de dólares. Por fim, as empresas no Brasil estão capitalizadas. A economia do país segue em uma situação propícia e estará muito receptiva a medidas pontuais que o governo venha a tomar."</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Expectativas  "O governo tem um papel fundamental, que é o de coordenação de expectativas. Se elas melhorarem, o país poderá vivenciar realizações que me parecem essenciais: os bancos poderão se sentir mais confortáveis para reduzir seus juros e emprestar mais, e as empresas poderão voltar a fazer investimentos." </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O governo tem um papel fundamental, que é o de coordenação de expectativas. Se elas melhorarem, o país poderá vivenciar realizações que me parecem essenciais: os bancos poderão se sentir mais confortáveis para reduzir seus juros e emprestar mais, e as empresas poderão voltar a fazer investimentos." </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">ABILIO DINIZ - Administrador de empresas, é presidente do conselho de administração do grupo Pão de Açúcar</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O governo tem de investir em setores que ofereçam mais empregos. Precisamos garantir, por exemplo, que a construção civil não pare, <br />\n		principalmente a residencial".</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Crise de crenças  "Esta é uma crise originada na ganância e em ideias erradas. Na crença, por exemplo, de que é muito fácil e rápido ganhar dinheiro, de que a rentabilidade poderia se manter em níveis extraordinários para sempre. Esta é uma crise originada no setor privado. Depois é que os governos foram envolvidos. O máximo que se pode dizer é que o poder público talvez tenha sido negligente em suas atribuições ao deixar acontecer essas coisas." </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"> "Esta é uma crise originada na ganância e em ideias erradas. Na crença, por exemplo, de que é muito fácil e rápido ganhar dinheiro, de que a rentabilidade poderia se manter em níveis extraordinários para sempre. Esta é uma crise originada no setor privado. Depois é que os governos foram envolvidos. O máximo que se pode dizer é que o poder público talvez tenha sido negligente em suas atribuições ao deixar acontecer essas coisas." </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Seleção natural  "As crises separam os ganhadores dos perdedores e é natural que ocorram fusões e aquisições. Contudo, à medida que isso ocorre, verifica-se redução no mercado de trabalho. Outro ponto que não pode ser esquecido é que, quando se observa um processo de consolidação, deixam de ser criados ativos novos, o que retarda o processo de retomada da economia."</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Emprego  "Não estou preocupado com a indústria. A sensação que tenho é que nela já ocorreu o grosso do ajuste. Estou preocupado com outros setores que não o fizeram e que empregam muita gente neste país, como a construção civil."</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">LUIZ PAULO VELLOZO LUCAS - Deputado federal (PSDB-ES), é presidente do Instituto Teotônio Vilela</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O investimento público é fundamental na ampliação da competitividade do país, no combate ao desemprego e na preparação para o futuro. E para<br />\n		isso precisamos de reformas".</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ação insuficiente  "As medidas que foram adotadas pontualmente pelo governo, como a liberação do compulsório e a redução do IPI dos automóveis, estavam na direção correta. Mas infelizmente não formam um conjunto coerente, fundamentado em um diagnóstico verdadeiro da situação do país e em uma comunicação transparente das dificuldades."</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Investimento público  "Neste momento de crise, a ampliação do investimento público é vista como uma das saídas possíveis. O problema é que o nível do investimento no Brasil é muito baixo, tanto como porcentual do PIB quanto da arrecadação. O destino da vultosa expansão de receita do governo nos últimos anos, principalmente da União, foi o aumento dos gastos com pessoal e custeio."</span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Estados e municípios  "Os estados e municípios têm de cumprir à risca a Lei de Responsabilidade Fiscal, e o governo federal não. A atual metodologia de controle das contas públicas praticamente obriga municípios saudáveis a fazer poupança e torna muito difícil a contratação de empréstimos."</span></p>', 'EP', 'revista Veja', 'http://veja.abril.com.br/150409/p_056.shtml');
INSERT INTO `conteudo` VALUES (54, 'PRIMEIRA LINHA', '2009-04-12', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p>A convite do Centro Industrial do Ceará (CIC), o senador Jarbas Vasconcelos fará palestra sobre o tema "Espírito Público", no dia 4 de maio.</p></span>', '', 'CE', 'jornal O Povo - Caderno People - Col. De A a Z - Luis Carlos Martins', 'http://www.opovo.com.br/opovo/people/868980.html');
INSERT INTO `conteudo` VALUES (55, 'CIC: ouvindo Jarbas', '2009-04-12', '\n\n<p>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Pela notoriedade que ganhou, denunciando o seu próprio partido, a quem acusa de procurar conviver com a corrupção praticada por muitos de seus membros, o senador Jarbas Vasconcelos tem sido convidado para falar em diferentes eventos por este Brasil afora, como o programado para Fortaleza. No próximo dia 4 de maio, atendendo a convite do Centro Industrial do Ceará, aqui estará para falar, no auditório da Fiec, sobre ´Espírito Público´. Pelo seu histórico neste Estado, não surpreende que o CIC esteja sempre procurando discutir e debater questões de natureza política e que envolvam o interesse público. Daí porque a iniciativa de trazer o senador e ex-governador de Pernambuco Jarbas Vasconcelos para a abordagem de uma questão que tanta repercussão ganhou no País, apenas serviu para comprovar que, seguindo a tradição da entidade - com Beni, Sérgio, Tasso, Amarílio - seus atuais dirigentes resolveram promover o debate, objeto das denúncias em questão.</span></p>', '', 'CE', 'jornal Diário do Nordeste - Política - Col. Edilmar Norões', 'http://diariodonordeste.globo.com/materia.asp?codigo=630091');
INSERT INTO `conteudo` VALUES (56, 'Crise faz indústria perder projeção externa', '2009-04-13', '\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Demanda e crédito em queda levam fatia da produção destinada ao mercado internacional ao menor nível desde 2002</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Volume exportado caiu para 21,9% no fim de 2008, após ter atingido 25,2% em 2005; produtos de couro e móveis estão entre os mais afetados <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">SAMANTHA LIMA</span><br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA SUCURSAL DO RIO </span><br />\n		<br />\n		As fábricas brasileiras de bens de maior valor agregado, que se esforçaram para conquistar o mercado externo nos últimos anos, viram a crise derrubar a exportação de seus produtos ao menor patamar desde o fim de 2002. A queda na demanda e a escassez de crédito ajudam a explicar o fenômeno.<br />\n		Levantamento da Firjan (federação das indústrias do Rio) mostra que o percentual exportado da indústria de transformação nacional caiu a 21,9% do volume produzido ao fim de 2008. De 2003 a 2007, as vendas externas chegaram a 24,1% do que era produzido, com um pico de 25,2% em 2005.<br />\n		A indústria de transformação compreende o setor que submete determinado produto a processo para transformá-lo em um outro bem de maior valor agregado, com o uso de máquinas ou equipamentos. Estão excluídos os setores extrativos, como a produção de petróleo e de minério de ferro, cujos preços têm cotação internacional.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De 2007 a 2008, o índice revelara tendência de queda, explicada pelo aquecimento do mercado interno. "Estava mais atraente vender internamente", diz Patrick Aguiar Carvalho, chefe de estudos econômicos da Firjan. "Desde setembro, porém, o mercado interno também se contrai. A crise continuará a derrubar a inserção dos produtos do país no exterior."<br />\n		A empresária carioca Gilda Sampaio sentiu em seus negócios o fechamento do mercado externo. Fundada há 20 anos, sua fábrica de bolsas de couro, a Dautore, havia conseguido fechar contratos de representação com revendedores em Nova York e Paris há três anos.<br />\n		No início do ano passado, as vendas externas chegaram a representar metade das 2.000 bolsas produzidas mensalmente. "Hoje, não chega a 20% da produção", diz. "Não houve encomendas canceladas, mas ficaram com estoque e sinalizaram que vão comprar menos."<br />\n		Em 2007, a Dautore havia ampliado a fábrica e contratado. Com a retração, a produção de bolsas caiu pela metade, e 30% dos empregados foram demitidos. "Mudamos fornecedores de couro para reduzir os custos em 20%", diz Gilda.<br />\n		Como mostra a Dautore, o ramo de produtos de couro é um dos mais afetados, segundo a Firjan. O percentual exportado caiu de 70% para 59%. Na indústria de móveis, foi de 69% para 59%, e na de minerais não metálicos, de 18% para 12%.<br />\n		Os dados revelam que a crise representou para o setor impacto maior do que o câmbio de 2005 a 2007, quando a indústria dizia que a desvalorização do dólar tirava a competitividade dos produtos. "Agora, mesmo com a valorização do dólar, as exportações não cresceram. Os importadores lá fora sofrem por falta de crédito", diz José Augusto de Castro, vice-presidente da AEB (Associação de Comércio Exterior do Brasil).<br />\n		Para as fábricas que viram seus clientes estrangeiros suspenderem encomenda, não resta saída que não se ajustar para produzir menos. "O mercado interno também está contraído, o desemprego cresce e a renda dá sinais de que não crescerá mais como antes, portanto não vai absorver essa produção", diz o economista Rogério César de Souza, do Iedi (Instituto de Estudos para o Desenvolvimento Industrial).</span></p>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 12pt; FONT-FAMILY: Arial">Empresa pode perder mercado e eficiência</span></p>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">DA SUCURSAL DO RIO</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Quando a indústria passa a exportar menos, incorre em dois riscos, segundo especialistas.<br />\n		O primeiro é a perda do mercado conquistado. "Quando se retira do mercado, a indústria que produz bens de maior valor agregado abre espaço para outros produtores. E não tenho dúvidas de que, neste momento, a China vai dominar os mercados de manufaturados que deixarmos vagos", diz José Augusto de Castro, vice-presidente da AEB (Associação de Comércio Exterior do Brasil).<br />\n		Segundo ele, a China desenvolveu uma estratégia agressiva para continuar exportando para outras economias. "O governo chinês está fornecendo crédito para os países, às vezes até em troca de mercadorias no futuro, como petróleo, no caso de países produtores." Uma vez perdido um mercado no exterior, reconquistá-lo pode ser tarefa de anos.<br />\n		O outro risco é a perda de eficiência e competitividade. Quando uma empresa se internacionaliza, concorre globalmente. "Para isso, tem que aprender a produzir mais e melhor gastando menos. Esse processo pode se perder num momento de crise", diz Patrick Aguiar Carvalho, chefe de pesquisas econômicas da Firjan.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1304200902.htm');
INSERT INTO `conteudo` VALUES (57, 'Emprego na indústria recua pelo quinto mês consecutivo', '2009-04-13', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2440875,860,10,9/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">emprego</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">na indústria caiu 1,3% em fevereiro, na comparação com janeiro, informou ontem o Instituto Brasileiro de Geografia e Estatística (IBGE). Esta é a quinta redução consecutiva na comparação com o mês imediatamente anterior, já descontados os efeitos sazonais. Em relação a fevereiro de 2008, houve queda de 4,2%, a maior desde o início da série histórica, em 2001. O quadro de emprego industrial acumula retração de 3,4% nos primeiro dois meses deste ano. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Treze das 14 regiões pesquisadas pelo IBGE e treze dos 18 setores reduziram o contingente de trabalhadores em relação a fevereiro de 2008. São Paulo (-3,6%), Minas Gerais (-5,5%) e região Norte e Centro-Oeste (-6,7%) exerceram as pressões negativas mais significativas no total do país. </span></p>', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Na indústria paulista, a maioria dos segmentos (13) teve resultado negativo, com destaque para as contribuições de produtos de metal (-8,2%), borracha e plástico (-9,1%), de máquinas e equipamentos (-5,7%) e meios de transporte (-5,4%). Este último, com queda de 12,3%, tem o principal impacto negativo em Minas Gerais, enquanto na região Norte e Centro-Oeste, destacam-se os recuos em madeira (-23,1%) e máquinas e aparelhos eletroeletrônicos e de comunicações (-14,6%). Por outro lado, somente Pernambuco (0,8%) aumentou o emprego, nesta base de comparação. </p>\n	\n<p align="justify">Segundo o IBGE, vale ressaltar que desde setembro, quando houve o agravamento da crise financeira mundial, as fábricas que mais contribuíram para a aceleração da queda foram: máquinas e equipamentos (de 9,9% para -4,1%), meios de transporte (de 8,2% para -4,7%) e máquinas e aparelhos eletroeletrônicos e de comunicações (de 10,0% para -2,9%). São exatamente esses setores nos quais a redução no ritmo produtivo foi mais aguda nesse período. </p>\n	\n<p align="justify">Folha de pagamentosO número de horas pagas aos trabalhadores da indústria recuou 0,4% em fevereiro se comparado com janeiro (sem ajuste sazonal), segundo o IBGE. É a quinta taxa negativa consecutiva nessa comparação, acumulando perda de 5,7% desde outubro do ano passado. O indicador de <a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2440875,860,10,9/l">\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">média móvel</span>\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>trimestral assinalou recuo de 1,3% em fevereiro, redução menos acentuada que a de janeiro (-1,7%). </p>\n	\n<p align="justify">Em relação a fevereiro de 2008, o número de horas pagas caiu 5,7%, menor taxa da série histórica iniciada em 2001. No primeiro bimestre do ano, a queda de 4,7% ficou bem abaixo do resultado do quarto trimestre de 2008 (-0,2%), ambas as comparações contra igual período do ano anterior. O indicador acumulado nos últimos doze meses, em trajetória descendente desde setembro (2,7%), atingiu em fevereiro 0,6%, a menor taxa desde março de 2007 (0,6%). </p>\n	\n<p align="justify">"Os indicadores negativos do emprego e do número de horas pagas em fevereiro foram diretamente influenciados por fatores relacionados à redução generalizada na atividade fabril, que vem afetando especialmente os ramos produtores de bens de consumo duráveis, intermediários e bens de capital", destaca o Instituto. </p>\n	\n<p align="justify">Apesar disso, o valor da folha de pagamento real da indústria, excluindo os efeitos sazonais, voltou a <a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2440875,860,10,9/l">\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">crescer</span>\n			<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>(1,9%) em relação ao mês imediatamente anterior, após quatros resultados negativos consecutivos, período em que registrou perda acumulada de 4,9%. O índice de média móvel trimestral ficou praticamente estável (-0,1%) entre janeiro e fevereiro, após mostrar quedas mais acentuadas nos dois últimos meses: dezembro (-1,1%) e janeiro (-1,5%). </p>\n	\n<p align="justify">No acumulado do primeiro bimestre, o aumento de 1,2% no valor real de pagamento da folha ficou abaixo do índice verificado para o aumento real auferido no quarto trimestre do ano passado (4,3%). No indicador acumulado nos últimos doze meses (5,3%), prosseguiu em trajetória descendente desde setembro passado (6,7%). </p></span>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2440875,860,10,9');
INSERT INTO `conteudo` VALUES (58, 'CONCESSÃO DE INCENTIVOS - Os mais fortes saem na frente', '2009-04-13', '\n\n<p>\n	<span style="FONT-STYLE: italic; FONT-FAMILY: Arial">Bahia, Pernambuco e Ceará acabam por concentrar a maior parte dos incentivos fiscais destinados ao Nordeste<br />\n		</span><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Vencem os mais fortes  ou os menos fracos. O titular da Superintendência de Desenvolvimento do Nordeste (Sudene), Paulo Fontana, admite que a política de incentivos fiscais adotada pelos governadores vem beneficiando, principalmente, três estados nordestinos: Bahia, Ceará e Pernambuco. De acordo com o superintendente, os incentivos de mais peso, que realmente ´puxam´ as empresas para o Nordeste, são os de âmbito federal, concedidos pela Sudene. Uma vez interessadas na região, as empresas acabam sendo atraídas, em sua grande maioria, por estas três unidades da Federação.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">´O Estado maior leva a melhor´, confirma. Prova disso é que mais de 50% dos incentivos fiscais concedidos pelo órgão se dirigiram para estes três estados. ´O empresário escolhe pela infraestrutura e pelo mercado consumidor do Estado. É uma decisão dos industriais´.<br />\n		<br />\n		Os incentivos garantiram a vinda de indústrias ao Nordeste, mas manteve  ou mesmo potencializou  as diferenças intra-regionais. No ano passado, a Sudene concedeu 408 benefícios, somando, segundo prevê Fontana (os dados fechados ainda não saíram), mais de R$ 2 bilhões. O Ceará ficou com 60 destes incentivos.<br />\n		<br />\n		O estudo ´Guerra fiscal do ICMS: quem ganha e quem perde´, do economista Ricardo Varsano, confirma este fato. ´Os vencedores da guerra são os estados financeiramente mais poderosos, capazes de suportar o ônus das renúncias e, ainda assim, assegurar razoáveis condições de produção´, analisa o texto. ´Com o passar do tempo, as renúncias fiscais se avolumam, e os estados de menor poder financeiro perdem a capacidade de prover os serviços e a infra-estrutura de que as empresas necessitam para produzir e escoar a produção. As batalhas da guerra fiscal passam a ser vencidas somente pelos de maior poder financeiro, que são também os que têm acesso mais fácil a crédito´, complementa. <br />\n		<br />\n		</span>\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">GANHADOR DA ´GUERRA´<br />\n		\n		<span style="FONT-SIZE: 12pt">Estado da Bahia é o maior beneficiado</span><br />\n		<br />\n		</span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Maior beneficiado com guerra fiscal, o Estado da Bahia vem apresentando forte incremento no número de indústrias nos últimos anos, e apresenta, além de uma boa infra-estrutura para as empresas, uma das mais agressivas políticas de renúncia fiscal.<br />\n		<br />\n		De 2005 a 2008, foram 368 empreendimentos beneficiados com o Desenvolve, programa de incentivos fiscais do Estado. Ao todo, os pleitos somaram quase R$ 70 bilhões em investimentos, com capacidade de geração de 112..932 empregos. ´Nós tivemos, infelizmente, que fazer a política de incentivos fiscais. Infelizmente, porque acontece a renúncia, mas, sem ela, não teríamos atraído as empresas que atraímos neste período´, avalia o diretor de Tributação da Secretaria da Fazenda do Estado da Bahia, Jorge Gonzaga.<br />\n		<br />\n		Os setores da agroindústria, metalurgia (ferro e cobre), geração de energia, transformação plástica, química e petroquímica, bebidas, equipamentos para irrigação, medicamentos (vacinas) e alimentos são os beneficiados com o programa. Entretanto, a cadeia de produtos petroquímicos tem um maior favorecimento.<br />\n		<br />\n		O desconto no ICMS pode chegar, na Bahia, a até 90%, tendo um prazo para pagamento do saldo de até seis anos. Têm direito a uma maior redução no imposto os empreendimentos que integrarem a uma cadeia produtiva, gerarem mais empregos, investirem em desenvolvimento tecnológico e responsabilidade ambiental, tiverem menor impacto ambiental e se distanciarem dos centros urbanos. Entretanto, apesar de apontar os ganhos com a política, ele admite que terá um esgotamento do instrumento. ´Se fossem somente os estados do Nordeste que oferecessem os incentivos, ainda seria bom. Mas agora, o Centro-Oeste também está entrando, o que complica´, preocupa-se. <br />\n		<br />\n		</span>\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">ENTENDA O FDI<br />\n		Programa incentiva a interiorização<br />\n		<br />\n		</span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Modificado no ano passado, o programa de incentivos fiscais para atração de indústrias ao Estado, o FDI (Fundo de Desenvolvimento Regional), procura ser mais seletivo na concessão dos benefícios, levando em consideração, especialmente, a descentralização da indústria, parâmetro utilizado também nos outros estados nordestinos. O valor máximo do incentivo é de 75% sobre o ICMS a ser recolhido, e o período do incentivo pode durar de cinco a 10 anos, renovável por igual período. O prazo de carência para o pagamento do saldo é de 36 meses, e o retorno pode variar de 25% a 1%. Ou seja, caso uma empresa tenha o incentivo máximo, a cada R$ 100 a ser recolhido, ela pagará R$ 25, e o restante ficará para o período do retorno. Quando chegar este momento, sobre estes R$ 75 de saldo, reajustados, a empresa irá pagar de 25% a apenas 1%.<br />\n		<br />\n		Para definir qual será o percentual de benefício, são levados em consideração cinco itens: geração de empregos, movimentação de mercadorias que o empreendimento vai gerar, localização, responsabilidade social e investimento em pesquisa e desenvolvimento. Segundo o gerente da Adece, Eduardo Neves, o programa ainda dá diferimento sobre a compra de máquinas e equipamentos, ou seja, a empresa não paga o ICMS na compra destes materiais, somente quando for se desfazer deles. Outra vantagem é a relação entre fornecedor incentivado e empresa incentivada. Nas transações comerciais entre estas empresas, não há pagamento do imposto. ´O incentivo fiscal é apenas um item. Vários fatores são levados em conta pelo empresário na hora de decidir a localização de um empreendimento´.</span></p>', 'EP', 'jornal Diário do Nordeste - Negócios', 'http://diariodonordeste.globo.com/materia.asp?codigo=630292');
INSERT INTO `conteudo` VALUES (59, 'CIC - PALESTRA COM JARBAS VASCONCELOS', '2009-04-13', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Não é nenhum partido político, mas o CIC, que vai promover palestra do senador Jarbas Vasconcelos (PMDB-PE), no Auditório da Fiec. O tema [e a corrupção nos partidos políticos. Resta saber qual será a participaçãodos políticos do PMDB cearense no referido evento.</p></span>', '', 'CE', 'jornal O Estado - Política - Col. Diário Politico - Fernando Maia', '');
INSERT INTO `conteudo` VALUES (60, 'AGORA VAI', '2009-04-10', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">O senador Jarbas Vasconcelos estará em Fortaleza em maio. A convite do CIC, falará de "Espírito Público". A palestra será na Fiec, às 8 horas, do dia 4. Não seria Espírito de Porco?</p></span>', '', 'CE', 'jornal O Estado - Política - Col. Macário Batista', 'http://www.oestadoce.com.br/?acao=colunas&subacao=ler&colID=6');
INSERT INTO `conteudo` VALUES (61, 'Redução de custos ', '2009-04-14', '\n\n<div>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; COLOR: #000080; FONT-FAMILY: Arial">BENJAMIN STEINBRUCH</span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"><br />\n		</span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></div>\n\n<div>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">TENHO DEFENDIDO a ideia de que, na atual crise, é muito importante evitar a disseminação do medo. Mantenho essa opinião, mas considero que chegou o momento de medidas corajosas para impedir o aprofundamento da queda de produção e do emprego. <br />\n		Sem tentar maximizar a gravidade da crise, seria prudente pensar com serenidade em uma ampla redução de custos no país. E a primeira medida nessa direção seria um corte geral de impostos e outros encargos. <br />\n		Nunca houve dúvidas sobre o impacto positivo da desoneração tributária na atividade econômica. Mas foi preciso que o país vivesse a experiência feita com a indústria automobilística nos últimos quatro meses para constatar o efeito prático dessa medida. <br />\n		</span></div>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Com a redução do IPI sobre os veículos, a indústria automobilística recuperou-se rapidamente. A produção cresceu em janeiro, fevereiro e março e hoje opera em nível próximo ao de seu recorde histórico -272 mil veículos em março. Nenhum outro setor importante no mundo teve desempenho semelhante no primeiro trimestre, no olho do furacão da crise global. <br />\n		Além da indústria veículos, outros setores já foram beneficiados por desonerações tributárias, como os segmentos de material de construção e de algumas atividades na região da Sudam. Foram medidas corretas, que poderiam atingir também material escolar, geladeiras, fogões e outros eletrodomésticos. <br />\n		No atual estágio da crise, porém, faz sentido pensar em medidas com efeitos gerais, de aplicação mais simples que as ações pontuais e que possam beneficiar toda a economia. Uma redução ampla de impostos, inclusive com desoneração das folhas de pagamento, certamente traria benefícios maiores à reativação da atividade e à criação de empregos. <br />\n		Para não comprometer as contas públicas, essa redução teria de ser acompanhada por um corte geral de gastos correntes (não de investimentos). Infelizmente, o governo não age nessa direção. Para compensar as perdas de receitas decorrentes das desonerações que já fez, estimadas em R$ 2,5 bilhões de dezembro a junho no caso do IPI, optou pelo aumento da alíquota de IPI sobre cigarros. Agora, pretende taxar mais também as bebidas. Essas medidas são corretas, mas insuficientes. <br />\n		Atuação mais eficiente exige também o controle de gastos correntes, entre eles os reajustes de salários de servidores, nos três níveis de governo. Quem se der ao trabalho de fazer uma busca no Google Notícias, escrevendo a expressão "reajuste dos servidores", verá que, só na última semana, o site registrou 879 notícias sobre esse tema, numa constatação de que as reivindicações de reajustes se espalham por todo o país, em Estados e municípios. Nos últimos dois anos, segundo levantamento da Folha, os gastos com servidores do Executivo cresceram 25,2% nos Estados e 26% nos municípios, para uma inflação de 10,6% no período. <br />\n		Na maioria dos casos, os pedidos de aumentos dos servidores são justificáveis, dadas as remunerações historicamente baixas. Mas, na atual situação de crise, cabe também aos servidores a responsabilidade de zelar pela condição financeira do setor público. Até porque não adianta conseguir gordos reajustes se depois o Estado vier a quebrar. <br />\n		Além do corte geral de impostos, há que apressar a redução de juros, via Selic e via "spread" bancário. Está certo o governo ao pressionar os bancos públicos a liderarem o corte do "spread". A redução geral de custos no país é uma exigência inadiável. <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">BENJAMIN STEINBRUCH</span></span>\n	<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">, 55, empresário, é diretor-presidente da Companhia Siderúrgica Nacional, presidente do conselho de administração da empresa e primeiro vice-presidente da Fiesp (Federação das Indústrias do Estado de São Paulo). \n		\n<p>&nbsp;</p></span></p>\n\n<p>&nbsp;</p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'Com a redução do IPI sobre os veículos, a indústria automobilística recuperou-se rapidamente. A produção cresceu em janeiro, fevereiro e março e hoje opera em nível próximo ao de seu recorde histórico -272 mil veículos em março. Nenhum outro setor importante no mundo teve desempenho semelhante no primeiro trimestre, no olho do furacão da crise global. ');
INSERT INTO `conteudo` VALUES (62, 'Empresários preveem mais investimento', '2009-04-15', '\n\n<p>O indicador Sensor da Fiesp, elaborado quinzenalmente para detectar a perspectivas dos empresários da indústria, mostra que melhoraram as expectativas em relação às condições de mercado e aos investimentos na primeira quinzena de abril.<br />\n	O Sensor para os investimentos, que registrou 38,9 pontos na sondagem realizada na segunda quinzena de janeiro, atingiu 51,9 pontos na primeira quinzena deste mês. O número 50 indica neutralidade de expectativa nas expectativas dos empresários da indústria.<br />\n	A sensação para as vendas foi positiva. Para o emprego, a expectativa dos empresários é de neutralidade, e, para os estoques, a avaliação é negativa.<br />\n	"Nós estamos em uma fase mais suave de ajustes, e o Sensor nos diz que tendemos para a neutralidade do emprego", afirma Paulo Francini, diretor do Departamento de Pesquisas e Estudos Econômicos da Fiesp.<br />\n	Francini avalia que os resultados do Sensor nesta quinzena reforçam a ideia de que a indústria de São Paulo deve se recuperar nos próximos meses.</p>', '', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1504200906.htm');
INSERT INTO `conteudo` VALUES (63, 'A crise e o País, um balanço até o momento', '2009-04-15', '\n\n<p>Os mercados internacionais apresentaram, durante as últimas semanas, desempenho muito positivo, acompanhando a queda acentuada do grau global de aversão a risco. </p>\n\n<p>Aparentemente, os agentes econômicos decidiram reduzir a probabilidade dada a cenários extremos pessimistas, assumindo que a postura de intervenção "sem limites" por parte de bancos centrais, governos e até organizações multilaterais será suficiente para evitar um cenário depressivo/deflacionário. Nesse contexto, destacou-se também a divulgação de indicadores de atividade econômica que surpreenderam positivamente as projeções dos analistas, apresentando evidências de estabilização após o colapso observado depois do quarto trimestre do ano passado. </p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Enxergar nessa melhora uma evidência de "fim da crise", no entanto, seria ainda exagerado otimismo. Os efeitos colaterais do cenário de contração da atividade econômica global sobre o nível de emprego e a capacidade de pagamento de empresas e famílias ainda representam um fator de risco relevante sobre a dinâmica de retomada. E a capacidade de expansão da oferta de crédito nos países desenvolvidos permanece limitada por uma situação patrimonial dos bancos ainda muito frágil, mesmo em um cenário em que a distribuição de perdas entre contribuintes e o sistema financeiro tem se dado com maior prejuízo para os primeiros. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Desta forma, em um cenário no qual o risco de "queda livre" chegou a ser considerado realista, evidências de estabilização devem ser entendidas como passos iniciais em direção à retomada. Mas não se deve alimentar a ilusão de retorno a um cenário semelhante ao que vigorava antes que se iniciasse o período mais agudo da crise: a dinâmica da economia mundial mudou para pior e as </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2445525,18,1,1/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">ações</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">implementadas com vistas a evitar um cenário de crise ainda mais agudo não são isentas de custo a médio e longo prazos. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Sem admitir, portanto, que se trata de um balanço pós-crise, creio ser válido um exercício de avaliação das condições da economia brasileira a partir de destaques positivos e negativos em meio a um cenário global adverso. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">1) Embora tendo sua economia considerada usualmente como pouco aberta e com fundamentos reconhecidamente sólidos, o Brasil sofreu os efeitos da contração global em forte intensidade, devendo apresentar taxa negativa no crescimento do PIB em 2009, após expansão superior a 5% no ano passado. Fica evidente, assim, a elevada correlação entre o ciclo doméstico e o internacional - que, a propósito, teve contribuição decisiva também na fase ascendente da economia global e que sugere que uma retomada consistente no Brasil depende bem mais da influência externa do que de eventuais medidas anticíclicas que possam ser adotadas em âmbito local. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">2) A evolução do balanço de pagamentos tem sido, de forma geral, melhor do que era esperado até pelos mais otimistas. Até o momento, a deterioração do resultado comercial tem sido modesta - ainda que à custa de uma forte desaceleração das importações, induzida pela contração da demanda doméstica. Mas a dinâmica das vendas externas, em um ambiente global tão desfavorável, está longe de ser muito ruim. Entre as demais contas do balanço em transações correntes, os sinais também são bons. Quando comparado a choques semelhantes ocorridos no passado, fica evidente que a composição das contas externas brasileiras é muito melhor, sendo caracterizada por um grau maior de </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2445525,18,1,1/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">flexibilidade</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, notadamente em contas que reagem mais rapidamente às oscilações do ciclo econômico, como as remessas de lucros e dividendos e as viagens internacionais. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">3) Ainda no âmbito das contas externas, merece destaque também a postura cautelosa do Banco Central (BC) na gestão das reservas internacionais. Mesmo nos momentos mais agudos de aversão global a risco, quando o fluxo de divisas se tornou negativo até mesmo como consequência da escassez da oferta de crédito, a autoridade monetária brasileira atuou de maneira muito </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2445525,18,1,1/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">eficiente</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, desfrutando dos benefícios de um regime de câmbio flutuante. Nesse contexto, a moeda brasileira se desvalorizou em relação às mínimas registradas no pré-crise, mas a situação estrutural das contas externas se manteve robusta, reduzindo o risco e destacando positivamente o Brasil frente à maior parte dos países emergentes. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">4) Destaque positivo, também, para o comportamento da inflação. Ainda que evidentemente influenciado pela forte contração da atividade econômica, o nível de repasse da desvalorização cambial sobre a inflação foi limitado, permitindo ao BC dar continuidade ao processo de redução da taxa Selic. O País pode considerar com realismo a hipótese de ter taxas básicas de juros em um dígito pela primeira vez em décadas. Sabe-se que o jogo contra a inflação nunca está totalmente ganho e que num mundo de tantos estímulos de caráter monetário os riscos devem ser considerados particularmente relevantes. Mas deve-se admitir que a crise representou oportunidade para que o País avançasse um pouco mais em sua reputação de repúdio à inflação. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">5) Por fim, o cenário fiscal. Como era esperado, o ritmo de desaceleração das receitas foi intenso, revertendo a tendência anterior e demonstrando o elevado grau de correlação com o ciclo econômico. Nesse contexto, a dinâmica fiscal piora, via superávits menores e com tendência de baixa, embora a trajetória da dívida pública como proporção do PIB seja de relativa estabilidade ou deterioração muito leve. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em síntese: estejamos nós mais próximos ao meio ou ao final deste processo de crise global, é razoável reforçar a avaliação positiva em relação ao desempenho do Brasil, mesmo assumindo crescimento negativo para o PIB em 2009. Desempenho esse que coloca o País em posição favorável para o momento da retomada - que, no entanto, pode estar ainda mais distante do que os mercados parecem querer acreditar. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">(Gazeta Mercantil/Caderno A - Pág. 3) JOSÉ MAURO DELELLA* - Superintendente de análises econômicas/área de mercado de capitais do Banco Itaú Asset. Próximo artigo do autor em 14 de maio)</span> \n	<span style="FONT-SIZE: 10pt"></span></p>', 'EP', 'Jornal Gazeta Mercantil - Opinião', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2445525,18,1,1');
INSERT INTO `conteudo` VALUES (64, 'Câmara aprova reforço de R$ 100 bilhões no caixa do BNDES ', '2009-04-16', 'BRASÍLIA - O Plenário da Câmara dos Deputados aprovou, nessa quarta-feira, a Medida Provisória 453/09, que autoriza a União a emprestar R$ 100 bilhões ao Banco Nacional de Desenvolvimento Econômico e Social (BNDES) para aumentar sua capacidade de financiar projetos de longo prazo. A MP, que é a sétima editada desde 2008 para diminuir os efeitos da crise econômica sobre o Brasil, segue para o Senado. ', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O relator da matéria, deputado Pedro Eugênio (PT-PE), fez poucas mudanças em seu projeto de lei de conversão. Uma delas autoriza o Executivo a incluir, nos contratos de financiamento com os recursos da MP, cláusulas que exijam, das empresas beneficiadas pelos empréstimos, o compromisso de criar postos de trabalho ou de restringir as demissões sem justa causa. Isso valerá durante um período específico a ser definido.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Inicialmente, Pedro Eugênio apenas determinava que o banco considerasse, na hora de conceder o empréstimo, o potencial de geração e de manutenção de empregos.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Segundo o governo, a demanda por financiamentos do BNDES cresceu 173% de 2003 (R$ 33,5 bilhões) a 2008 (R$ 91,5 bilhões) - quase três vezes mais que o crescimento acumulado do PIB no mesmo período.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O empréstimo poderá ocorrer tanto com a emissão de títulos diretamente em favor do banco quanto com o uso do dinheiro do superávit financeiro da União obtido em 2008. No caso dos títulos, o Ministério da Fazenda definirá suas características.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Após contatos com o ministério, o deputado Pedro Eugênio também mudou o percentual máximo dos R$ 100 bilhões que será remunerado pela Taxa de Juros de Longo Prazo (TJLP). De acordo com a MP original, até 30% (R$ 30 bilhões) poderiam ser corrigidos com base no custo de captação externo, em dólares; e até 70% (R$ 70 bilhões) seriam remunerados pela TJLP mais juros de 2,5% ao ano.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O texto aprovado mantém a remuneração de até 30% do montante com base em dólares; mas, em vez dos 70%, prevê que o valor remanescente seja corrigido pela TJLP. Na prática, isso permite que o Ministério da Fazenda tenha maior flexibilidade para definir essas remunerações.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Se 20%, por exemplo, forem corrigidos pela variação do dólar, os outros 80% poderão ser corrigidos pela TJLP, sem o limite anterior de 70%.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A emissão de títulos em montante expressivo não deve afetar a dívida líquida federal, segundo argumenta o Executivo. Como a operação envolve dois entes públicos, os títulos lançados serão contabilizados como passivo do Tesouro e como ativo da União. Ou seja, em termos contábeis o efeito fiscal seria nulo.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em relação ao dinheiro disponível que poderá ser colocado no BNDES, o governo diz que o custo fiscal seria baixo, equivalente ao de captação para o Tesouro e à remuneração a ser dada pelo banco. A MP não tem, contudo, uma estimativa para o impacto fiscal.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Pedro Eugênio incluiu emenda que proíbe o BNDES de dar empréstimos a empresas da iniciativa privada cujos dirigentes sejam condenados por assédio moral ou sexual, racismo, trabalho infantil, trabalho escravo ou crime contra o meio ambiente. Isso também vale para a renovação do contrato.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O relator esclareceu que essa emenda segue um dispositivo da Lei de Diretrizes Orçamentárias (LDO) deste ano. \n		<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n			\n<p align="justify">&nbsp;</p></span></span></p>\n\n<p align="justify">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p align="justify">&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p align="justify">&nbsp;</p>', 'EP', 'jornal Valor On Line - Política', 'http://www.valoronline.com.br/ValorOnLine/MateriaCompleta.aspx?codmateria=5520239&codcategoria=6&dtMateria=2009-4-16&tp=3');
INSERT INTO `conteudo` VALUES (65, 'Resultado do emprego mostra que economia se recupera, diz LCA', '2009-04-17', '\n\n<p>O resultado do emprego no mês de março divulgado nesta semana pelo Caged é mais um sinal de que a economia brasileira está em recuperação. A avaliação é de Bráulio Borges, economista-chefe da LCA Consultores, que espera que os próximos meses também tenham um saldo positivo de emprego.<br />\n	No mês passado, foram criadas 34.818 vagas com carteira assinada. Apesar do saldo positivo, o resultado é muito menor que o registrado em março de 2008, com abertura de 206.556 postos no país.</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para Borges, o que importa é a comparação com o mês anterior, e não com o mesmo período do ano passado. Em fevereiro, 9.179 novos empregos foram criados no mercado formal, segundo o Caged.<br />\n		"É covardia comparar qualquer indicador deste ano com o mesmo período do ano passado. A economia se retraiu muito com a crise. A questão agora é avaliar a conjuntura atual", afirma o economista.<br />\n		Segundo análise da LCA, retirando as influências sazonais, março foi o primeiro mês de criação efetiva de vagas desde novembro de 2008 -com 30 mil postos. Com ajuste sazonal, o mês de fevereiro teve um fechamento de 17 mil postos, de acordo com a consultoria.<br />\n		Para Borges, a tendência é que o quadro de emprego melhore ainda mais no segundo trimestre. Além do reaquecimento da economia, ele cita as contratações para o Dia das Mães e para o Dia dos Namorados como fatores de impulsão do emprego no período.<br />\n		Nesse sentido, Borges também considera que as medidas adotadas pelo governo no primeiro trimestre para aquecer a economia, como a edição do pacote habitacional, vão se refletir em um aumento do emprego "em taxas mais robustas" no segundo trimestre.<br />\n		Para Borges, o desemprego no Brasil não vai superar a taxa de 9% da população economicamente ativa neste ano. "O nível de desemprego tende a se estabilizar", afirma.</span></p>', 'EP', 'Jornal Folha de São Paulo - Dinheiro - Col. Mercado Aberto', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1704200901.htm');
INSERT INTO `conteudo` VALUES (66, 'TELINHA', '2009-04-17', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">\n		<span style="FONT-WEIGHT: normal">>> Promô do CIC, o Ciclo de Debates - edição do próximo domingo - terá como tema em discussão O Legislativo Municipal e a Governança Urbana, juntando o presidente da CMF, Salmito Filho, o diretor da CDL, Pio Rodrigues Neto, e o arquiteto Francisco Nasser Hissa. O programa irá ao ar às 19 horas, pela TVC (com reprise no dia 24, às 22 horas).</span></p></span>', '', 'CE', 'Jornal O Povo - Col. Sônia Pinheiro', 'http://www.opovo.com.br/opovo/colunas/soniapinheiro/');
INSERT INTO `conteudo` VALUES (67, 'Novo mínimo - Indústria reclama e comércio aceita bem mínimo de R$ 506', '2009-04-17', '\n\n<p>A proposta do Governo Federal de aumentar o valor do salário mínimo para R$ 506,44 a partir de janeiro de 2010 foi recebida de formas diferentes pelos setores da indústria e do comércio. Para o setor industrial, o momento não é ideal para um aumento de R$ 41,44 no salário. Já o comércio vê oportunidades</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Dalviane Pires<br />\n		da Redação<br />\n		<br />\n		Paula Lima da Silva, 27, é apenas mais uma brasileira que vive do salário mínimo. Há um ano trabalha com carteira assinada. Está em seu segundo emprego formal. Foi pelo \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O POVO</span> que Paula ficou sabendo, ontem, que muito provavelmente a partir de janeiro de 2010 vai ganhar R$ 506,44 e não mais R$ 465. São R$ 41,44 que ajudarão a fazer a diferença. "Não sabia que ia aumentar. Mas se aumentasse mais seria ainda melhor", diz Paula. <br />\n		<br />\n		O entusiasmo de Paula se reflete também no comércio. De acordo com Séptimus Andrade, presidente em exercício do Sindicato dos Lojistas do Comércio de Fortaleza (Sindilojas), "quando o salário aumenta, as vendas aumentam pelo menos na mesma proporção". "Apoiamos todo de aumento para o mínimo. Gostaríamos que esse aumento fosse ainda maior pois melhora o poder de compra dos consumidores", pontua. <br />\n		<br />\n		Já a indústria cearense recebeu com cautela a proposta do mínimo de R$ 506,44. "Temos que considerar duas coisas: o aumento é maior do que a inflação. É aumento real. E segundo a vigência do salário mínino atual que será de apenas 11 meses. Isso em uma épica de crise. Minha preocupação é com o momento", afirma Pedro Jorge Viana, coordenador da Unidade de Economia e Estatística do Instituto de Desenvolvimento Industrial do Ceará (Indi), ligado à Federação das Indústrias do Estado do Ceará (Fiec). <br />\n		<br />\n		Viana estima que os custos da indústria com mão-de-obra pode chegar a 30%."É um percentual alto. Concordo até em dar um aumento maior que a inflação, mas não quase o dobro, como é o caso", finaliza Viana. <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Família</span> <br />\n		O novo mínimo vai mexer também com o orçamento familiar dos que têm funcionários com carteira assinada. É o caso da funcionária pública Adriana Alcântara. "Quando o mínimo aumenta, tudo aumenta junto. É um aumento que não existe. O serviço prestado em casa é tão importante quanto qualquer outro e tem que ser bem remunerado. Mas é muito pesado pra quem paga", pontua Adriana, que tem uma funcionária e paga integralmente, arcando de forma independente com os custos do INSS. <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">>> FALE COM A GENTE</span> <br />\n		dalviane@opovo.com.br <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">NÚMEROS</span> <br />\n		<br />\n		30% <br />\n		É O CUSTO MÉDIO DAS INDÚSTRIAS CEARENSES COM MÃO-DE-OBRA <br />\n		R$41 <br />\n		É O AUMENTO DO SALÁRIO MÍNIMO >SE A PROPOSTA DO GOVERNO DE >R$ 506 FOR APROVADA <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">PARA COMEÇAR A VALER</span> <br />\n		<br />\n		>Antes de valer R$ 506,44 o novo salário mínimo tem que ser aprovado pela Câmara e pelo Senado Federal. <br />\n		<br />\n		>Após passar pela Câmara e pelo Senado, a proposta segue para ser sancionada pelo presidente Lula. <br />\n		<br />\n		>O presidente também pode editar uma medida provisória estabelecendo o novo valor. <br />\n		<br />\n		>Se esse valor se confirmar, o salário mínimo terá aumentado 153,22% desde que Lula assumiu a Presidência. <br />\n		<br />\n		>Quando Lula assumiu em janeiro de 2003, o mínimo era de R$ 200. <br />\n		<br />\n		>Para a definição dos R$ 506, foi aplicada a regra seguida desde o ano passado: inflação acumulada mais a variação do Produto Interno Bruto (PIB) de dois anos antes. </span></p>', 'EP', 'Jornal O Povo - Economia', 'http://www.opovo.com.br/opovo/economia/870816.html');
INSERT INTO `conteudo` VALUES (68, 'Economia inicia lenta recuperação', '2009-04-18', 'DADOS RECENTES relativos à produção industrial, ao emprego e ao consumo sugerem alguma retomada do crescimento da economia brasileira, a partir de um patamar mais baixo, que resultou do forte ajuste dos níveis de produção no final de 2008 e, também, com uma velocidade menor comparativamente ao triênio 2006-2008. Diante desses fatos, é necessário indagar, em primeiro lugar, se essa recuperação é sustentável e, em segundo, a sua intensidade.', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O agravamento da crise internacional em setembro de 2008 transmitiu-se para a economia brasileira incidindo sobre o lado da oferta e com maior ênfase na produção industrial. A razão para tal foi a severa contração do crédito no plano global, mimetizada pelos bancos no âmbito doméstico. <br />\n		Essa brusca retração atingiu também parcelas da demanda, tais como as exportações, dependentes de créditos externos, e a parte do consumo que está mais sujeita à disponibilidade de crédito, concentrada nos bens duráveis e nos automóveis. Nesse ajustamento, é possível notar, porém, uma assimetria, com parte da demanda preservando seu patamar, como é o caso do consumo de bens correntes, do gasto público lato sensu e mesmo de parte dos investimentos privados. <br />\n		A lenta recuperação da produção que ocorre a partir de fevereiro de 2009 é explicada parcialmente pelos fatores assinalados acima e pelo ajuste do nível de estoques. Mas responde também a uma pequena mas não insignificante recuperação do crédito, cuja ampliação concentrou-se nos bancos públicos. Não é também irrelevante a ampliação do saldo comercial, apesar de conseguido pela contração mais rápida das importações. Tudo indica, portanto, que, tendo alcançado o fundo do poço, a economia brasileira iniciou um lento processo de recuperação. <br />\n		A continuidade da retomada em bases sólidas não está, todavia, assegurada. O primeiro fator que a condicionará será a evolução do crédito. A política do governo nesse aspecto precisa enfatizar suas dimensões positivas como o papel atribuído aos bancos públicos. Mas deve ainda abolir a lentidão do Banco Central na redução dos juros. Isso, ademais, ajudará a política fiscal expansionista e manterá sob controle o crescimento da dívida, a despeito da redução do superávit primário. <br />\n		As maiores ameaças à retomada do crescimento virão da economia internacional. Nem tanto do agravamento da crise, pois o cenário mais provável é o de estabilização das economias centrais num baixo patamar de crescimento, mas da instabilidade caracterizada por breves e intensos ciclos de liquidez. <br />\n		Nesse contexto, os percalços advirão do elevado grau de abertura financeira da economia brasileira, que poderá funcionar como mecanismo de transmissão dessa volatilidade. <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">RICARDO CARNEIRO</span></span> \n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">é professor titular do Instituto de Economia da Unicamp. \n		\n<p>&nbsp;</p></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1804200910.htm');
INSERT INTO `conteudo` VALUES (69, 'FHC: dia 30 de junho em Fortaleza', '2009-04-18', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O ex-presidente Fernando Henrique Cardoso confirmou ontem, por meio de sua assessoria, que virá dar palestra em Fortaleza. A data foi definida: 30 de junho próximo. O tema também: "Cenários Econômicos e Políticos no Brasil e no Exterior". Ele falará dentro do programa comemorativo dos 90 anos de fundação do Centro Industrial do Ceará. O local está em acertos, segundo o presidente do CIC, Robinson Castro e Silva, que fecha patrocinadores para bancar o gordo cachê guardado em segredo. Fala-se que FHC teria dado um bom desconto. Por causa do amigo Tasso Jereissati. </span></p>', '', 'CE', 'Jornal O Povo - Cidade - Col. Vertical', 'http://www.opovo.com.br/opovo/colunas/vertical/');
INSERT INTO `conteudo` VALUES (70, 'Empresários veem novos sinais de recuperação na economia', '2009-04-20', '\n\n<p>Nas últimas semanas, a economia está dando sinais evidentes de recuperação, ainda que tímidos e num ritmo bem abaixo do período anterior à crise. A conclusão ficou evidente ontem durante apresentação do presidente do Banco Central, Henrique Meirelles, em seminário sobre a crise durante o 8º Fórum Empresarial de Comandatuba, na Bahia.<br />\n	Em sua apresentação, Mei- relles mostrou vários números indicando uma trajetória de recuperação, mas recomendou cautela ao interpretar esses dados. Ele acha que pode ser precipitada uma nova onda de otimismo do mercado, depois de tanto tempo de pessimismo exacerbado.<br />\n	O problema é que, além de ser a maior crise mundial desde a de 1929, os dados da economia são divulgados com enorme defasagem. O PIB do primeiro trimestre, por exemplo, será muito ruim e só será divulgado em junho, quando tudo leva a crer que a economia esteja muito melhor. Apesar da recuperação, o país deverá estar tecnicamente em recessão (dois trimestres negativos do PIB).</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O tom da apresentação de Meirelles, apesar de todos os cuidados tomados, foi de otimismo. Mesmo quando falou sobre política monetária, ele afirmou diversas vezes que a tendência dos juros no Brasil é declinante.<br />\n		Os empresários gostaram da apresentação e mostraram dados comprovando essa tendência de recuperação da economia brasileira. O presidente da Anfavea (associação das montadoras), Jackson Schneider, por exemplo, afirmou que, neste mês, as vendas de automóveis leves continuam no mesmo patamar -ou pouco acima- do de março, o que é um dado surpreendente.<br />\n		Wilson Ferreira Jr., presidente da CPFL, também vai na mesma direção. O consumo de energia residencial em São Paulo e no Rio Grande do Sul, as duas áreas onde atua a CPFL, cresceu entre 8% e 12% em março e neste mês em relação ao ano passado; o consumo comercial, 6%; o industrial, 3%.<br />\n		Mais cauteloso, Fábio Barbosa, presidente da Febraban (federação dos bancos), prefere esperar um pouco mais para poder traçar um quadro definitivo do atual momento. Ele acha que a consolidação desse processo depende essencialmente do que acontecer, daqui para a frente, nos EUA.</span><br />\n	</p>', 'EP', 'Jornal Folha de São Paulo - Dinheiro - Col. Mercado Aberto', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2004200901.htm');
INSERT INTO `conteudo` VALUES (71, 'SERRA JUNINO ', '2009-04-20', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">E o presidenciável José Serra acertou data para vir ao Ceará. Em junho, quando, no auditório da Fiec, dará palestra sobre Desigualdades Regionais, dentro da festa dos 90 anos do CIC. Eis boa ocasião para o tucano desmitificar ideia de que não gosta de nordestino. </span></p>', '', 'CE', 'Jornal O Povo - Cidade - Col. Vertical', 'http://www.opovo.com.br/opovo/colunas/vertical/');
INSERT INTO `conteudo` VALUES (72, 'País não ameaça indústria brasileira, diz novo embaixador', '2009-04-22', '\n\n<p>RAUL JUSTE LORES<br />\n	DE PEQUIM </p>\n\n<p>O presidente Lula visita a China em maio e, segundo o novo embaixador do país no Brasil, "abre uma nova etapa de oportunidades de investimentos entre os dois países".<br />\n	Apesar da retórica de "parceria estratégica", os investimentos bilaterais são mínimos e ainda há muitas barreiras para produtos brasileiros no mercado do gigante chinês.<br />\n	Qiu Xiaoqi chegou há um mês a Brasília e diz que a China não é uma "ameaça" aos empresários brasileiros e que é um país aberto. "Os dois países estão crescendo e são complementares. O Brasil tem recursos naturais de que a China precisa e o Brasil precisa das manufaturas chinesas, que são bem recebidas", disse à Folha.</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ele não quis discutir a proposta brasileira de abandonar o dólar nas transações comerciais bilaterais, usando apenas reais e yuans, "pois são necessários muitos estudos nessa área para saber se é viável".<br />\n		Como é comum na diplomacia chinesa, o embaixador se especializou em uma área do mundo -Ibero-América- e há 30 anos desempenha funções em países como Cuba, Peru, Chile e Bolívia, além de ter sido diretor-geral da América Latina na chancelaria. Era embaixador da China na Espanha e acaba de trocar Madri por Brasília. Em espanhol, conversou com a \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span>.</span></p>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">FOLHA - A Fiesp já acusou a China de começar a desviar os produtos que não consegue vender nos EUA ou na Europa para o Brasil a preços irreais. A China não teme uma guerra comercial?<br />\n		QIU XIAOQI </p></span>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">- Não há motivos para preocupação. A China está disposta a resolver questões comerciais, que sempre devem ser em benefício recíproco. A China não é ameaça para nada.<br />\n		No intercâmbio comercial, os dois lados podem ganhar.<br />\n		O comércio bilateral, segundo as estatísticas chinesas, já é de US$ 50 bilhões. Um terço do comércio da China com a América Latina acontece com o Brasil. É o principal sócio da China na região. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">FOLHA - Apesar do discurso de parceria, os investimentos da China no Brasil são mínimos. Por que a China tem investido tanto na África e em outros países e não no Brasil?<br />\n		QIU </p></span>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">- O vice-presidente Xi Jinping esteve no Brasil em fevereiro, assinando diversos convênios bilaterais e de investimentos como o da Petrobras.<br />\n		China e Brasil são economias importantes e complementares. O Brasil é rico em recursos naturais e tem necessidades que a China pode suprir. O Brasil precisa das manufaturas chinesas, que são bem recebidas. Há mais condições de investimentos nos dois lados, os governos das duas partes podem promover mais investimentos.<br />\n		A visita do presidente Lula em maio a Pequim será outra oportunidade.<br />\n		A economia chinesa cresce rápido, mas também com dificuldades pela desaceleração global. Mas temos projetos de estímulo para superar isso. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">FOLHA - O presidente chinês, Hu Jintao, esteve no Brasil no final de 2004 e prometeu investimentos de US$ 70 bilhões no Brasil e na Argentina. Nem 1% desse montante ainda foi investido. O que emperra os investimentos?<br />\n		QIU </p></span>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">- Há desconhecimento mútuo entre os dois países, a América Latina está longe da China, temos que superar essa barreira. Primeiro, os dois países têm que se fazer conhecer. Empresas chinesas sempre esperam compras em boas condições para investir. Precisamos investir em promoção. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">FOLHA - Menos de 40 empresas brasileiras estão estabelecidas na China e boa parte tem apenas um pequeno escritório. Parece que também não é fácil investir na China.<br />\n		QIU </p></span>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">- Acho que as empresas brasileiras devem ser mais audazes, mais valentes, na hora de investir na China. A China é muito aberta para investimentos estrangeiros, muito vantajosa. O mercado chinês é muito aberto ao que vem de todo o mundo. Temos uma economia de mercado socialista. Se o mercado não está aberto, é ruim para o nosso desenvolvimento. Queremos ser competitivos globalmente. </span></p>\n<span style="FONT-WEIGHT: bold; FONT-STYLE: italic">\n	\n<p>FOLHA - Mas há barreiras tarifárias e burocracias que impedem a entrada da carne de frango, de porcos, do álcool combustível e de outros produtos brasileiros competitivos.<br />\n		QIU </p></span>\n<span style="FONT-STYLE: italic">\n	\n<p>&nbsp;</p></span>\n\n<p>- Como acontece no comércio, essas questões devem ser discutidas, negociadas pelos dois países. Mas, repito, a China é aberta. </p>\n<span style="FONT-WEIGHT: bold; FONT-STYLE: italic">\n	\n<p>FOLHA - Em que outras áreas Brasil e China podem se aproximar?<br />\n		QIU </p></span>\n<span style="FONT-STYLE: italic">\n	\n<p>&nbsp;</p></span>\n\n<p>- Em assuntos internacionais somos parecidos. A crise fortaleceu posições coordenadas e ambos queremos reformas do sistema financeiro internacional, mais supervisão e lutamos contra o protecionismo comercial.<br />\n	O Brasil é o mais sólido país da América Latina em pesquisa científica, de alta tecnologia. Podemos ter mais intercâmbio nessa área, que é de todo interesse para o meu país.</p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2204200915.htm');
INSERT INTO `conteudo` VALUES (73, 'CRÉDITO: Bahia, Ceará e Maranhão terão acesso a R$ 958 milhões', '2009-04-22', '\n\n<p>BRASÍLIA, 20 de abril de 2009 - Bahia, Ceará e Maranhão terão, sozinhos, acesso a R$ 958 milhões da nova linha de crédito subsidiado de R$ 4 bilhões anunciada pelo governo federal para ajudar os Estados a manterem o ritmo de investimentos <br />\n	</p>', '\n\n<p>Os detalhes da nova modalidade de concessão de empréstimos, incluindo a repartição dos recursos entre as unidades da federação, foram divulgados hoje pelo secretário-adjunto do Tesouro Nacional, Cléber Oliveira. Os valores foram divididos seguindo as mesmas proporções de repasses do Fundo de Participação dos Estados (FPE) estabelecidas pelo Tribunal de Contas da União (TCU) no final do ano passado. Dessa forma, quem poderá pegar menos dinheiro emprestado são Distrito Federal, São Paulo e Santa Catarina. Os R$ 4 bilhões de liberação de crédito, inclusive, equivalem justamente a um mês de repasses do FPE, explicou Oliveira. </p>\n\n<p>O juro final a ser cobrado é de 9,25% ao ano, considerando que os encargos para os Estados equivalem à Taxa de Juros de Longo Prazo (TJLP) mais 3% anual. Os recursos são oriundos do caixa do Fundo de Amparo ao Trabalhador (FAT), administrados pelo Banco Nacional de Desenvolvimento Econômico e Social (BNDES). Essa linha de crédito poderá ser paga em até oito anos, incluindo um ano de carência. Os juros serão cobrados em parcelas trimestrais durante o período de carência e, vencido essa etapa, a cada mês. O valor do principal será cobrado em parcelas mensais. "Há expectativa de desembolso rápido, viabilizando projetos de investimento", disse Oliveira. Serão agentes do repasse somente instituições financeiras federais. <br />\n	</p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453910,860,10,7');
INSERT INTO `conteudo` VALUES (74, 'Brasil pode oferecer uma resposta para a crise', '2009-04-22', '\n\n<p>Brasília, 22 de Abril de 2009 - Escalado pelo presidente Luiz Inácio Lula da Silva para ajudar a formular e debater um novo modelo de desenvolvimento para o País, o Secretário Especial de Assuntos Estratégicos, Roberto Mangabeira Unger, ocupa uma austera sala na Esplanada dos Ministérios, com brasões alusivos à República e uma imagem de José Bonifácio. Lá, o ministro recebeu a Gazeta Mercantil para uma conversa de pouco mais de uma hora, e declarou que as medidas contracíclicas adotadas pela equipe econômica do governo são apenas a base das ações do executivo para enfrentar a crise e que é preciso avançar mais. Falou sobre a necessidade de criar um novo modelo econômico global e que o Brasil pode ser o agente disso, além de comentar os primeiros passos de seu "pupilo", o presidente norte-americano Barack Obama. <br />\n	</p>', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Que ações o governo deve tomar para enfrentar a crise internacional? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Precisamos reorientar o projeto nacional. A crise é um momento privilegiado para acelerar essa reorientação, muito mais do que uma ameaça. Nada é mais assustador na discussão mundial da crise do que a pobreza das ideias. A rigor a única ideia amplamente difundida no mundo é o keneysianismo mumificado. É uma luz fraquinha para a dimensão da tarefa que estamos enfrentando. Há duas respostas principais à crise que tem emergido do mundo até agora. Respostas práticas e respostas intelectuais. No Atlântico Norte: Estados Unidos e Europa Ocidental, o foco da resposta está no resgate dos bancos falidos, regulação dos mercados financeiros e adoção de políticas fiscais e monetárias. É o keynesianismo vulgar. E no Nordeste Asiático, apesar da discussão sobre a necessidade de substituir o mercado exportador por mercado interno existe o protecionismo. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Mas os americanos não estão mais preocupados com ajuda a bancos e a economia real, enquanto os europeus... </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Há uma diferença entre os europeus e os americanos. Os europeus enfatizam a tarefa regulatória, os americanos com mais realismo, estão mais preocupados com os resultados imediatos. Mas é uma diferença apenas relativa. Eles estão na mesma direção geral. O discurso deles suprime três camadas profundas mais importantes. Da abordagem desses temas depende a eficácia dos governos em relação aos temas mais superficiais. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Quais são esses problemas estruturais da crise econômica mundial? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O primeiro tema mais profundo é a necessidade de enfrentar e superar os desequilíbrios estruturais na economia mundial, entre os países deficitários em comércio e poupança, a começar pelos Estados Unidos e os superavitários como a China. O motor do crescimento da economia global tem sido o superávit e a poupança chinesas e os déficits e a despoupança americana. Esse motor está quebrado. Ele não pode ser consertado. O segundo tema sonegado do debate é a necessidade de entender a regulação dos mercados financeiros. Hoje a produção, em larga medida, se auto-financia. Todos os estudos empíricos sugerem que bem mais de 80% do financiamento da produção em todas as grandes economias de mercado se baseia nos lucros retidos. O sistema financeiro é relativamente indiferente a produção. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Então ajudar bancos não vai resolver a crise? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Temos que inovar nos arranjos de governo a relação entre o sistema financeiro e a produção. O objetivo dessas inovações seria assegurar que mais da poupança de longo prazo fosse canalizada para </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">investimentos</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">produtivos de longo prazo. O terceiro tema suprimido do debate é o vínculo entre recuperação e redistribuição da renda. Os americanos que são o epicentro da crise construíram na segunda metade do século XX um mercado de consumo em massa, o que requer, em princípio, popularização do poder aquisitivo e redistribuição progressiva da renda. Isso não houve nos Estados Unidos. Houve o oposto. Nas últimas décadas do século XX, houve uma violenta concentração da renda e da riqueza. Como então conseguiram eles reconciliar o mercado de consumo em massa com a redistribuição regressiva da renda, parte da explicação está na sobrevalorização dos imóveis. Os imóveis sobrevalorizados funcionaram como um lastro para o violento endividamento das pessoas físicas e a pseudodemocratização do crédito fez às vezes da redistribuição progressiva da renda que não houve. E esse mecanismo está destroçado. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - O que pode substituir a desgastada relação comercial entre China e os Estados Unidos na economia global? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Os americanos não podem mais viver do dinheiro dos outros. Vão ter que poupar muito mais. Vão ter que ter déficits muito menores. Isso vai exigir uma outra política nos Estados Unidos e na China ao mesmo tempo. Nos Estados Unidos uma política focada na economia real. É nisto que estou querendo chegar. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - O senhor estava dizendo que o Nordeste Asiático tem outra alternativa para enfrentar a crise. Onde entra o Brasil nesse jogo? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No Nordeste Asiático eles estão montando uma segunda resposta. Não é a mera substituição do mercado exportador pelo mercado interno. O verdadeiro foco é o protecionismo. A </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">tendência</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">de reorganizar o foco semi-autárquico dentro da economia global. Como fizeram no século passado, em resposta à crise da década de 30. Nós, no Brasil e na América do Sul, teríamos todas as razões e condições para começar a exportar uma terceira resposta. Diferente da resposta que está prevalecendo no Atlântico Norte e no Nordeste Asiático. Uma resposta que mantenha o foco na economia real. Vejo na crise uma oportunidade para ampliar a nossa base produtiva e democratizá-la ao mesmo tempo. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Mas afinal, que caminho o Brasil está trilhando para enfrentar a crise? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O que vou dizer não é a posição oficial do governo brasileiro, mas também não é uma posição puramente pessoal. É uma tentativa de interpretar o que me parece ser a vertente principal de opinião que está emergindo dentro do governo. Embora os pormenores sejam controvertidos. A resposta se desdobra em três planos. Uma política contracíclica forte, o produtivismo democratizante e iniciativas institucionais para reorganizar o sistema financeiro. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Quais os pontos principais da política contracíclica do governo? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ela tem três componentes convencionais. A manutenção e fortalecimento da renda popular, cujos instrumentos principais de curso prazo são o salário mínimo e programas de distribuição de renda. A aceleração dos investimentos públicos, do gasto público em geral e dos investimentos privados com estímulo público. E acelerar a baixa dos juros. Sempre lembrando que no Brasil o juro </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">real</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">é superior a taxa média de retorno dos </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">negócios</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">. Portanto, nesta circunstância, toda a atividade produtiva é, a rigor, irracional. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Como reorganizar o sistema financeiro no Brasil voltado para a produção? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No Brasil nunca tivemos uma rede genuinamente local, descentralizada, como os Estados Unidos construíram na primeira metade do século XIX. Na nossa realidade quando alguém deposita dinheiro em um banco privado nacional no Piauí é muito provável que aquele dinheiro seja investido em São Paulo. Não podemos ter de um dia para outro instituições locais de crédito, mas o que podemos fazer é tomar medidas regulatórias que façam com que, pelo menos, parte desse dinheiro seja investido naquele estado ou pelo menos naquela região. E com isso assegurar uma base para a resposta à crise que seja não só socialmente ampla, mas também territorialmente ampla. Eu vejo esses eventos como uma amplificação da política contracíclica convencional. Mas ao mesmo tempo como uma fonte para um terceiro plano de ações. São ações que prefiguram um novo rumo de desenvolvimento. Rumo baseado na ampliação de oportunidades econômicas e educativas. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Que exemplo o senhor daria disto? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Vou dar dois exemplos, um agrícola e outro sobre relações de trabalho capital. Capital e trabalho tem três problemas: O primeiro é que quase a metade da população economicamente ativa está no mercado informal, trabalhando nas sombras da ilegalidade. É uma catástrofe. É preciso uma desoneração radical da folha de salários, ainda que com preservação de direitos financiados porém pelos impostos gerais. O segundo é que na economia formal uma parte crescente de trabalhadores está em situações de trabalho temporário, terceirizado ou autônomo. Estes também estão fora da proteção eficaz da lei. Se acrescentarmos essa fatia crescente da economia formal a metade informal é a vasta maioria do povo brasileiro que está fora da lei, ainda que não seja ilegal. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - O que o governo planeja para resolver essa questão </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">trabalhista? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Temos que construir uma proposta para definir, ao lado do regime existente das leis trabalhistas, um segundo regime, um segundo grupo para proteger, organizar e representar esses trabalhadores. Eu já propus ao presidente em colaboração com o Ministério do Trabalho. Toda a tendência das lideranças sindicais, da esquerda, é negar esse problema. Uma política de avestruz, atribuir isso só a evasão fraudulenta das leis. Quando na verdade é o produto de uma mudança profunda nos paradigmas de produção que tende a ser acelerado sobre as circunstâncias da crise. É preciso domar isso. Os dois discursos sobre o trabalho que existem no Brasil, da flexibilização e do direito adquirido não fazem isso. A flexibilização é interpretada pelos trabalhadores como um eufemismo para descrever a destruição dos direitos deles. E o direito adquirido resolve o problema da minoria que está dentro, mas não da maioria que está fora. E em terceiro lugar, há meio século, de forma descontínua, mas persistente, cai a participação do salário na renda nacional. A parte do bolo que vai para o capital aumenta e a parte que vai para o trabalho diminui. É preciso reverter isso por iniciativas institucionais e não apenas por políticas que procuram influenciar o salário nominal, como é a política de salário mínimo. E o ponto de partida natural é regular em lei, melhor do que está regulado agora, o preceito constitucional da participação dos trabalhadores nos lucros e resultados das empresas. Garantido contra o risco de manipulação pelo acesso amplo de sindicatos à contabilidade das empresas. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - O senhor acredita que isso possa ser feito ainda no governo Lula? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Eu já pus nas mãos do presidente uma minuta dessa regulação em lei, depois de ter discutido amplamente com as centrais sindicais. São exemplos de uma prática que eu chamaria a </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">inovação</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">institucional a serviço da democratização da economia de mercado. Não é apenas a regulação da economia de mercado. É a democratização dela por meio de inovações nas instituições que definem a economia de mercado. Portanto, essa é a minha concepção do que poderia ser a resposta brasileira ou sul-americana à crise. Não é uma resposta meramente financista e fiscalista. Ele tem esses três degraus: o degrau da política contracíclica, o degrau das iniciativas de produtivismo democratizante e o degrau das inovações institucionais a serviço da democratização da economia de mercado. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - A sua posição frente a crise tem uma boa acolhida dentro do governo. Do presidente Lula? Da Casa Civil ? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Eu tenho proposto essas ideias como um desdobramento de um discurso que o presidente vem fazendo. Que é o discurso de priorizar a economia real. E ver a crise como oportunidade para um avanço. Agora não posso pretender que o que acabo de lhe dizer seja uma mera transcrição das ideias do governo. Eu fiz um esboço em uma determinada direção. Tenho discutido com o presidente e ministros e começo uma discussão no Congresso. Mas antes de eu responder sobre a concordância ou discordância, há o problema preliminar que é o esclarecimento das ideias. Acredito que estamos em um estágio muito preliminar da formulação dessa posição. Há um desejo muito forte no Brasil. Não só na cabeça do presidente. Nos quadros dirigentes do país, no </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">empresariado</span> \n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, de dar a volta por cima, de dar uma resposta produtivista, criadora e audaciosa, como o Brasil deu no século passado na crise da década de 30. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - E no curto prazo. Eu sei que não é a sua área, não é a sua função dentro do governo. Mas as ações que o governo brasileiro tem tomado frente a crise têm surtido os efeitos desejados? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No Brasil eu vejo uma grande abertura. A resposta que lhe dei da maneira de equacionar, solucionar a crise é uma resposta de curto prazo, tanto quanto de longo prazo. O problema não é esse. É que exige uma tomada de posição muito contundente. Eu não posso pretender que essa posição já tenha sido cristalizada na definição que eu proponho. Mas também nada cristalizou contra essa proposta. Estamos em uma situação relativamente aberta. O que falei é algo intermediário entre a minha preferência e a minha interpretação da posição que está emergindo. Eu lhe dei uma interpretação esperançosa. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - O senhor está dentro do governo. A percepção que se tem de fora é que o governo tem tomado medidas emergenciais que estão socorrendo alguns setores. Mas fica difícil ver os efeitos na economia real? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Concordo. O sistema é difícil de conciliar. O que digo é que não seria justo interpretá-lo como apenas um amontoado de respostas casuísticas. As respostas que estão sendo dadas em maior ou menor grau, claramente manifestam esse intuito de aproveitar a crise para criar uma base produtiva e democratizá-la ao mesmo tempo. O que ocorre é que entre o mundo das ações concretas e essa vontade geral, os conceitos não saem já organizados. E é natural que não sejam. Não há outro lugar no mundo onde estejam. Isso é um esforço contra a maré das ideias dominantes. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Como o senhor que foi professor do presidente Barack Obama está vendo as suas iniciativas contra a crise? </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Muito do que Obama tem dito vai na mesma direção do que tem dito o presidente Lula. O mais importante nisso tudo é a economia real. Tudo o mais é acessório a isso. Por outro lado, ao compor a sua equipe econômica, chamou gente que tem ideias muito convencionais. Eu os conheço. Não são pessoas com visões alternativas e algumas delas sofrem de defeitos muito comuns que é só aprender aquilo que já sabem. O presidente Obama tem uma visão mais ampla, mas não é um economista. Os Estados Unidos estão em um momento de grande inflexão histórica. Por baixo há um movimento intenso. E o presidente, inteligente e intuitivo, previsivelmente oscilará entre o horizonte doutrinário circunscrito pelos mandarins econômicos e a inquietude e a impaciência do país embaixo. É a combinação entre políticas e ideias que pode influenciar esse movimento pendular. Não basta querer reorientar é preciso saber como. As ideias não aparecem quando se quer que elas apareçam. Não é convocando reuniões de poderosos do mundo que se convocam as ideias. As ideias não vêm para esses eventos. </span></p>\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Gazeta Mercantil - Como o Brasil pode aproveitar essa aproximação entre o presidente Lula e Obama? </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O Brasil é essencialmente vitalidade. É vida. Esse é o ponto de conexão entre o Brasil e os Estados Unidos. Dois países extremamente desiguais, cheios de vida, em que as pessoas comuns continuam a julgar que tudo é possível. Mas nos metemos em uma camisa de força que suprime essa vitalidade ao invés de instrumentalizá-la. A crise eu vejo como um momento propício para quebrar a camisa de força. Para nós que queremos essa reorganização do País, o nosso exército é a crise.</span> \n	<span style="FONT-SIZE: 10pt"></span></p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2453911,860,10,8');
INSERT INTO `conteudo` VALUES (75, 'TELINHA', '2009-04-24', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	<span style="FONT-WEIGHT: normal">\n		\n<p align="justify">\n			<span style="FONT-WEIGHT: normal">>> Educação profissional é o que discutirá o deputado federal Ariosto Holanda no programa Ciclo de Debates (by CIC), que vai ao ar no domingo, às 7 da noite, pela TVC. <br />\n				<br />\n				>> Das discussões tomarão parte: Samuel Brasileiro, presidente do Instituto Centro de Ensino Tecnológico (Centec) e Cláudio Ricardo Gomes de Lima (Reitor do Ineral de Educação, Ciência e Tecnologia).&nbsp;</span>\n			<span style="FONT-WEIGHT: normal">&nbsp;</span></p></span></span>', '', 'CE', 'Jornal O Povo - Col. Sônia Pinheiro', 'http://www.opovo.com.br/opovo/colunas/soniapinheiro/872480.html');
INSERT INTO `conteudo` VALUES (76, 'INDÚSTRIA BRASILEIRA - Produção deve recuar 4,4%', '2009-04-24', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	<span style="FONT-SIZE: 14pt; FONT-FAMILY: Arial">\n		\n<p>&nbsp;</p>\n		\n<p align="justify">\n			<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n		\n<p>&nbsp;</p></span></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">Para manter a produção industrial nos níveis de 2008, o governo deve continuar adotando medidas de estímulo<br />\n	</span><br />\n\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Brasília. O Instituto de Pesquisa Econômica Aplicada (Ipea) calcula uma queda de 4,4% na produção industrial no primeiro trimestre de 2009 em decorrência direta de uma redução de 26% nas exportações do setor industrial, resultado da crise financeira internacional.<br />\n	<br />\n	Portanto - alerta o Ipea -, para manter a produção industrial nos níveis de 2008, o governo deve continuar adotando medidas de estímulo à economia interna de forma que a demanda doméstica cresça na ordem de pelo menos 4,4% para compensar o efeito negativo do recuo das exportações industriais, caso a queda das vendas externas continue nesse ritmo.</span>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">´Isso pode servir de alerta ao governo, que já vem adotando medidas anticíclicas importantes de incentivo à demanda, como a redução de impostos e a antecipação do reajuste do salário mínimo´, comentou o diretor de Estudos Setoriais do Ipea, Márcio Worhlers, ao apresentar o estudo ´Impactos da queda nas exportações sobre a produção doméstica´, publicado no boletim ´Radar´, que traz a análise das exportações da indústria brasileira.<br />\n		<br />\n		No estudo, os pesquisadores do Ipea avaliaram vários segmentos industriais constatando que alguns - como madeira, couro e calçados, petróleo e combustíveis, máquinas e equipamentos, metalurgia e veículos - foram mais afetados pela queda nas vendas externas de produtos, porque são aqueles cuja participação nas exportações é grande em relação ao valor da sua produção.<br />\n		<br />\n		´E vários desses setores podem encontrar alívio no mercado doméstico´, completou Fernanda De Negri, uma das pesquisadoras responsáveis pelo estudo. A pesquisadora acrescentou que, se as exportações continuarem, ao longo de 2009, no mesmo ritmo de queda deste primeiro trimestre, o recuo da produção industrial será da ordem de 4,5%.<br />\n		<br />\n		A própria pesquisadora, no entanto, destacou que já há sinais de que seja possível uma reação das exportações brasileiras ao longo do ano, mas ainda impossível de mensurar.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Pessimismo</span><br />\n		<br />\n		O setor industrial continua pessimista em relação aos negócios e a economia, segundo o Índice de Confiança do Empresário Industrial (Icei) de abril da Confederação Nacional da Indústria (CNI). O índice do mês alcançou 49,4 pontos, o que significa uma queda de 12,6 pontos na comparação com abril do ano passado e um crescimento de 2,0 pontos na comparação com janeiro de 2009. Segundo a CNI, somente os empresários de grande porte registraram confiança em abril, atingindo 51,8 pontos. As pequenas empresas registraram o menor valor desde julho de 2002: 46,8 pontos, 2,7 pontos a menos que em janeiro. O índice de confiança das médias empresas ficou em 48,8 pontos. Para a CNI, a perspectiva das pequenas e médias empresas sinaliza que deverá ser mantida a tendência de desaceleração da atividade. A pesquisa foi realizada de 1º a 23 deste mês com 1 259 empresas.</span></p>', 'EP', 'jornal Diário do Nordeste - Negócios', 'http://diariodonordeste.globo.com/materia.asp?codigo=633217');
INSERT INTO `conteudo` VALUES (77, 'Crédito a empresas volta a nível pré-crise', '2009-04-24', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Juros e volumes concedidos de capital de giro estão perto dos vistos antes do agravamento da crise, mas "spread" sobe <br />\n		<br />\n		Empresários afirmam que não viram melhora nas condições de financiamento das companhias; bancos temem inadimplência alta </span></p>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Depois de praticamente secar, o crédito para empresas começa a funcionar com taxas e volumes próximos aos vistos em agosto e setembro, antes do agravamento da crise. Mas os "spreads" [diferença entre a taxa de captação dos bancos e a cobrada do cliente], seguem elevados, diz o Banco Central.<br />\n			Principal fonte de financiamento operacional das empresas, as novas concessões para capital de giro aumentaram 62,5% em março em relação a fevereiro e atingiram R$ 21,836 bilhões -próximo dos R$ 22,02 bilhões de setembro. Na comparação com março de 2008, houve aumento de 47,1%.<br />\n			Os juros médios do capital de giro recuaram de 36% ao ano para 33,9%, de fevereiro para março. A taxa já retornou ao patamar de agosto e setembro, respectivamente, de 33% e 33,6%. O "spread" caiu de 24,9 pontos para 23,8 pontos, acima dos 18,6 pontos de agosto e dos 19,1 pontos de setembro.</span><br />\n		</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para Fabio Barbosa, presidente do Santander e da Febraban, a retomada do crédito em março se deve a questões sazonais (março é sempre melhor que fevereiro) e de melhora na economia. "Estamos vendo a continuidade de um processo; não houve uma ruptura no Brasil, mas uma desaceleração no ritmo de crescimento. A economia, de fato, dá uma retomada em março. E isso leva as empresas a buscarem capital de giro para atender os clientes", disse.<br />\n		Para Barbosa, a maior preocupação é com a inadimplência, que, no caso do capital de giro, subiu de 1,89% para 2,22%, a maior desde maio de 2007. Por isso, os "spreads" estão altos, diz Barbosa.<br />\n		"Esse estrangulamento de liquidez que tivemos em outubro, com o fechamento das linhas de comércio exterior, dos mercados de capitais, e a retirada dos bancos pequenos e médios, pouco a pouco, vêm sendo corrigido. Só falta o mercado de capitais voltar a funcionar", disse Norberto Barbedo, vice-presidente do Bradesco.<br />\n		Para Silvio de Carvalho, diretor-executivo do Itaú Unibanco, os números do BC mostram uma evolução pequena no crédito para empresas. Carvalho lembra que as grandes empresas, que voltaram a se financiar no país, chegam com risco e taxas menores. "Como as grandes empresas estão mais presentes, elas têm um nível de juros menor do que as médias. É um pouco esse o efeito que também vemos nos "spreads"."<br />\n		Os empresários, entretanto, dizem que ainda não sentiram a melhora no mercado de crédito detectada pelo BC. "Não está normal a situação no país", resume Júlio Gomes de Almeida, consultor do Iedi (Instituto de Estudos para o Desenvolvimento Industrial) e ex-secretário de Política Econômica do Ministério da Fazenda.<br />\n		Almeida afirma que cada segmento enfrenta uma dificuldade diferente. Para as grandes companhias, nunca faltou financiamento. "No caso das pessoas jurídicas, o ritmo de queda das taxas é menor. O "spread" subiu demais devido a uma cautela excessiva com a possibilidade de alta da inadimplência, o que não ocorreu."<br />\n		Já para as micro, pequenas e médias empresas, a dificuldade é ter acesso ao crédito. "Nem temos recebido reclamações sobre o custo, porque sequer tem crédito", diz Marcel Solimeo, economista-chefe da Associação Comercial de São Paulo. "Os bancos aumentaram a seletividade dos clientes, deixando de fora os menores."<br />\n		O varejo é o setor quem mais tem sofrido com a escassez de crédito, segundo especialistas. O setor tem renegociado com fornecedores e diminuído os prazos e limites de financiamento ao consumidor.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2404200906.htm');
INSERT INTO `conteudo` VALUES (78, 'Pessimismo de empresários diminui, mostra índice da CNI', '2009-04-24', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA FOLHA ONLINE, EM BRASÍLIA</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O índice de confiança do empresário industrial, medido pela CNI (Confederação Nacional da Indústria), apresentou recuperação no início do segundo trimestre em relação ao primeiro.<br />\n		O índice ficou em 49,2 pontos, dois acima do de janeiro. Na comparação com abril do ano passado, houve uma queda de 12,6 pontos.<br />\n		Segundo a CNI, o índice abaixo de 50 pontos mostra que a maioria dos empresários ainda não está confiante no cenário econômico.</span><br />\n	</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A recuperação foi puxada pelas grandes e pelas médias empresas. Para os grandes empresários, o índice já está em território positivo, com 51,8 pontos. Para as médias, ainda se situa em 48,8 pontos. No caso das pequenas, recuou 2,7 pontos ante o início do ano, para 46,8 pontos.<br />\n		Dos 27 setores industriais pesquisados, 19 apresentaram índices abaixo de 50 pontos. Os piores resultados foram observados nas indústrias de álcool e madeira.<br />\n		O setor de veículos automotores, beneficiado pela redução de tributos, foi o que apresentou a maior recuperação da confiança.<br />\n		O levantamento foi realizado com 1.259 empresas, de todos os portes, entre os dias 1º e 23 deste mês.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2404200910.htm');
INSERT INTO `conteudo` VALUES (79, 'INDÚSTRIA: Queda de 4,4% da produção confirma recessão', '2009-04-24', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">BRASÍLIA, 24 de abril de 2009 - A produção industrial nacional caiu 4,4% no primeiro trimestre, na comparação com igual período de 2008, por causa da queda de 26% nas exportações da indústria de transformação. Os setores de veículos, madeira, petróleo e couro e calçados são os mais prejudicados pela retração das vendas externas. Os dados são do estudo do Instituto de Pesquisa Econômica Aplicada (Ipea) Radar: Produção, Tecnologia e Comércio Exterior, divulgado ontem.</span>\n	<span style="FONT-SIZE: 10pt"></span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Os técnicos do Ipea prevêem que a redução do PIB da indústria deve levar a uma retração do PIB do país neste primeiro trimestre. Por isso, o Ipea já confirma uma recessão "técnica" do Produto Interno Bruto (PIB) no primeiro trimestre, mesmo sem precisar de quanto seria a queda, já que também houve redução do PIB no último trimestre do ano passado. O resultado é reflexo dos efeitos da crise econômica mundial que derrubou a demanda externa e, consequentemente, as exportações brasileiras. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O recuo dos embarques da indústria de transformação superou a média das exportações totais que caíram 19% no trimestre. Se o atual </span><a href="http://www.investnews.com.br/IN_News.aspx?parms=2459140,26,1,1/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">cenário</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">se mantiver ao longo deste ano, o IPEA calcula que as exportações poderão encerrar o ano com redução de 26%, em média, o que geraria impacto na produção industrial de até 4,5%. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A perda da produção industrial poderá ser menor este ano se for confirmada a previsão do Ministério do Desenvolvimento, Indústria e Comércio Exterior (Mdic) de redução das vendas externas de 20% para US$ 160 bilhões este ano, ao invés dos 26% previstos pelo instituto, estimou a coordenadora de Política Industrial do instituto, Fernanda De Negri. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O instituto trabalha com perspectiva de crescimento para o PIB em 2009. O diretor de Estudos Setoriais do Ipea, Márcio Wohlers de Almeida, assegura que a economia nacional terá crescimento entre zero e 1%, apesar de o Fundo Monetário Internacional (FMI) prever queda de 1,3% para o PIB do Brasil. As expectativas do instituto, ligado ao Ministério do Planejamento, são menos otimistas do que a do governo que mantém a previsão de aumento de 2%. Na opinião de Almeida, a previsão do FMI é muito pessimista porque as estimativas do organismo "são muito voláteis" e são alteradas a cada semana. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Almeida afirmou que a economia brasileira começa a mostrar sinais de reaquecimento por causa das medidas anticíclicas adotadas pelo governo. Almeida acrescentou que os dados previstos para a economia brasileira "não são tão alarmante" se comparados com as previsões para o Japão e Coréia, que devem reduzir o PIB em 6% e 4%, respectivamente. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A assessora econômica do Mdic, Joana Mostafa, acredita que o mercado interno poderá absorver a oferta de produtos que seria destinada este ano ao mercado externo. E assim, evitar uma queda de 4,5% da produção industrial este ano. Joana disse também que o País está mais "armado" para o enfrentar a crise do que os parceiros da América Latina, graças aos </span><a href="http://www.investnews.com.br/IN_News.aspx?parms=2459140,26,1,1/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">investimentos</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">previstos no Programa de Aceleração do Crescimento (PAC) e dos empréstimos concedidos ao setor produtivo pelo Banco Nacional de Desenvolvimento e Social (BNDES). </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Já o economista da Confederação Nacional da Indústria (CNI), Renato da Fonseca, avaliou que a falta de crédito é um dos principais problemas enfrentados pelo setor exportador, principalmente para as médias e pequenas empresas. Ele lembrou que a crise começou a interferir nos resultados das grandes empresas diante da falta de crédito internacional e pela falta de confiança nos mercados. "Depois que a crise começou a prejudicar as empresas de menor porte", disse o economista. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No boletim, os técnicos do Ipea sublinham que a redução das vendas externas tem tido impactos significativos na produção doméstica, que se agrava com a restrição ao crédito ao setor produtivo. "Mesmo com a desvalorização do </span><a href="http://www.investnews.com.br/IN_News.aspx?parms=2459140,26,1,1/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">real</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, as expectativas para 2009 não são boas", descrevem. Os técnicos do Ipea sugerem ao governo a manutenção da política de incentivos econômicos para aquecer o mercado interno. Pois a redução da taxa Selic provavelmente surtirá efeito nos investimentos apenas em 2010. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Dentre os 22 setores analisados entre janeiro e março, os mais prejudicados são os que possuem forte correlação com o comércio exterior. O primeiro da lista é o de veículos para o qual o estudo do Ipea mostra queda de 51% nas exportações no primeiro trimestre e recuo de 7,7% sobre igual trimestre do ano passado. Logo em seguida vem o de madeira, que apresenta retração de 48% nas exportações entre janeiro e março e recuo de 16,6% na produção industrial do setor, na comparação com o primeiro trimestre de 2008. O segmento de petróleo e combustíveis ocupa o terceiro lugar da lista ao registrar perda de 41% nos embarques externos e de decréscimo de 9,9% na produção. Depois vem couro e calçados com declínio de 40% nas vendas externas e de 14,3% na produção. (Viviane Monteiro - Gazeta Mercantil)</span><br />\n	</p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.investnews.com.br/IN_News.aspx?parms=2459140,26,1,1');
INSERT INTO `conteudo` VALUES (80, 'JUROS - Selic deve cair para 10,25%, prevê mercado', '2009-04-28', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 14pt; COLOR: #000080; FONT-FAMILY: Arial">\n	\n<p>&nbsp;</p>\n	\n<p align="justify">\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 18pt; FONT-FAMILY: Arial"></span>\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n	\n<p>&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: bold; FONT-SIZE: 18pt; FONT-FAMILY: Arial"></span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA FOLHA ONLINE, EM BRASÍLIA </span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O Comitê de Política Monetária do Banco Central deve reduzir a taxa básica de juros dos atuais 11,25% para 10,25% ao ano na reunião de amanhã, segundo a expectativa de economistas ouvidos pelo próprio BC na pesquisa semanal Focus. A previsão é que a Selic encerre o ano a 9,25%, e o PIB caia 0,39% -na semana passada, a aposta era de -0,49%.</span></p>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>', '', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2804200912.htm');
INSERT INTO `conteudo` VALUES (81, 'A crise na indústria brasileira', '2009-04-28', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Desde setembro do ano passado, quando a crise financeira começou a aparecer no mundo, a indústria brasileira foi o setor que mais sentiu os efeitos do colapso. Segundo análise do Instituto de Estudos para o Desenvolvimento Industrial (IEDI), tomando-se como referência o mês de setembro de 2008, houve forte retração até dezembro do mesmo ano (20,1%;), retração essa suavizada no primeiro bimestre de 2009 (16,9%.). De acordo com o Instituto "houve uma desaceleração da queda da produção da indústria entre esses dois períodos. Mas, ela não indica uma tendência de recuperação".</span></p>', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	\n<p>Algumas marcas do problema</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A crise financeira teve um impacto forte no consumo de energia no país, principalmente, entre outubro de 2008 e fevereiro de 2009. Segundo dados do Operador Nacional do Sistema Elétrico e da Empresa de Pesquisa Energética, os consumidores livres foram os mais atingidos pela crise, com a conseqüente redução do consumo. A carga dos consumidores livres caiu 6,1% no primeiro trimestre, em relação ao mesmo trimestre de 2008.</span><br />\n	\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O emprego na indústria brasileira caiu 4,2% em fevereiro na comparação com o mesmo mês do ano passado, marcando o maior recuo da série histórica iniciada em 2001, segundo IBGE<br />\n		No primeiro trimestre de 2009, as exportações brasileiras caíram 19% em relação aos três primeiros meses de 2008. A indústria sofreu um grande abalo, com a queda chegando a 26%. Os dados foram divulgados pelo Instituto de Pesquisa Econômica Aplicada (IPEA).</span></p>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Boas perspectivas<br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O que nós podemos vislumbrar são boas perspectivas no fechamento do primeiro trimestre do ano" - comenta Pedro Jorge Ramos Viana, coordenador de Economia e Estatística do Instituto de Desenvolvimento Industrial do Ceará (Indi), ligado à Federação das Indústrias do Estado (Fiec). "Na comparação com janeiro o Ceará teve crescimento (0,8%) em fevereiro, o que deverá ser repetido em março. Em fevereiro, todos os sinais foram positivos, o que deverá ser apresentado no próximo dia 10 de maio, quando divulgaremos a pesquisa dos indicadores da indústria. Ao que tudo indica, o avião passou por uma forte turbulência, mas já regularizou o seu vôo".</span></p></span><br />\n\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">"O que nós podemos vislumbrar são boas perspectivas no fechamento do primeiro trimestre do ano" - comenta Pedro Jorge Ramos Viana, coordenador de Economia e Estatística do Instituto de Desenvolvimento Industrial do Ceará (Indi), ligado à Federação das Indústrias do Estado (Fiec). "Na comparação com janeiro o Ceará teve crescimento (0,8%) em fevereiro, o que deverá ser repetido em março. Em fevereiro, todos os sinais foram positivos, o que deverá ser apresentado no próximo dia 10 de maio, quando divulgaremos a pesquisa dos indicadores da indústria. Ao que tudo indica, o avião passou por uma forte turbulência, mas já regularizou o seu vôo".</span>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Recuperação ainda difícil<br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A produção da indústria em fevereiro cresceu 1,8% em relação a janeiro. Trata-se de um sinal positivo para o setor, mas insuficiente para anular as quedas anteriores, na opinião do coordenador de Indústria do IBGE, Silvio Sales. Na comparação com anos anteriores, o nível de produção voltou quatro anos no tempo.<br />\n			"O presidente da Confederação Nacional da Indústria (CNI), Armando Monteiro Neto, acredita que o nível de atividade da indústria brasileira em 2009 apresentará leve crescimento, próximo de zero, em relação ao ano passado. Ele acredita que o desempenho da indústria apresentará recuperação, principalmente no segundo semestre, já que o período de janeiro a junho ainda será um período de ajustes na atividade econômica" (Yahoo Notícias)<br />\n			"A desconfiança de consumidores e empresários, a restrição de crédito e o recuo das exportações estão fazendo da indústria a principal vítima, entre os setores da economia, dos efeitos nefastos da crise econômica no Brasil. A avaliação de economistas é que o setor já saiu do mais fundo do poço a que chegou no quarto trimestre do ano passado, mas a recuperação não virá antes do segundo semestre". (Ag. Estado).</span></p></span><br />\n\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A produção da indústria em fevereiro cresceu 1,8% em relação a janeiro. Trata-se de um sinal positivo para o setor, mas insuficiente para anular as quedas anteriores, na opinião do coordenador de Indústria do IBGE, Silvio Sales. Na comparação com anos anteriores, o nível de produção voltou quatro anos no tempo.<br />\n	"O presidente da Confederação Nacional da Indústria (CNI), Armando Monteiro Neto, acredita que o nível de atividade da indústria brasileira em 2009 apresentará leve crescimento, próximo de zero, em relação ao ano passado. Ele acredita que o desempenho da indústria apresentará recuperação, principalmente no segundo semestre, já que o período de janeiro a junho ainda será um período de ajustes na atividade econômica" (Yahoo Notícias)<br />\n	"A desconfiança de consumidores e empresários, a restrição de crédito e o recuo das exportações estão fazendo da indústria a principal vítima, entre os setores da economia, dos efeitos nefastos da crise econômica no Brasil. A avaliação de economistas é que o setor já saiu do mais fundo do poço a que chegou no quarto trimestre do ano passado, mas a recuperação não virá antes do segundo semestre". (Ag. Estado).</span>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Salários do setor público mantém economia girando<br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">"No final do ano passado, início deste ano, tivemos uma forte queda em todos os segmentos da indústria do Distrito Federal"  diz Ricardo de Figueiredo Caldas, vice-presidente da Federação das Indústrias do Distrito Federal (Fibra). Felizmente, temos uma forte massa salarial do setor público, que continua comprando e mantendo o giro da economia. Seguindo fevereiro, em março teremos uma queda pouco acentuada e, em abril, já veremos sinais positivos de recuperação. Creio que chegou a hora do Governo reduzir tributos para colocar mais dinheiro na mão dos consumidores".</span></p></span><br />\n\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">"No final do ano passado, início deste ano, tivemos uma forte queda em todos os segmentos da indústria do Distrito Federal"  diz Ricardo de Figueiredo Caldas, vice-presidente da Federação das Indústrias do Distrito Federal (Fibra). Felizmente, temos uma forte massa salarial do setor público, que continua comprando e mantendo o giro da economia. Seguindo fevereiro, em março teremos uma queda pouco acentuada e, em abril, já veremos sinais positivos de recuperação. Creio que chegou a hora do Governo reduzir tributos para colocar mais dinheiro na mão dos consumidores".</span>\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Sinais positivos<br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A indústria brasileira começa a dar sinais de recuperação no primeiro trimestre deste ano, após queda generalizada nos pedidos recebidos em dezembro. Um mapeamento feito com base na Sondagem da Indústria de Transformação da Fundação Getúlio Vargas (FGV) revela que em dezembro, no ápice da crise, todos os 14 setores pesquisados pelo estudo feito a pedido do Grupo Estado registraram queda na demanda global em relação a setembro. "Já em março, sete setores saíram do terreno negativo na demanda global na comparação com dezembro", observa o responsável pela área técnica da sondagem, Jorge Ferreira Braga.</span></p></span><br />\n\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A indústria brasileira começa a dar sinais de recuperação no primeiro trimestre deste ano, após queda generalizada nos pedidos recebidos em dezembro. Um mapeamento feito com base na Sondagem da Indústria de Transformação da Fundação Getúlio Vargas (FGV) revela que em dezembro, no ápice da crise, todos os 14 setores pesquisados pelo estudo feito a pedido do Grupo Estado registraram queda na demanda global em relação a setembro. "Já em março, sete setores saíram do terreno negativo na demanda global na comparação com dezembro", observa o responsável pela área técnica da sondagem, Jorge Ferreira Braga.</span>', 'EP', 'jornal O Estado - Economia - Col. Estratégia & Gestão', 'http://www.oestadoce.com.br/?acao=colunas&subacao=ler&colID=11');
INSERT INTO `conteudo` VALUES (82, 'Inflação deve segurar ritmo do corte de juro do Copom', '2009-04-29', '\n<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O Copom dará continuidade ao ciclo de queda no juro na reunião de hoje, mas em um ritmo menor que o corte de 1,5 ponto percentual feito pelo órgão em março, embora essa opção não esteja totalmente descartada. O motivo é que a inflação voltou ao radar de preocupação da autoridade monetária. Essa é a opinião de Octavio de Barros, diretor de Pesquisas Econômicas do Bradesco.<br />\n		Para Barros, o Copom reduzirá hoje a Selic em um ponto percentual -para 10,25% ao ano. O corte menor que o de março se justifica pelo retorno da inflação como um fator de preocupação para o órgão.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Diante dos sinais de recuperação [da economia], a inflação volta ao radar, não no sentido de ser uma ameaça imediata, mas sim como fator potencial e latente de preocupação, olhando bem para a frente."<br />\n		Segundo o economista, dados recentes, como o resultado do comércio e da indústria, sinalizam que efetivamente o pior momento da crise já passou e que há um movimento de recuperação se iniciando.<br />\n		Esse restabelecimento da economia, somado à persistência da elevação dos preços de serviços, pressiona a inflação, de acordo com Barros. "Agora, as incertezas em relação à atividade econômica doméstica coexistem com as relacionadas aos impactos da recuperação na margem sobre a inflação."<br />\n		A recuperação da atividade e os incentivos fiscais e tributários não vão impedir a desaceleração da economia brasileira neste ano, afirma Barros.<br />\n		Para ele, esses sinais vão surtir efeito cumulativo no final do ano. Barros estima que o país estará crescendo em um ritmo anualizado e dessazonalizado de 4% no fim deste ano. E esse raciocínio será considerado na decisão do Copom sobre o corte dos juros de hoje, segundo ele.</span></p>', 'EP', 'Jornal Folha de São Paulo - Dinheiro - Col. Mercado Aberto', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2904200901.htm');
INSERT INTO `conteudo` VALUES (83, 'Indústria vê recuperação "frágil" da atividade', '2009-04-29', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ritmo de produção se mantém estável em março, com avanço de 0,5%, mas recua 15% no 1º tri na comparação com 2008 <br />\n		Especialistas afirmam que indústria não mostra vigor na recuperação após a forte retração do setor desde o final do ano passado </span></p>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">VERENA FORNETTI</span><br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">COLABORAÇÃO PARA A FOLHA </span><br />\n		<br />\n		</span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">O nível de atividade da indústria paulista se manteve estável em março, segundo a Fiesp (Federação das Indústrias do Estado de São Paulo). Os dados da associação indicam que a atividade industrial parou de cair, mas que a retomada do crescimento será lenta.<br />\n		O INA (Indicador de Nível de Atividade) subiu 0,5% em março em relação a fevereiro (resultado com ajuste sazonal). Sem a correção, o crescimento foi de 10,4% -o dado não indica recuperação porque março tem mais dias úteis e, tradicionalmente, registra crescimento expressivo ante o mês anterior.<br />\n		</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A pesquisa mostra que o nível de atividade em que a indústria paulista se estabilizou no primeiro trimestre é muito inferior ao de igual período do ano passado. A queda foi de 14,9% em relação a janeiro a março de 2008, a maior desde 2003 nessa comparação.<br />\n		"O choque na indústria foi forte, e a capacidade de recuperação nos três primeiros meses do ano foi frágil. Não existe um impulso de recuperação da grande queda que sofremos", diz Paulo Francini, diretor do Departamento de Pesquisas e Estudos Econômicos da Fiesp.<br />\n		A federação revela que o número de horas pagas aos trabalhadores na indústria paulista caiu 1,1% ante fevereiro na série ajustada sazonalmente. O indicador de horas trabalhadas na produção teve retração de 0,7% (dado com correção). O nível de utilização da capacidade instalada foi de 76,7% em março, o menor desde julho de 2003.<br />\n		Para Francini, enquanto os setores que têm mais peso para a indústria não recuperarem o ímpeto de crescimento, o setor deve avançar devagar. Os sete segmentos mais importantes -que juntos têm 40% de participação na indústria de São Paulo- tiveram contração de 23% no ano em relação a 2008. Entre eles estão metalurgia básica, produtos metálicos exceto máquinas e equipamentos e veículos automotores.<br />\n		A indústria têxtil, no entanto, retomou o fôlego. A atividade do setor cresceu quase 3% em março ante fevereiro, na série ajustada sazonalmente. Vendas e horas trabalhadas também cresceram, segundo a Fiesp.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Perspectivas melhores</span><br />\n		Ariadne Vitoriano, analista da consultoria Tendências, afirma que a indústria deve começar a se recuperar de forma mais vigorosa a partir do meio deste ano. "A queda brusca no final do ano passado foi mais um ajuste de estoques. Quando esse ajuste se esgotar, a indústria deve crescer, não ao nível em que estava no ano passado, mas em nível melhor do que nos primeiros meses do ano."<br />\n		O indicador Sensor da Fiesp, que monitora a expectativa dos empresários na quinzena atual, indica que, pela primeira vez desde outubro do ano passado, a sensação dos empresários para o nível de emprego é positiva. A percepção também foi favorável para mercado, vendas e investimentos.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi2904200907.htm');
INSERT INTO `conteudo` VALUES (84, 'Indústria usa só 68% da capacidade instalada', '2009-04-30', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">EDUARDO CUCOLO</span><br />\n	\n	<span style="FONT-WEIGHT: normal">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA FOLHA ONLINE, EM BRASÍLIA </span><br />\n		<br />\n		</span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A indústria brasileira trabalhou com apenas 68% da sua capacidade no primeiro trimestre do ano -o menor patamar nos últimos dez anos, segundo levantamento da CNI (Confederação Nacional da Indústria).<br />\n		Segundo a entidade, a queda ocorre porque os empresários tentam reduzir os estoques, que estão acima do esperado para esta época do ano.<br />\n		O movimento impactou a produção e o emprego de forma negativa no trimestre. </span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Os dois indicadores registraram as maiores quedas desde 1999.<br />\n		"Os estoques continuam acima do planejado. O ajuste do final de 2008 não foi suficiente para trazê-los ao nível desejado. Eles ficaram constantes no trimestre, mas os empresários queriam que eles tivessem caído", disse o economista da CNI Renato da Fonseca.<br />\n		O uso da capacidade instalada chegou a 78% no trimestre anterior à piora na crise internacional, mas já havia recuado para 74% no final de 2008.<br />\n		A queda no uso da capacidade no primeiro trimestre foi puxada pelas pequenas empresas, que estão trabalhando com o percentual de 62%. O percentual é maior nas médias (67%) e nas grandes empresas (73%).<br />\n		O indicador que mede o nível de produção recuou 4,7 pontos percentuais até março em relação ao trimestre anterior. Com isso, chegou ao menor patamar desde 1999, para 36,1 pontos -em uma escala de 0 a 100.<br />\n		Em relação ao número de trabalhadores na indústria, o indicador caiu para o nível recorde negativo de 39,2 pontos -redução de 4,8 pontos.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Pessimismo menor</span><br />\n		A CNI avalia que este trimestre ainda será um período de queda na produção, mas os economistas da entidade dizem esperar que haja uma queda menor do que a verificada no começo do ano.<br />\n		Entre os 1.329 empresários consultados, a maioria continua pessimista, mas a situação melhorou ante a última pesquisa. As exportações são o item que mais preocupa, seguido pela redução no número de empregados, compra de matérias- -primas e queda na demanda.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi3004200911.htm');
INSERT INTO `conteudo` VALUES (85, 'Produção industrial registra o pior resultado desde 1999', '2009-04-30', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Brasília, 30 de Abril de 2009 - Os efeitos da crise financeira internacional se intensificaram no setor industrial no primeiro trimestre. A produção da indústria nacional caiu para 36,1 pontos, número de 16,5 pontos menor que o registrado em igual trimestre de 2008 e 4,7 pontos abai-xo do índice apurado no trimestre anterior Os dados fazem parte da sondagem industrial da Confederação Nacional da Indústria (CNI) que mostra que a média histórica da produção é de 51,6 pontos. </span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Diante do arrefecimento do setor, a utilização da capacidade instalada recuou para 68%, ficando abaixo da média histórica de 74%. Em igual etapa do ano passado, o setor trabalhava com 75% da capacidade instalada. Ambos resultados são os piores desde 1999, início da série da pesquisa. O emprego também caiu para 39,2 pontos, 14,1 pontos abaixo do apurado em igual trimestre do ano passado. Na pesquisa, os indicadores variam de zero a 100 pontos, sendo que os números abaixo de 50 sinalizam expectativas negativas. Acima de 50, portanto, incide </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2468022,860,10,10/l">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; TEXT-DECORATION: underline">otimismo</span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">dos empresários. A pesquisa foi feita neste mês, até o dia 27, com 1,329 mil empresas, das quais 740 de pequeno, 386 de médio e 203 grande portes. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Segundo os economistas da CNI, a falta de demanda é o principal problema enfrentado pelas empresas. Além disso, os empresários continuam a reclamar da elevada carga tributária "e o acirramento da competição". Apesar da retração no primeiro trimestre deste ano, as estimativas dos industriais menos pessimistas, em relação ao desempenho da </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2468022,860,10,10/l">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; TEXT-DECORATION: underline">economia</span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O indicador de perspectiva de demanda subiu de 39,7 pontos, no último trimestre de 2008, para 48,3 pontos, em igual etapa deste ano. Melhoraram ainda as expectativas em relação ao emprego, cujo indicador subiu de 40,5 pontos para 44,8 pontos; e as previsões de compra de matéria-prima, cujo indicador passou de 38,9 pontos para 46,9 pontos, na mesma comparação. Porém, as previsões para as exportações continuam pessimistas. O indicador de expectativa de exportação recuou de 41,7, no último trimestre de 2008, para 39,8 pontos no trimestre seguinte. É o menor índice desde janeiro de 2002. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Segundo o estudo, os efeitos da crise internacional se intensificaram no início deste ano em todos os portes e atividades industriais. "Todos os 27 setores das indústrias de transformação e extrativa diminuíram o número de empregados". E, com exceção da indústria farmacêutica, as demais atividades reduziram a produção. Os setores com maiores quedas foram os de madeira, que recuou para 21,2 pontos; e o de metalurgia básica, cujo indicador caiu para 21,7 pontos. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Na pesquisa, a CNI destaca que as empresas permanecem com dificuldades para acessar os financiamentos no sistema financeiro, entre janeiro e março. O indicador de acesso ao </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2468022,860,10,10/l">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; TEXT-DECORATION: underline">crédito</span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">ficou em 32,4 pontos no primeiro trimestre e acumula queda de 15,2 pontos em relação ao igual período de 2008. Os estoques "mantêm-se praticamente" inalterados em relação ao último trimestre de 2008, ao oscilar para 49,9 pontos, ante 53,5 pontos. </span></p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2468022,860,10,10');
INSERT INTO `conteudo` VALUES (86, 'Análise - As oportunidades do pós-crise no Ceará', '2009-05-01', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">O economista Luís Eduardo Barros mostra que, para o Ceará, a crise econômica que assusta o mundo pode ser a chance de melhorar o mercado de trabalho, tanto em quantidade como em qualidade</p>\n	\n<p align="justify">Luís Eduardo Barros - economista, vice Presidente do Ibef-CE e diretor do CIC<br />\n		Especial para O POVO<br />\n		</p>\n	\n<p align="justify">Pode parecer irônico discutir oportunidades de trabalho durante a maior crise da economia mundial desde a Grande Depressão dos anos 30 do século passado. Mas, isso é mais do que necessário. É indispensável. Por uma série de motivos que merecem ser aprofundados. Desde a posição do Ceará em relação a crise, à dinâmica dos fatos econômicos em curso, o exemplo de crises anteriores e, principalmente, a disposição das pessoas diante dos desafios. <br />\n		</p></span>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Não podemos perder de vista que a crise atual é essencialmente financeira, tendo sido calamitosa para as bolsas de ações e os grandes capitais internacionais. Sendo assim, é fácil compreender que em um país sem capital excedente e que paga os mais elevados juros do mundo, as conseqüências dessa crise foram basicamente indiretas. O crescimento da economia e a segurança dos títulos do Governo absorviam a maior parte dos recursos financeiros, sem dar espaço à exuberância irracional do mercado de capitais norte-americano. Tanto é que nenhum banco brasileiro mantinha títulos sub-Prime em seu portifólio, razão básica para a não contaminação direta da economia brasileira, ao contrário de muitos bancos europeus e asiáticos. <br />\n		<br />\n		Na verdade, os efeitos da crise no Brasil estão sendo sentidos basicamente nos setores exportadores, devido à retração da demanda internacional, e nos setores industriais dependentes de crédito como veículos, linha branca etc. Não por acaso os setores focados pelo Governo com as medidas circunstanciais de redução de impostos como alternativa para mitigação dos efeitos danosos da crise. Já em março a maior parte dos estoques de veículos havia sido escoada e o volume de crédito concedido pelo sistema financeiro timidamente voltou a apresentar crescimento em relação ao mesmo período de 2008, comprovando estarem sendo eficazes as medidas tomadas internamente. <br />\n		<br />\n		Ora, se a economia brasileira teve pouco impacto direto da crise, o que esperar de um estado pequeno e pobre como o Ceará, sem excedentes financeiros no seu DNA econômico? Com uma economia ancorada principalmente nos serviços e uma indústria mais voltada para o mercado interno de baixa renda, o Ceará encontra-se a léguas de distância do epicentro da crise. Mais ainda porque o governo estadual vinha entesourando caixa o que lhe permite não só manter como ampliar o volume de investimentos conforme previsto antes da crise. <br />\n		<br />\n		Circunstancialmente, uma porção de investimentos estratégicos como a refinaria de petróleo, a siderúrgica, a termoelétrica a carvão, os parques eólicos, o centro de feiras e muitos outros sonhos cearenses, avançaram vigorosamente nos últimos anos, independentemente da crise. Os mais pessimistas reconhecem que, mesmo no pior cenário, atrasos são esperados, mas nenhum dos investimentos teve sua implantação cancelada. Não por serem cearenses ou por prestígio do governador ou de políticos locais! <br />\n		<br />\n		A força desses investimentos é que são estratégicos por si mesmos e os recursos já estavam programados. Restrições circunstanciais de crédito poderão acarretar atrasos até que as fontes de financiamentos sejam recompostas, mas a Petrobrás precisará em poucos anos de capacidade adicional par refinar o petróleo excedente que o Brasil estará produzindo após a recente auto-suficiência, precisando ser exportado com maior valor agregado do que se for exportado como óleo cru. Da mesma forma, a demanda de placas de aço estava esgotada em nível mundial e continuará assim no longo prazo, mesmo que se admita que o mercado internacional sofrerá dificuldades nos próximos anos. Basta lembrarmos que um empreendimento do porte de uma siderúrgica, como a projetada para o Ceará, exigirá para sua implantação um prazo muito mais longo do que será a duração da crise mundial. O mesmo raciocínio vale para a termoelétrica, os parque eólicos e outros investimentos na infra-estrutura, dimensionados a partir de demandas confirmadas. <br />\n		<br />\n		Paralelamente, a consciência cada vez mais firme na vocação natural do Ceará para o turismo, tem levado a profundos estudos para incrementar a competitividade do estado como destino turístico. Desse diagnóstico foram gestados muitos projetos de investimentos no Ceará, desde os vários "resorts" com campos de golfe em construção, à duplicação das rodovias de acesso a esses empreendimentos em estágio avançado, os projetos como a construção do Centro de Feiras, do Aquário de dimensões e padrão internacionais, dos projetados aeroportos regionais para alavancar o acesso a áreas turísticas interestaduais como Lençóis maranhenses - Foz do Parnaíba - Jericoacoara - Camocim ou mesmo Canoa Quebrada - Tibau - Mossoró. <br />\n		<br />\n		Além desses setores merecedores de investimentos estratégicos, emerge a construção civil como a grande geradora de empregos, principalmente para os menos qualificados, principalmente quando os governantes compreenderam a capacidade desse setor de movimentar a economia, devido sua extensa e abrangente cadeia produtiva. Esse potencial não só foi compreendido, como tem merecido vultosos incentivos do governo federal, que vão de financiamento barato à redução de impostos e programas especiais voltados para a baixa renda. <br />\n		<br />\n		O fato é que as oportunidades de emprego no Ceará mostram um forte potencial de crescimento a curto prazo. O preocupante nesse cenário é que todas essas oportunidades podem não se concretizar, por falta de candidatos qualificados, o que confirmaria a tragédia de nosso sistema de ensino que não prepara o aluno para o mercado de trabalho. </span><br />\n	</p>', 'CE', 'jornal O Povo - Política', 'http://www.opovo.com.br/opovo/politica/874493.html');
INSERT INTO `conteudo` VALUES (87, 'Indústria vive recuperação ', '2009-05-06', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	\n<p>Segundo o instituto, desde o início do ano, setor reage e supera produção do mês anterior, mas ainda de forma "gradual"<br />\n		Em relação ao 1º trimestre do ano passado, queda ainda é forte, de 14,7%; produção de bens de capital recua pela 1ª vez em 22 trimestres</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">PEDRO SOARES</span><br />\n	\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA SUCURSAL DO RIO </span><br />\n	<br />\n	\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A indústria viveu no primeiro trimestre uma fase de "suave" e "gradual" recuperação, que resultou em uma expansão de 4,8% (na taxa livre de influências sazonais acumulada no período) desde que bateu no fundo do poço, em dezembro, segundo o IBGE. Em março, o crescimento na margem foi de 0,7% em relação a fevereiro, quando a produção havia subido com mais força -1,9%.<br />\n		No último trimestre do ano passado, a produção industrial havia caído 20,1% em relação ao nível de setembro, sob efeito do agravamento da crise.<br />\n		Pelos dados do IBGE, a indústria recuou 14,7% no primeiro trimestre em relação aos três primeiros meses do ano passado, o pior resultado desde 1991. Essa taxa negativa, somada à queda do último trimestre de 2008, colocou o setor fabril em recessão e fez a produção registrar uma queda acumulada de 16,7% -a mais intensa desde 1990, quando o país sofria os efeitos do Plano Collor.</span></p>', '\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Desde o início do ano, a indústria reage e suplanta a produção do mês anterior, mas o fato é que esse movimento tem sido ainda muito moderado. Há uma recuperação importante, mas muito suave e gradual, a ponto de os indicadores comparativos com 2008 permanecerem ainda negativos", disse Silvio Sales, coordenador de Indústria do IBGE.<br />\n		Em março, a retração foi de 10% em relação ao mesmo período de 2008, impulsionada para baixo especialmente pelo fraco desempenho de bens de capital, categoria que inclui máquinas e equipamentos e sinaliza o comportamento dos investimentos.<br />\n		Segundo o IBGE, a produção de bens de capital cedeu 6,3% ante fevereiro, única categoria com taxa negativa no mês. Caiu 23% em relação a março de 2008. No primeiro trimestre, acumulou retração de 20,8% em relação ao mesmo período de 2008, interrompendo uma trajetória de 22 trimestres consecutivos de expansão.<br />\n		Os dados, dizem especialistas, enterraram um longo e promissor período de crescimento do investimento. "A indústria saiu do fundo do poço e passa por uma fase de recuperação lenta, mas o fraco desempenho dos bens de capital trava uma perspectiva de melhora mais acelerada", diz Sérgio Vale, da MB Associados.<br />\n		Para Bráulio Borges, da LCA, a produção de máquinas e equipamentos frustrou as expectativas e explica o resultado ainda modesto da indústria no primeiro trimestre. "Os bens de capital limitaram a recuperação da indústria."<br />\n		Na visão de Marcela Prada, o fraco resultado de bens de capital indica a piora da expectativa de empresários, que já postergam ou cancelam investimentos diante da menor demanda -especialmente a externa.<br />\n		O tombo de bens de capital minimizou os efeitos positivos da desova de estoques acumulados pelas indústrias desde que a crise eclodiu. Reduz ainda o impacto das medidas de estímulo do governo à produção, como a redução do IPI sobre carros e mais recentemente sobre eletrodomésticos.<br />\n		"Os estoques voltaram aos níveis normais, o que sinaliza um desempenho melhor daqui para a frente. Mas os bens de capital frustraram", diz Borges.<br />\n		Por seu turno, o crescimento na fabricação de veículos impulsionou o desempenho de bens duráveis -alta de 1,7% de fevereiro para março, a maior entre as categorias pesquisadas. Com expansão de 7%, a produção de veículos automotores liderou a alta da indústria.</span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 18pt">\n			\n<p>Para analistas, resultado empurra país para a recessão, com recuo de até 2% no 1º tri</p></span>\n		<span style="FONT-SIZE: 18pt">\n			\n<p>&nbsp;</p></span>\n		\n<p>\n			<span style="FONT-SIZE: 12pt"></span></p>\n		<span style="FONT-SIZE: 10pt">\n			\n<p>DA SUCURSAL DO RIO</p></span>\n		\n<p>\n			<span style="FONT-SIZE: 12pt"></span></p>\n		\n<p>O fraco desempenho da indústria desde o recrudescimento da crise, em setembro de 2008, empurrou o país para uma recessão, segundo especialistas ouvidos pela \n			<span style="FONT-WEIGHT: bold">Folha</span>. É que, se as expectativas se confirmarem, o PIB brasileiro cairá por dois trimestres consecutivos na comparação com o trimestre imediatamente anterior, o que caracteriza a chamada "recessão técnica".<br />\n			Pelas previsões da LCA, a economia do país encolheu de 0,5% a 1% no primeiro trimestre deste ano na comparação com o quarto trimestre de 2008 na taxa livre de influências sazonais. O IBGE constatou uma retração de 3,6% nos últimos três meses de 2008, o que interrompeu o maior ciclo de crescimento sustentado do Brasil desde o Real. Foi o pior desempenho do PIB da série histórica, iniciada em 1996.<br />\n			Na comparação com o terceiro trimestre de 2008, a LCA projeta uma queda do PIB entre 1,5% e 2%. "Houve uma recuperação bem mais tímida da indústria do que o esperado, o que rebateu no PIB", afirma Bráulio Borges, economista da consultoria.<br />\n			Nas contas da Tendências, o PIB deve ter recuado 1,5% do quatro trimestre de 2008 para o primeiro de 2009. "Será um dado bem negativo e claramente puxado para baixo pela indústria", afirma Marcela Prada, economista da consultoria.<br />\n			Segundo Sérgio Vale, economista da MB Associados, os dados já divulgados não deixam dúvida de que o país vive um período de recessão -que não será tão prolongado como os anteriores, mas também consumirá "um bom tempo" até que ocorra uma recuperação.<br />\n			Vale estima uma retração de 1,3% ante o último trimestre de 2008. Em relação ao terceiro trimestre de 2008, a projeção é de uma queda de 2,4%.<br />\n			"Nessa crise, o país entrou numa recessão com uma rapidez e uma intensidade muito fortes. É de esperar, por isso, uma retomada mais rápida do que nas crises passadas. Mas essa recuperação ainda vai demorar. Vai consumir um bom tempo até que o país volte aos níveis registrados até setembro do ano passado", diz Vale.<br />\n			O Brasil, diz, é ainda mais prejudicado pela fraca retomada da economia global, que reduz as exportações e, consequentemente, afeta a indústria.<br />\n			Foi justamente a indústria que teve desempenho abaixo do esperado e levou o país à recessão, segundo os especialistas. Para este ano, a previsão é que a produção industrial recue até 5%, o que resultará numa queda do PIB próxima a 0,5% "Não há nenhum indício nos dados de produção industrial que sinalizem alguma retomada mais consistente da indústria", diz a Rosenberg e Associados, em relatório.</p></span></div>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0605200907.htm');
INSERT INTO `conteudo` VALUES (88, 'Produção industrial cai 14,7% no trimestre', '2009-05-06', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">São Paulo, 6 de Maio de 2009 - A forte retração da produção industrial nos primeiros três meses do ano indica que a </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2475378,860,10,4/l">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; TEXT-DECORATION: underline">economia</span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">brasileira não escapou do quadro de recessão técnica, caracterizado por dois trimestres consecutivos de queda. Por outro lado, para analistas consultados pela Gazeta Mercantil, há sinais de que a indústria deve começar a reagir a partir do segundo trimestre deste ano. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em março, a produção industrial apresentou uma leve recuperação, com crescimento de 0,7% sobre fevereiro, mas retração de 10% em relação a igual mês do ano passado, segundo o Instituto Brasileiro de Geografia e Estatística (IBGE). Com o resultado, acumulou três primeiros meses do ano uma queda de 14,7% na comparação com o primeiro trimestre do ano passado, o pior desempenho desde o primeiro trimestre de 1991. "O crescimento de 0,7% é muito tímido. Mesmo a indústria crescesse 2%, todos os meses, ainda fecharia o ano no negativo", diz o economista Rogério César de Souza, do Instituto para Estudos do Desenvolvimento Industrial (Iedi). Em 12 meses, a produção recuou 1,9%.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Segundo o economista-chefe da LCA Consultores, Bráulio Borges, a produção industrial cresceu apenas 4,8% entre janeiro e março em relação aos níveis de dezembro do ano passado, após uma redução de 20,1% nos últimos três meses de 2008 do patamar de atividade pré-crise, em setembro. "A indústria recuperou apenas um quarto das perdas", afirma o economista. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A consultoria projeta uma queda de 0,5% a 1,5% do Produto Interno Bruto no primeiro trimestre do ano em relação aos último três meses de 2008. Na comparação o primeiro trimestre do ano passado, a retração deve ser de 1,5% e 2%. "E, se isso se confirmar, será interrompida uma sequência de 28 trimestres consecutivos de desempenho positivo na comparação anual", diz Borges. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Mas atividade pode começar a reagir a partir do segundo trimestre. Na sua avaliação, o Brasil seguiu o roteiro dos Estados Unidos, onde o consumo de estoques foi responsável por metade da queda de 6% do PIB. No caso brasileiro, sem a influência dos estoques, o PIB ficaria estável em relação ao trimestre anterior. "O lado bom é que, no segundo trimestre, a indústria vai ter que reagir. As empresas começaram abril com os estoques normalizados", diz Borges, que prevê uma retomada dos patamares de produção mais próximos do período pré-crise só no segundo semestre. Para o ano, a projeção é de crescimento de 0,9% do PIB. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Marianna Costa, da Link Investimentos, que espera uma expansão de 0,5% do PIB em 2009, também projeta uma recuperação da atividade apenas em meados do ano. Para a economista, a produção industrial deve levar o PIB do primeiro trimestre a uma queda de 1,3% na margem e de 2,4% em relação ao mesmo período de 2008. "A indústria continua mostrando a tendência nos últimos meses. A atividade ainda está deprimida." </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para Ariadne Vitoriano, da </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2475378,860,10,4/l">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; TEXT-DECORATION: underline">Tendências</span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Consultoria Integrada, a produção industrial deverá apresentar alta mais significativas apenas no segundo trimestre de 2009. Esta melhora será resultado do efeito da redução do Imposto sobre Produtos Industrializados, das vendas firmes do varejo, da queda da Selic que começa a surtir efeito na economia e dos ajustes de estoques. O ganho em março ainda é marginal e o desempenho da indústria "permanece fraco", assinala. Segundo Ariadne, mesmo com uma retomada da atividade industrial em meados deste ano ao patamar de desempenho de 2008 está fora de alcance para a indústria. A projeção é de um recuo de 5% da produção industrial de 2009. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Inês Filipa Marques Pereira, da Arkhe Corretora, destaca que com uma base de comparação alta em 2008 e o desaquecimento da economia, as taxas mensais ainda deverão mostrar desempenho negativo, enquanto nos dados com ajuste sazonal a indústria deve mostrar recuperação moderada. Para a economista, o resultado de março mostra uma atividade produtiva operando sem uniformidade, com uma parte mostrando recuperação, caso do setor automotivo e impacto positivo sobre toda a cadeia que o alimenta. "Porém, algumas atividades ainda sofrem com o arrefecimento econômico, principalmente os setores ligados à produção de bens de capital, refletindo um menor nível de investimentos", disse. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Investimentos em queda </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em março, a indústria automobilística apresentou boa recuperação e puxou a alta da produção, com crescimento de 7% no mês, além do crescimento do setor farmacêutico (9%), produtos químicos (3,5%) e instrumentos médico-hospitalares (20,8%). O pior desempenho de março ocorreu em bens de capital, que fechou o mês com recuo de 23%. No primeiro trimestre, o setor acumula queda de 20,8%, após 22 meses trimestres de crescimento. Segundo Ariadne, da Tendências, a indústria de máquinas e equipamentos terá uma recuperação lenta porque depende de outros segmentos para conseguir crescer. "Se o investimento é adiado, compromete a produção futura. Esperamos que isso se reverta nos próximos meses", completa Souza, do Iedi, defendendo incentivos fiscais para que as empresas voltem a </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2475378,860,10,4/l">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial; TEXT-DECORATION: underline">investir</span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Com o resultado, a LCA projeta uma queda de 11% da formação bruta de capital fixo no primeiro trimestre em relação ao mesmo período do ano passado. No quarto trimestre de 2008, o investimento ainda estava em alta, de 3,8%, na comparação com 2007. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No setor de máquinas e equipamentos, as quedas mais expressivas ocorreram na produção de bens de capital para energia elétrica ( - 51,8%) e construção ( - 68,9%). A retração acentuada na fabricação de bens de capital para o setor elétrico e de construção, é decorrente de um movimento pontual, analisa Paulo Godoy, presidente da Associação Brasileira da Infra-Estrutura e Indústrias de Base (Abdib). </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">As distribuidoras de energia elétrica não sofreram impactos significativos com a retração da atividade econômico. "Pode ter ocorrido postergação de compras regulares na distribuição", comenta Godoy. Na indústria de construção civil, o recuo é "relativamente normal" neste período. Sem perspectiva de expansão, as construtoras utilizam capacidade ociosa adiam a decisão de investir. </span></p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2475378,860,10,4');
INSERT INTO `conteudo` VALUES (89, 'ACIMA DA MÉDIA NACIONAL - Produção industrial no CE cresce 1,5%', '2009-05-08', '\n<span style="FONT-SIZE: 18pt; FONT-FAMILY: Arial">\n	\n<p>&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Com variação superior à média nacional (0,7%) e do Nordeste (0,1%), a produção industrial do Ceará avança pelo segundo mês consecutivo, mas continua com resultados negativos frente aos indicadores de 2008. Em março, a indústria cearense cresceu 1,5%, com ajuste sazonal, acumulando ganho de 2,4% entre o mês pesquisado e janeiro. Mas, no confronto com igual período de 2008, mostra recuo de 7,0%. No fechamento do primeiro trimestre, o comportamento é decrescente tanto frente a igual período do ano anterior (-7,5%) como em relação ao último trimestre de 2008 (-3,0%). A taxa anualizada, indicador acumulado nos últimos doze meses (-0,4%), segue em trajetória descendente desde setembro de 2008 (3,8%). Os dados são da Pesquisa Industrial Mensal (PIM), divulgada ontem pelo IBGE (Instituto Brasileiro de Geografia e Estatística).</span><br />\n		</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Para Pedro Jorge Ramos, coordenador de Economia e Estatísticas do Indi (Instituto de Desenvolvimento Industrial), da Federação das Indústrias do Ceará (Fiec), ´já começou a recuperação da indústria. O resultado do IBGE casa com os indicadores do Indi, que registram aumento da capacidade instalada´. ´As características do parque fabril cearense são inelásticas em relação à renda. O que é fabricado é consumido no mercado interno, o que garantiu essa tendência de recuperação mais cedo´, explica.<br />\n		<br />\n		Mesmo com resultados positivos em fevereiro e março, o setor fabril cearense completa uma seqüência de cinco meses de taxas negativas, frente a março de 2008. Nesta comparação, sete dos dez segmentos pesquisados apontaram redução na produção, com o principal impacto vindo de alimentos e bebidas (-12,9%). Puxaram também a taxa para baixo os produtos químicos (-18,8%), a metalurgia básica (-55,5%) e o têxtil (-4,9%), por conta, respectivamente, de recuos nos itens vacinas veterinárias; vergalhões e barras de aços ao carbono; e em tecidos de malha e de algodão.<br />\n		<br />\n		Por outro lado, a maior pressão positiva veio de refino de petróleo e da produção de álcool (40,8%), impulsionado sobretudo pelo avanço na produção de 80% dos produtos investigados no setor, com destaque para o óleo diesel e gasolina.<br />\n		<br />\n		No acumulado janeiro-março, a indústria cearense também registrou queda (7,5%). Novamente alimentos e bebidas (-13,6%) assinalou a contribuição negativa mais relevante, vindo a seguir calçados e artigos de couro (-10,6%), metalurgia básica (-55,2%) e máquinas, aparelhos e materiais elétricos (-27,3%).<br />\n		<br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Nordeste</span><br />\n		<br />\n		Em março, a atividade industrial no Nordeste apresentou variação de 0,1% em relação a fevereiro, na série livre dos efeitos sazonais, terceira taxa positiva consecutiva, acumulando neste período um ganho de 5,4%. Na comparação contra igual período do ano anterior observa-se recuo de 4,9%. Nos três primeiros meses de 2009 os resultados foram negativos tanto frente a igual período do ano anterior (-9,4%), quanto em relação ao último trimestre de 2008 (-2,1%). Nos últimos 12 meses a taxa da do Nordeste mostrou retração de 2,4%, mantendo também comportamento de queda observada desde o último mês de setembro. O recuo de 4,9% no indicador mensal é devido à queda em oito dos 11 segmentos pesquisados pelo IBGE. O principal impacto total na região nordestina veio de produtos químicos (-12,0%), seguido por metalurgia básica (-22,9%) e máquinas, aparelhos e materiais elétricos (-40,8%).</span></p>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">CNI APONTA<br />\n		<br />\n		Indústria dá sinais de melhora em março<br />\n		<br />\n		</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Brasília. Os indicadores industriais no mês de março, divulgados ontem pela Confederação Nacional da Indústria (CNI), mostram que a atividade fabril continuou se retraindo em relação ao mesmo período do ano passado, mas na comparação com fevereiro, já há sinais de melhora. As vendas reais, que são medidas pelo faturamento da indústria, apresentaram uma elevação de 2,9% em março, em termos dessazonalizados, ante fevereiro, enquanto na comparação com março de 2008 houve uma queda de 1,6%. No primeiro trimestre, o declínio acumulado foi de 7,6% em relação a igual intervalo do ano passado.<br />\n		<br />\n		A utilização da capacidade instalada também registrou crescimento no terceiro mês do ano ante fevereiro, após cinco meses seguidos de queda na comparação com o mês anterior. O índice passou de 78,2% em fevereiro para 78,7% em março de 2009, também descontados os efeitos sazonais. Em março do ano passado, a capacidade instalada na indústria era de 83%.<br />\n		<br />\n		Já as horas trabalhadas na produção recuaram pelo segundo mês consecutivo. A queda foi de 0,2% ante fevereiro e de 6,6% ante março de 2008. No acumulado do primeiro trimestre, o indicador diminuiu 7,4% em relação ao mesmo período de 2008. A indústria também fechou postos de trabalho em março pelo quinto mês seguido. O emprego industrial caiu 0,7% no terceiro mês do ano ante fevereiro e 2,5% na comparação com março do ano passado. No acumulado do primeiro trimestre, a queda foi de 1,4% em relação aos primeiros três meses de 2008. Já a massa salarial caiu 1,8% em março ante março de 2008.</span></p>', 'EP', 'jornal Diário do Nordeste - Negócios', 'http://diariodonordeste.globo.com/materia.asp?codigo=636643');
INSERT INTO `conteudo` VALUES (90, 'Indicadores apontam nova baixa da indústria em abril', '2009-05-09', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Após recuar 10% em março, ante 2008, setor industrial deve cair 13% em abril<br />\n		<br />\n		Redução na produção de veículos, na demanda de energia e no transporte de cargas indica queda anual maior que a vista em março</span></p>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal">\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">VINICIUS TORRES FREIRE</span><br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">COLUNISTA DA FOLHA </span><br />\n		<br />\n		</span>\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">A atividade industrial deve ter recuado ainda mais em abril, se os dados forem comparados com o mesmo mês de 2008. Os indícios de nova queda forte da produção da indústrias apareceram em números divulgados até ontem sobre produção de veículos, carga de energia elétrica e tráfego de veículos pesados referentes a abril (vide gráfico). Tais números permitem, normalmente, antecipar com alguma precisão o dado oficial da produção industrial, calculado pelo IBGE.<br />\n		</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Estimativa preliminar produzida pela \n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Folha</span> indica que a produção industrial de abril deve ter recuado em torno de 13% (ante abril de 2008). Marcela Prada, da consultoria Tendências, estima também preliminarmente uma queda de 14%. Mas, por ora, acredita que, em relação a março passado, em abril a indústria pode ter crescido entre 1,5% e 2%. Ainda assim, sua consultoria estima que a indústria terá encolhido 5% ao final de 2009.<br />\n		Em março, segundo o indicador produzido pelo IBGE, a indústria brasileira cresceu 0,7% sobre fevereiro, depois de altas de 1,9% em fevereiro e 2,1% em janeiro, sempre em relação ao mês anterior. Ou seja, o ritmo de recuperação em relação à queda brusca de 12,7% de dezembro vinha sendo cada vez mais fraco. Em relação a março de 2008, a queda foi de 10%.<br />\n		Os indicadores mais recentes do ânimo na indústria são os da Sondagem Industrial da Fundação Getulio Vargas. Um deles, a percepção do nível adequado de estoques, melhorou em abril, mas ainda estava em valor compatível com uma atividade industrial muito fraca.<br />\n		Em março, quando atingiu o pior nível em quase seis anos, equivalia aos valores registrados na crise de 2001 (apagão, crise argentina, atentados terroristas, recessão nos EUA) e em meados de 2003, quando a economia estagnou devido aos choques de 2002 (desvalorização do real, fuga de capitas, eleição de Lula).<br />\n		O nível ainda alto de estoques e a conclusão dos investimentos projetados antes da crise devem reduzir bastante as novas despesas em capital, puxando a indústria para baixo. O faturamento real da indústria de máquinas e equipamentos caiu 25% no primeiro trimestre de 2009 (ante 2008), segundo a Abimaq (Associação Brasileira da Indústria de Máquinas). O resultado de março foi 30% inferior à média registrada em 2008.</span></p>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi0905200907.htm');
INSERT INTO `conteudo` VALUES (91, 'TELINHA', '2009-05-10', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	\n<p>\n		<span style="FONT-FAMILY: Arial">\n			<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt">>> Presidente do PPS, o ex-senador Roberto Freire é a figura central do Ciclo de Debates (programa do CIC - Centro Industrial do Ceará), esta noite (start: às 7), na TVC. O ex-comunista discute o momento político do BR à côté do representante da OAB no Conselho Nacional de Justiça, Jorge Hélio Chaves Oliveira, e do jornalista Luís Sérgio Santos.</span></span></p></span>', '', 'CE', 'Jornal O Povo - Col. Sônia Pinheiro', 'http://www.opovo.com.br/opovo/colunas/soniapinheiro/876515.html');
INSERT INTO `conteudo` VALUES (92, 'Emprego na indústria tem pior desempenho em 8 anos', '2009-05-13', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	\n<p>Queda de 5% em relação a março do ano passado é o maior tombo desde 2001<br />\n		<br />\n		Para o IBGE, movimento de queda é generalizado, pois atingiu todos os indicadores, as regiões e quase todos os setores</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Combalido pela crise, o mercado de trabalho na indústria registrou, em março, o pior desempenho em oito anos: o nível de emprego no setor caiu 5% em relação a março de 2008, o maior tombo desde o início da série do IBGE, em 2001.<br />\n		Já em relação a fevereiro, a ocupação teve retração de 0,6% na taxa com ajuste sazonal, o sexto resultado negativo seguido nessa base de comparação. Em queda desde outubro, o indicador registra perda acumulada de 5,8%.</span></p>', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Até mesmo o rendimento no setor, que se mantinha em alta graças à inflação mais baixa, não resistiu: a folha de pagamento recuou 2,2% na comparação com março de 2008, a primeira taxa negativa desde o começo da crise. Ante fevereiro, houve queda de 2,3%.<br />\n		Segundo André Macedo, economista do IBGE, os efeitos da crise ainda afetam o mercado de trabalho industrial. "O movimento de queda é generalizado, atingindo todos os indicadores, as regiões e quase todos os setores. Esse movimento acompanha o menor dinamismo da produção da indústria."<br />\n		Pelos dados do IBGE, foram fechados postos de trabalho em 14 dos 18 ramos industriais pesquisados e em todos os 14 locais -com destaque para São Paulo, maior parque fabril do país, com perda de 4% em relação a março de 2008.<br />\n		Para Macedo, a tendência de "suave recuperação" apontada pelos dados de produção de março não se refletiu em melhora do mercado de trabalho industrial. Naquele mês, a indústria cresceu 0,7% ante fevereiro, num sinal, diz o IBGE, de "reação gradual" do setor.<br />\n		O único sinal positivo, diz Macedo, é o fato de a ocupação ter caído menos em março do que em meses anteriores -tanto em janeiro como em fevereiro, o recuo havia sido de 1,4% na taxa com ajuste sazonal.<br />\n		É, porém, insuficiente para prever a recuperação do emprego industrial no curto prazo. Os próprios números da pesquisa não trazem alento, já que o total de horas pagas pelo setor caiu 0,9% em relação a fevereiro e 5,6% ante março de 2008. O indicador é antecedente ao emprego, ou seja, quando crescem as horas trabalhadas é sinal de contratações no futuro.<br />\n		<br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Fundo" ficou para trás</span><br />\n		"Não há sinalização de resultados positivos mais à frente, se olharmos as horas pagas como um indicador antecedente. A indústria ainda mantinha estoques no final do primeiro trimestre e não precisou lançar mão de horas extras."<br />\n		Já para Fábio Romão, economista da LCA, o emprego fabril tende a melhorar em abril, embalado sazonalmente pelo início do processamento da safra de cana-de-açúcar -que emprega especialmente na indústria paulista.<br />\n		O economista estima que o setor tenha aberto quase 34 mil vagas, segundo projeção feita com base nos dados do Caged (Cadastro Geral de Empregados e Desempregados) -que em março registrou o fechamento de 35 mil postos de trabalho formais na indústria.<br />\n		"A queda do emprego começou a estancar, depois de um ajuste muito rápido e intenso. O fundo do poço ficou no primeiro trimestre." A indústria voltou a contratar, diz, ainda que de modo "muito lento".<br />\n		Para o Iedi (Instituto de Estudos para o Desenvolvimento Industrial), o crescimento do emprego em relação a fevereiro mostra que "a queda perdeu ritmo em março", o que é "um bom sinal". "Não significa que, no curto prazo, os problemas do emprego industrial estejam resolvidos. Os ajustes podem continuar", diz o instituto.</p>\n	\n<p align="justify">&nbsp;</p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n			\n<p align="justify">Todas as classes consomem mais<br />\n				no 1º tri </p></span>\n		\n<p align="justify">Pesquisa da LatinPanel aponta mudança nos hábitos dos consumidores, com mais refeições em casa</p></span>\n	\n<p align="justify"><br />\n		<br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">TATIANA RESENDE</span><br />\n		\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">DA REDAÇÃO </span><br />\n		<br />\n		A crise ainda não afetou o consumo das famílias brasileiras com alimentos, bebidas, higiene e beleza e limpeza do lar. Segundo pesquisa da LatinPanel, houve aumento em gastos e em volume em todas as cestas pesquisadas e classes sociais no primeiro trimestre ante igual período do ano anterior.<br />\n		O volume de compras subiu 9%, e os gastos, 15%, com destaque para o consumo das famílias das classes D e E, beneficiadas pelo aumento real do salário mínimo e por programas sociais, como o Bolsa Família.<br />\n		Parte dessa expansão se deve à maior folga no orçamento mensal com a diminuição na demanda por bens duráveis, que perderam mercado com a retração no crédito.<br />\n		A frequência de idas ao ponto de venda também cresceu: 6% no geral e 8% entre a população de baixa renda. "O consumidor está mais crítico, fazendo compras mais "picadinhas". Vai comprando à medida que precisa", afirma Ana Claudia Fioratti, diretora-executiva do instituto de pesquisa que acompanha semanalmente 8.200 domicílios em todo o país.<br />\n		A cesta de higiene e beleza foi a que teve o maior crescimento nos gastos no primeiro trimestre e nos últimos 12 meses encerrados em março na comparação com os respectivos períodos anteriores.<br />\n		Para Marcos Quintarelli, consultor especializado em varejo, isso reflete as despesas mais criteriosas também com serviços. Um exemplo: as pessoas preferem fazer tratamento e pintura de cabelo em casa em vez de ir ao cabeleireiro. "O consumidor está trocando hábitos de consumo e isso deve continuar até o fim do ano."<br />\n		Outra prova disso é que, pela primeira vez desde o início da pesquisa desse dado, em 2004, as despesas com alimentação em casa (7,8%) subiram acima dos gastos com refeição fora (3,6%), considerando 2008 ante o ano anterior. No confronto de 2007 com 2006, os consumidores tinham gastado 5,4% menos para se alimentar no lar e 8,5% mais para comer fora.<br />\n		Na avaliação da diretora da LatinPanel, em tempos de crise o consumidor fica ainda mais atento à relação custo-benefício. Nos últimos 12 meses terminados em março, quase metade das categorias pesquisadas (46%) teve expansão em volume e em gasto, como suco pronto para beber, massa instantânea e sorvete.<br />\n		Os supermercados estão de olho nesse nicho. De acordo com o presidente da Abras (associação do setor), Sussumu Honda, as opções de comidas pré-prontas nas gôndolas vêm crescendo acompanhando a demanda, que inclui também os consumidores que levam esses pratos para o trabalho para economizar no almoço.<br />\n		Estudo em 11 mercados da Europa, da Ásia e da América Latina feito pela TNS World Panel, grupo ao qual pertence a LatinPanel, aponta o Brasil como o terceiro colocado em expansão no volume de compras de bens não duráveis, considerando a variação nos últimos 12 meses até fevereiro. Em gasto, o país ocupa a quarta posição.</p></span>', 'EP', 'jornal Folha de São Paulo - Dinheiro', 'http://www1.folha.uol.com.br/fsp/dinheiro/fi1305200912.htm');
INSERT INTO `conteudo` VALUES (93, 'Indústria gera 19 mil postos em abril no estado de São Paulo', '2009-05-15', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">15 de Maio de 2009 - O emprego na indústria de transformação paulista começa a sinalizar </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2494355,860,10,10/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">estabilidade</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">, segundo Paulo Francini, diretor do departamento de </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2494355,860,10,10/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">economia</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">da Federação das Indústrias do Estado de São Paulo (Fiesp). "Estamos em rota de estabilização, o que não surpreenderia se em maio chegássemos a uma estabilidade", disse. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">No mês passado, a atividade industrial teve um saldo positivo de 19 mil vagas, o que representa um crescimento (sem ajuste sazonal) de 0,8% em relação a março. No entanto, ao considerar a sazonalidade do período, o nível de emprego apresenta queda de 1,09%. No primeiro quadrimestre as fábricas paulistas demitiram 47,5 mil trabalhadores - uma redução de 1,95% face o mesmo período de 2008. Nos últimos 12 meses, o nível de emprego na indústria acumula queda de 6,76%. </span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com Francini, o setor de açúcar e álcool foi o principal responsável pela geração de emprego em abril. "O movimento perdeu força em relação a março, mas ainda reflete um maior volume de contratações para atender a safra e usinagem", explica o economista, destacando que o índice de crescimento do setor atingiu 19,8% no período. Já os demais segmentos industriais juntos acumulam queda de 0,4%. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Cortes em 17 setores A pesquisa da Fiesp mostra que 17 setores fizeram demissões e 5 realizaram contratações (fabricação de coque, produtos derivados do petróleo e biocombustíveis; produtos alimentícios; produtos diversos, bebidas e confec-ção de vestuário e acessórios). "Entre as fábricas que demitiram, vale destacar a redução de 1,8% no quadro de pessoal do setor de máquinas e equipamentos. Sinal amarelo para </span><a href="http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2494355,860,10,10/l">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">investimentos</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">", disse Francini. </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com o diretor da Fiesp, foi essa expansão no mercado de açúcar e álcool que puxou um crescimento de 2,5% no interior, com destaque para Sertãozinho (+6,17%), Rio Claro (+9,9%) e Jaú (21,23%). Diante disso, o nível de emprego no interior cresceu 2,15% enquanto na Grande São Paulo caiu 1,26%. "Por outro lado, o desemprego se intensificou na Grande São Paulo devido, principalmente, aos cortes em montadoras e empresas ligadas a auto-peças", afirma. Entre as cidades que registraram maior índice de demissão estão Mogi das Cruzes (-3,96%), Cotia (-2,08%) e São Caetano do Sul (-2,63%).</span></p>', 'EP', 'Jornal Gazeta Mercantil - Brasil', 'http://www.gazetamercantil.com.br/GZM_News.aspx?parms=2494355,860,10,10');
INSERT INTO `conteudo` VALUES (94, 'INCLUSÃO DIGITAL', '2009-05-15', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	\n<p>\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Tarcísio Pequeno, presidente da FUNCAP (Fundação Cearense de Apoio ao Desenvolvimento Científico), Baltazar Neto,  presidente do Instituto Titan e ex-presidente do CIC (Centro Industrial do Ceará), e o empresário Ricardo Liebmann debatem sobre inclusão digital no programa Ciclo de Debates, que vai ao ar neste domingo, 17, às 19 horas, pela TVC. A reprise agora é às quartas-feiras, às 22 horas.</span></p></span>', '', 'CE', 'jornal O Estado - Caderno Linha Azul - Col. Solange Palhano', 'http://www.oestadoce.com.br/index.php?acao=colunas&subacao=ler&colID=9');
INSERT INTO `conteudo` VALUES (95, 'EDUCAR & AÇÃO', '2009-05-20', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">\n		<span style="FONT-WEIGHT: normal">Cláudio de Alencastro, especialista em educação e articulista da Veja, dará palestra segunda-feira em Fortaleza. Durante o lançamento do projeto Observatório Social - Educação, às 8h30min, na Fiec, dentro dos 90 anos do Centro Industrial do Ceará. O projeto vai monitorar os indicadores da educação e expor para que a população aprenda a cobrar. </span></p></span>', '', 'CE', 'Jornal O Povo - Cidade - Col. Vertical', 'http://www.opovo.com.br/opovo/colunas/vertical/878957.html');
INSERT INTO `conteudo` VALUES (96, 'CIC LANÇA MOVIMENTO PELA CIDADANIA - Educação é o mote principal', '2009-05-20', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">A partir do dia 25 de maio próximo, Fortaleza fará parte do \n		<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">movimento social pela cidadania</span> que hoje contempla 14 cidades brasileiras e conta com a participação de diversos setores sociais na definição e monitoramento dos indicadores de sustentabilidade, visando a promoção da qualidade de vida dos seus habitantes. </p>\n	\n<p>A iniciativa parte do Centro Industrial do Ceará (CIC), com o lançamento, nesta data, do projeto do Observatório Social  Educação, às 8h30, no auditório Luis Esteves Neto, na Casa da Indústria. O evento contará com a presença de um dos expoentes da educação no país, o economista e articulista da revista Veja, Cláudio de Moura Castro, que falará sobre "Educação: Um olhar para o desenvolvimento". O lançamento integra a programação pelos 90 anos de fundação do CIC.</p></span>', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">Exemplo</p></span>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial"></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">"Para conhecer as ações do movimento, o CIC trouxe, no ano passado, o presidente do Instituto de Cidadania Empresarial do Maranhão e conselheiro do Observatório Social de São Luís, Ted Lago, que apresentou as ações realizadas pela entidade, que vem mensurando e divulgando os indicadores da qualidade de vida em São Luís, municiando a sociedade com informações sobre temas como educação, saúde, dinâmicas urbana e econômica, assistência social, cultura, segurança e gestão pública", explica o presidente do CIC, Robinson de Castro e Silva.</p>\n	\n<p align="justify">Reunindo grupos ecléticos (professores universitários, empresários, economistas, educadores, sociólogos etc), o CIC promoveu diversas oficinas para debater a formatação do Observatório Social. </p>\n	\n<p align="justify">"Concluímos que a educação deveria ser o ponto de partida do Observatório, uma vez que os indicadores empregados na avaliação da qualidade na educação não poderiam ser nem os que estavam sendo veiculados pelo governo, tampouco os que eram levantados pela oposição. Essa armadilha na análise dos indicadores se consolida com o tempo e dificulta muito a compreensão dos fatores críticos relacionados</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; COLOR: #ff0000; FONT-FAMILY: Arial"></span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">ao tema. Também ficou evidente que existem lacunas ou vieses específicos de comunicação que têm complicado o entendimento da população no que se refere ao papel social que a educação exerce no desenvolvimento do Estado", afirma Ruth Cunha, professora universitária e diretora do CIC. \n		\n<p align="justify">&nbsp;</p>\n		\n<p align="justify">Dentre os municípios que aderiram ao movimento pela cidadania, que visa criar uma rede de cidades sustentáveis, estão Rio de Janeiro, São Paulo, Teresópolis, Maringá, Vitória, Januária, Ilhabela, Belém, São Luís, Recife e Goiânia.</p>\n		\n<p align="justify">&nbsp;</p>\n		\n<p align="justify">&nbsp;</p>\n		<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n			\n<p align="justify">Serviço:</p></span></span></p>\n\n<p align="justify">&nbsp;</p>\n\n<p align="justify">Dentre os municípios que aderiram ao movimento pela cidadania, que visa criar uma rede de cidades sustentáveis, estão Rio de Janeiro, São Paulo, Teresópolis, Maringá, Vitória, Januária, Ilhabela, Belém, São Luís, Recife e Goiânia.</p>\n\n<p align="justify">&nbsp;</p>\n\n<p align="justify">&nbsp;</p>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">Serviço:</p></span>\n\n<p>&nbsp;</p>\n\n<p align="justify">&nbsp;</p>\n\n<p align="justify">Dentre os municípios que aderiram ao movimento pela cidadania, que visa criar uma rede de cidades sustentáveis, estão Rio de Janeiro, São Paulo, Teresópolis, Maringá, Vitória, Januária, Ilhabela, Belém, São Luís, Recife e Goiânia.</p>\n\n<p align="justify">&nbsp;</p>\n\n<p align="justify">&nbsp;</p>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">Serviço:</p></span>\n\n<p>&nbsp;</p>\n\n<p align="justify">&nbsp;</p>\n\n<p align="justify">Dentre os municípios que aderiram ao movimento pela cidadania, que visa criar uma rede de cidades sustentáveis, estão Rio de Janeiro, São Paulo, Teresópolis, Maringá, Vitória, Januária, Ilhabela, Belém, São Luís, Recife e Goiânia.</p>\n\n<p align="justify">&nbsp;</p>\n\n<p align="justify">&nbsp;</p>\n<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">\n	\n<p align="justify">Serviço:</p></span>\n<span style="FONT-SIZE: 10pt">\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Lançamento  Observatório Social - Educação</span></p>\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Palestrante: Cláudio de Moura Castro - "Educação: Um olhar para o desenvolvimento".</span></p>\n	\n<p align="justify">\n		<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Local: Auditório Luis Esteves Neto, Casa da Indústria (Av. Br. Studart, 1980)</span></p>\n	\n<p align="justify">\n		<span style="FONT-FAMILY: Arial">Data: 25 de maio  segunda-feira - 08h30</span></p></span>', 'EP', '', '');
INSERT INTO `conteudo` VALUES (97, 'Estímulo - CIC lança programa de inclusão a educação e conhecimento', '2009-05-21', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">&nbsp;</p></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O Estado ultrapassou a fase do atendimento às necessidades básicas da população para dar um salto de qualidade humana e entrar em um novo ciclo de inclusão ao conhecimento. Pelo menos esse é o entendimento do Centro Industrial do Ceará (CIC), órgão ligado à Federação das Indústrias do Estado do Ceará (Fiec). <br />\n		<br />\n		A entidade organiza o que chama de Observatório Social de Educação e pretende contar com o envolvimento da sociedade civil na difusão do conhecimento, através de redes de educação. "Vamos mobilizar famílias, igrejas, comércio, faculdades enfim, diversos níveis dos poderes públicos e privados", explicou a diretora do CIC, a professora da Universidade Estadual do Ceará, Ruth Cunha. Ela observa que a mobilização é suprapartidária e está baseada em estruturas externas e internas.</span> </p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Primeiro precisamos de ações de relacionamentos, construção de parcerias e mobilização social, que caracteriza o movimento externo. Já no âmbito interno, temos desafios de ordem administrativa e financeira", explica Cunha e completa que o programa será lançado no próximo dia 25 de maio, às 8h30, no auditório Luis Esteves Neto, na Casa da Indústria. <br />\n		<br />\n		O evento vai contar com a presença do economista Cláudio de Moura Castro, que dará palestra sobre o tema "Educação: Um olhar para o desenvolvimento". O lançamento faz parte das comemorações pelos 90 anos de fundação do CIC. <br />\n		<br />\n		"Esperamos que a ocasião também sirva para sensibilizar entidades públicas e privadas da importância do Observatório, que é de utilidade pública e tem a pretensão de discutir a qualidade de vida das pessoas", acredita Ruth Cunha. <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Mobilização</span> <br />\n		Segundo a organização do Observatório Social de Educação do Ceará, hoje existem 14 municípios com movimentos parecidos, entre eles Rio de Janeiro, São Paulo, Vitória, Belém, São Luís, Recife e Goiânia. <br />\n		<br />\n		O presidente do CIC, Robinson de Castro e Silva, diz que um bom exemplo é São Luís. "O Instituto de Cidadania Empresarial do Maranhão vem mensurando e divulgando os indicadores da qualidade de vida da cidade, municiando a sociedade com informações sobre temas como educação, saúde, dinâmicas urbanas e econômicas, assistência social, cultura, segurança e gestão pública", explica. </span><br />\n	</p>', 'CE', 'Jornal O Povo - Economia', 'http://www.opovo.com.br/opovo/economia/879132.html');
INSERT INTO `conteudo` VALUES (98, 'EDUCAÇÃO JÁ! ', '2009-05-23', '\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">\n	\n<p align="justify">Cláudio de Moura Castro, educador e articulista da Veja, dará palestra segunda-feira próxima, às 8h30min, na Fiec, durante lançamento do Observatório Social da Educação. Esse organismo, que nasce por inspiração do CIC, promete fiscalizar municípios e seus gastos nessa área. <br />\n		</p></span>\n\n<p><br />\n	</p>', '', 'CE', 'Jornal O Povo - Cidade - Col. Vertical', 'http://www.opovo.com.br/opovo/colunas/vertical/879775.html');
INSERT INTO `conteudo` VALUES (99, 'Em recuperação', '2009-05-23', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ponto de vista</span></p>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Alexsandre Lira. Analista econômico do Ipece<br />\n		</span></div>\n\n<div align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Depois dos resultados negativos da indústria cearense no final do ano passado, já é possível observar certa recuperação dessa atividade a partir do início desse ano. Desde fevereiro, a produção industrial começou a dar os primeiros sinais de retomada do seu dinamismo. Com isso, espera-se que esse ritmo de crescimento se mantenha ao longo dos próximos meses. <br />\n		</span></div>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">As vendas do comércio varejista apresentaram um balanço positivo em relação ao ano anterior, apesar da leve redução do ritmo de crescimento de alguns setores que compõem o setor. Vale destacar, que, durante os três primeiros meses do ano de 2009, foram observadas taxas de crescimento sempre positivas com ajuste sazonal. Espera-se que tal tendência se mantenha nos próximos meses principalmente, a partir do mês de julho. <br />\n		<br />\n		Um dos segmentos que mais contribuíram para o bom desempenho do Ceará foram os combustíveis e lubrificantes, decorrente do maior número de carros vendidos após a redução de IPI; alimentos e bebidas, pelo maior poder de compra da população e o segmento de materiais de construções, cujas vendas serão superiores pelo maior aquecimento previsto no setor imobiliário para o segundo semestre, com a efetivação dos programas governamentais "Minha Vida, Minha Casa". Mas, é esperado uma redução das vendas de automóveis, dado que seus preços sofrerão reajustes em decorrência do retorno da alíquota do IPI. <br />\n		<br />\n		Tendo em vista o ritmo de crescimento da atividade industrial e a expectativa de aumento da atividade turística no Estado, as expectativas para a geração de novos postos de trabalho são bastante positivas, principalmente em decorrência de fatores sazonais que apontam maior geração de postos de vagas a partir do mês de julho. <br />\n		</span></p>', 'EP', 'Jornal O Povo - Economia', 'http://www.opovo.com.br/opovo/economia/879706.html');
INSERT INTO `conteudo` VALUES (100, '90 ANOS DO CIC - Educação como indutor da inclusão social', '2009-05-23', '\n\n<div>\n	<span style="FONT-STYLE: italic; FONT-FAMILY: Arial">CIC comemora 90 anos de fundação com palestra ministrada pelo economista Cláudio de Moura Castro<br />\n		</span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></div>\n\n<div>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Pode-se dizer que passou o tempo em que a educação era medida por números. Afinal, os indicadores quantitativos não garantem a qualidade do ensino, desde a formação básica.<br />\n		Agora, a preocupação que norteia os setores da sociedade civil está ligada à forma de como o ensino é oferecido.<br />\n		Tanto que, no próximo dia 25, o Centro Industrial do Ceará apresentará o projeto "Observatório Social-Educação", que marca os 90 anos do CIC.<br />\n		Por meio dele, explica Ruth Cunha, diretora executiva do CIC, a idéia é que seja construído um espaço de observação, acompanhamento e reflexão acerca da área no Estado para que a própria sociedade, a partir da informação difundida, possa cobrar melhorias.</span></div>', '\n<span style="FONT-WEIGHT: bold; FONT-FAMILY: Arial">\n	\n<p>Modelo de gestão<br />\n		</p></span>\n\n<p align="justify">\n	<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">"Levaremos um modelo de gestão, mas não podemos dizer como será, pois o CIC não se propõe a ser executor, mas um articulador desse processo, até que o Observatório possa caminhar de forma independente", diz.<br />\n		Para isso, detalha Ruth, a proposta é que a iniciativa seja pautada no modelo com basicamente dois enfoques: a relação com o ambiente externo, com ações de parcerias, mobilização e divulgação de conhecimento; e relação com o ambiente interno, levando-se em conta o caráter organizacional. "Durante seis meses realizamos oficinas internas, consultorias para estruturar o projeto que será mostrado. No Ceará, optamos por iniciar a partir da Educação. Depois, pode-se discutir outros aspectos", antecipa.<br />\n		Robinson de Castro e Silva, presidente do CIC, ´entende que a educação é a única maneira de se fazer inclusão social de forma a fomentar a competitividade das empresas e de fortalecer a democracia´.<br />\n		<br />\n		´É um modo contemporâneo de envolver a sociedade, o poder público, a academia, e propor um pacto sobre um tema já sonhado por outras gestões, mas que avançou muito pouco´, assinala. No entanto, como salientou Ruth Cunha "o Observatório não tem o objetivo de propor mudanças".<br />\n		<br />\n		A idéia é observar o que se passa, traduzir essa visão em conhecimento específico e fazer a socialização da educação. "Pela primeira vez há a sugestão de inclusão social não pelo fator econômico, mas pela Educação", menciona.<br />\n		<br />\n		A perspectiva, conforme a diretora executiva do CIC, é que a nova entidade possa construir indicadores próprios, que apontem como o recurso está sendo gasto, como está se dando a formação. "O Observatório possibilitará uma ampliação na competitividade; melhoria da democracia, pois a população estará mais esclarecida; as pessoas se tornarão melhores, mais úteis e felizes, além da haver inclusão pela educação´, comenta.<br />\n		<br />\n		Para a apresentação do projeto é convidada toda a sociedade civil organizada e não somente pessoas ligadas à Educação. Entre os participantes, estarão representantes de universidades, faculdades, Ministério Público, Câmara Municipal e Assembléia Legislativa. Na ocasião, em que também se comemora 90 anos de fundação do CIC, uma palestra será ministrada pelo economista Cláudio de Moura Castro. A temática será "Educação: um olhar para o desenvolvimento". O evento também integra o Movimento Social pela Cidadania, que atualmente contempla 14 cidades brasileiras.<br />\n		<br />\n		Ainda nesse sentido, nos próximos dias 28 e 29, a Rede Social Brasileira por Cidades Justas e Sustentáveis, que integra o Movimento Social pela Cidadania, realizará um encontro em Recife, sendo o CIC um dos convidados. "O interessante é que há a oportunidade de criar um intercâmbio de informações, de modelos, resultados, enfim, uma troca de experiências", ressalta.<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Mais informações:</span><br />\n		Lançamento Observatório Social - Educação nos 90 anos do CIC, no dia 25 de maio, às 8h30<br />\n		Local: Casa da Indústria (Av. Barão de Studart, 1980)</span></p>', 'CE', 'jornal Diário do Nordeste - Negócios', 'http://diariodonordeste.globo.com/materia.asp?codigo=640894');
INSERT INTO `conteudo` VALUES (101, 'Iniciativa  -  Projeto busca criar novos indicadores sobre educação', '2009-05-26', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Empresários, universidades e poder público se unem para cobrar qualidade na educação básica. O Observatório Social em Educação foi lançado ontem na Casa da Indústria </span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Viviane Gonçalves<br />\n		da Redação <br />\n		</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A relação entre empresários e políticas públicas voltadas para a educação vai ficar mais próxima a partir do Observatório Social em Educação. O projeto, de iniciativa do Centro da Indústria do Ceará (CIC), foi lançado ontem, na Casa da Indústria, e contou com a participação de empresários, políticos e representantes de universidades. O evento faz parte das comemorações aos 90 anos do CIC. As ações começam a ser executadas ainda este mês. A previsão é que em 2010 a sociedade já colha reflexos do projeto. </span><br />\n	</p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O economista e articulador da revista Veja Cláudio de Moura Castro esteve presente no evento e falou sobre a importância do empresário cobrar qualidade na educação básica. "Se a educação é ruim, os empresários vão receber mão de obra desqualificada. Não adianta se interessar vagamente pela educação pública, é preciso um conjunto de atividades que façam a diferença", explica. Para o economista, o Brasil já passou da fase de discussão sobre a importância da qualidade na educação, o momento é de ação. "Há uma suposta invisibilidade de resultados na educação. O Observatório vai criar um repertório de exemplos favoráveis nesta área", afirma. <br />\n		<br />\n		Moura Castro citou o Rio Grande do Sul como exemplo de estado que teve a ação direta do empresariado na educação e teve um salto na qualidade e desenvolvimento. "Os empresários devem estar convencidos de sua influência política e usá-las em prol da educação. Infelizmente essa consciência ainda é pequena", acredita. <br />\n		<br />\n		De acordo com o presidente do CIC, Robinson Passos, todos os setores da sociedade vão se mobilizar para a execução do Observatório Social em Educação. "O CIC fará a articulação entre a iniciativa pública e privada para traçar um plano estratégico de eliminação das contradições sobre a educação", afirma Passos. <br />\n		<br />\n		As universidades terão um papel definidor na catalisação de novos indicadores sociais. O vice-reitor da Universidade Estadual do Ceará (Uece), Antônio Gomes, ressaltou a importância da educação para o desenvolvimento do Ceará. "Tudo o que é inserido de conhecimento gera um impacto muito grande nos municípios, principalmente nos menores. A universidade vai ajudar no Observatório porque tem uma inserção forte no interior do Estado", disse. <br />\n		<br />\n		Segundo o coordenador de Desenvolvimento Regional da pró-reitoria de Extensão da UFC, Fernando Xavier, o Observatório Social vai servir não apenas para a análise dos indicadores educacionais, mas também uma visão projetista. "As políticas públicas poderão ser modificadas com a criação de um novo modelo de gestão", completa. <br />\n		<br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial">NÚMEROS</span>\n		<span style="FONT-SIZE: 10pt; FONT-STYLE: italic; FONT-FAMILY: Arial"></span> <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">90</span> - ANOS ESTÁ COMEMORANDO O CENTRO DA INDÚSTRIA DO CEARÁ (CIC) </span></p>', 'CE', 'Jornal O Povo - Cidade', 'http://www.opovo.com.br/opovo/fortaleza/880350.html');
INSERT INTO `conteudo` VALUES (102, 'ECONOMISTA DEFENDE QUALIDADE - Desenvolvimento só com educação', '2009-05-26', '\n\n<p>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Usar o poder político do empresariado para interferir nos rumos da educação brasileira e impedir que as escolas de ensino básico sejam instrumento de manobra política. Mais do que discurso, o momento pede ações que visem mudar o perfil da educação no País, possibilitando um salto de qualidade do ensino brasileiro.<br />\n		<br />\n		A opinião é do economista Cláudio de Moura Castro, que ministrou, ontem na Fiec, a palestra ´Educação: um olhar para o desenvolvimento´. Além de integrar o calendário de comemorações do aniversário de 90 anos do Centro Industrial do Ceará (CIC), o evento marcou o lançamento do projeto ´Observatório Social-Educação´. Segundo o economista, os empresários são parte diretamente interessada na educação. ´Se ela é ruim, consequentemente as empresas irão receber mão-de-obra ruim´, explica Castro, para quem o Observatório, além de revelar o interesse do empresariado pela educação, é ferramenta prática de ação mais específica, mais pontual, por parte dos empresários.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">De acordo com ele, a simples atitude de um empresário de se aproximar dos gestores para exigir que a escola não seja usada para fins de interesse político fará a diferença. ´A capacidade de mobilização dos empresários para defender lobbys em Brasília, para reclamar e gritar em favor de seus interesses, precisa ser usada também para exigir e cobrar o cumprimento de metas para melhorar a qualidade da nossa educação´, propõe Castro. Segundo o economista, o Brasil deu um salto quantitativo na educação na década de 1990, ampliando o acesso do ensino básico pelo País. ´Agora é a vez de darmos um salto de qualidade. Precisamos convencer a população que está recebendo educação de péssimo nível´, observa.</span></p>', 'CE', 'jornal Diário do Nordeste - Negócios', 'http://diariodonordeste.globo.com/materia.asp?codigo=641496');
INSERT INTO `conteudo` VALUES (103, 'Observatório Social discute qualidade de vida na Educação', '2009-05-25', '\n<span style="FONT-WEIGHT: bold; FONT-SIZE: 14pt; FONT-FAMILY: Arial">\n	\n<p>Como diretrizes, o movimento tem a pretensão de fazer um acompanhamento da situação da educação na cidade</p></span>\n<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">A busca por uma educação de qualidade é uma das bandeiras do movimento social pela cidadania, presente em 14 cidades brasileiras. A partir de hoje, a iniciativa chega a Fortaleza através de um esforço do CIC (Centro Industrial do Ceará). O Observatório Social em Educação será lançado na Fiec (Federação das Indústrias do Estado do Ceará) às 8h30 com a palestra "Educação: um olhar para o desenvolvimento" do professor Cláudio de Moura Castro. A previsão é de que estejam presentes ao evento 100 pessoas entre representantes de entidades e instituições, como OAB (Ordem dos Advogados do Brasil), Assembleia Legislativa, MP (Ministério Público), além de membros da sociedade em geral.</span></p>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O Observatório pretende discutir inicialmente a Educação Básica no Estado levando em consideração o que a população entende como qualidade de vida na Educação. A diretora da CIC e coordenadora do Observatório, Ruth Cunha, explicou que a proposta é partir dos indicadores divulgados pelos Governos e pela oposição e buscar entender o que eles significam realmente e quais são as reais condições da Educação do Brasil, privilegiando a qualidade em detrimento da quantidade. "O grande diferencial é sair dessa mesmice de estar olhando sempre para os números. Temos que observar e também fazer uma tradução e uma definição dessa observação, uma análise, uma leitura dos números. O que importa é que eu compreenda", destacou.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em um momento posterior, segundo Ruth, o observatório poderá criar suas próprias estatísticas com base na compreensão de educação que a população tem. É possível também que sejam realizados seminários e oficinas, dependendo de como o debate vai se desenvolver. Ela explicou que não existe um projeto pronto para o Observatório. A proposta é que ele seja construído pela sociedade. "No momento do fazer acontecer, ele vai se modificando", argumentou. Para isso, segundo a coordenadora, é necessário ter a participação de instituições diversas e de membros da sociedade civil. "Nós queremos todos os parceiros possíveis porque esse movimento precisa nascer de maneira coletiva", completou.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">O CIC está estabelecendo parcerias com órgãos como Fiec (Federação das Indústrias do Estado do Ceará), CDL (Câmara dos Dirigentes Lojistas de Fortaleza, Sindicatos Industriais, Sebrae (Serviço de Apoio às Micro e Pequenas Empresas do Ceará) e Senai (Serviço Nacional de Aprendizagem Industrial). As Universidades Federal do Ceará (UFC) e Estadual do Ceará (Uece) também estão sendo convidadas a participar do debate.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Nesse primeiro momento, segundo Ruth, serão lançadas as bases do Observatório e sua identidade. A iniciativa conta com dois modelos de gestão. O externo vai cuidar da formação de parcerias e mobilização social e o interno vai ser responsável pelo cadastro de participantes e pela análise de pesquisas.</span></p>\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Como diretrizes, o movimento tem a pretensão de fazer um acompanhamento da situação da educação na cidade, criando indicadores de qualidade que possam ser utilizados para dar sugestões ao Poder Público de como resolver os problemas. A preocupação, de acordo com Ruth, não é somente com a Educação formal, mas é necessário também criar parcerias para se discutir Educação no trânsito, com o meio ambiente e com a sociedade de uma forma geral. "O Observatório é um instrumento que tende a ser um veículo de comunicação com a sociedade", ressaltou.</span></p>', 'CE', 'jornal O Estado - Geral', 'http://www.oestadoce.com.br/index.php?acao=noticias&subacao=ler_noticia&cadernoID=22&noticiaID=12853');
INSERT INTO `conteudo` VALUES (104, 'Os 90 anos do CIC', '2009-05-25', '\n\n<div>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Marcondes Rosa de Sousa<br />\n		</span>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></div>\n\n<div>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Casual, nosso encontro, em loja do Iguatemi: Robinson de Castro e Silva e eu. Ele me convida para os 90 anos do Cic, por ele presidido. Cláudio de Moura Castro a dar o tom, com palestra e debate, a apontar para a criação do Observatório Social em Educação. <br />\n		<br />\n		Em casa, repasso, em flashback, papel e cena dos "jovens empresários do CIC", nos anos 80. Pró-reitor de extensão da UFC, os conheci. Recebíamos, retornado do exílio político, Celso Furtado, nos Encontros Culturais (UFC e O POVO) para nos falar das perspectivas para a região nordestina. No aeroporto, os "jovens empresários do CIC" pediam-me que Celso, no vácuo de palestrante não vindo, fosse até lá. Celso, dispôs-se ... Em troca, os empresários com ele vieram para o Castelo Branco (UFC)... Esperançoso e cálido o debate... </span></div>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Em mim, dizem os marketeiros políticos, habita o "poder de resina". E foi o que senti, dias atrás, nas eleições da Adufc, entre os docentes fragmentados em sua tarefa do dar verbo à ação dos gestores e o sentimento político. E, na Rádio Universitária, o "valorizando e repensando o Nordeste", tentando recobrar sua função de "sintonia da terra"., <br />\n		<br />\n		Bordão, em mim, cena com Beni Veras. Pedia-me ele que, ghost writer, pautasse roteiro de palestra para Tasso Jereissati, então governador: "Dos ideais do CIC a uma prática de governo", Que ideais? "Tocamos de oitiva"!  disse Beni, Ao assistir à palestra, sentenciaria FHC: "Essa, a social democracia arrancada do barro do chão, Não a abstrata, que importamos da Europa"... <br />\n		<br />\n		Hoje, tempos plurais. Neles, a confusão da torre de Babel cede lugar à polissemia das pentecostais línguas-de-fogo: cabeça, o mundo intelectual; mãos, a gestão social e do estado; tato e coração, os agentes de nossa política. <br />\n		<br />\n		Essa a sintaxe a recobrarmos, construtiva, em nossa vida social e política! <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Marcondes Rosa de Sousa</span> - Professor da UFC e da Uece <br />\n		</span><a href="mailto:%20marcondesrosa@gmail.com">\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial; TEXT-DECORATION: underline">marcondesrosa@gmail.com</span>\n		<span style="FONT-SIZE: 10pt; COLOR: #0000ff; FONT-FAMILY: Arial"></span></a>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial"></span></p>', 'CE', 'jornal O Povo - Opinião - Artigo', 'http://www.opovo.com.br/opovo/opiniao/880081.html');
INSERT INTO `conteudo` VALUES (105, 'O CIC com o olhar na educação', '2009-05-25', '\n\n<div>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ruth Mattos Cunha<br />\n		</span></div>\n\n<div>\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Quem disse que educação é coisa que interessa só aos pais e professores? Quem falou que educação era problema difícil de resolver e que somente alguns poucos saberiam como fazer uma educação de qualidade? <br />\n		<br />\n		Os empresários e profissionais liberais que formam o Centro Industrial do Ceará (CIC) perceberam que a educação é assunto para ser debatido não só na escola e trouxeram esse tema para os espaços empresariais, especialmente, em relação aos reflexos que a má qualidade do ensino provoca no desenvolvimento do Ceará. </span><br />\n	</div>', '\n\n<p align="justify">\n	<span style="FONT-SIZE: 10pt; FONT-FAMILY: Arial">Debates e troca de ideias mostraram que os indicadores empregados na avaliação da qualidade na educação não poderiam ser nem os que estavam sendo veiculados pelo governo, tampouco os que eram levantados pela oposição. Essa armadilha na análise dos indicadores se consolida com o tempo e dificulta muito a compreensão dos fatores críticos relacionados ao tema. Também ficou evidente que existem vieses específicos de comunicação que têm complicado o entendimento da população no que se refere ao papel social que a educação exerce no desenvolvimento do Estado. <br />\n		<br />\n		Diante dessas constatações, veio a proposta de nos unirmos a um movimento de mobilização que tem sido articulado em rede em diversas cidades do Brasil. É um processo de articulação social e suprapartidário que contribui para o surgimento de atitudes maduras e responsáveis de cidadania e que deseja ser um canal de motivação para a promoção do cidadão nas decisões sobre assuntos que lhes dizem respeito. A educação, em decorrência de sua importância básica e fundamental, veio como primeiro desafio. <br />\n		<br />\n		A partir de oficinas internas e discussões, criamos o projeto do Observatório Social de Fortaleza, que será lançado no dia 25 de maio, com palestra do especialista em educação Cláudio de Moura Castro, "Um novo olhar para a Educação no Brasil", fundamentada em parâmetros e metas de qualidade. <br />\n		<br />\n		\n		<span style="FONT-WEIGHT: bold; FONT-SIZE: 10pt; FONT-FAMILY: Arial">Ruth Mattos Cunha </span>- Diretora do CIC e professora universitária </span></p>', 'CE', 'jornal O Povo - Opinião - Artigo', 'http://www.opovo.com.br/opovo/opiniao/880082.html');
