INSERT INTO `evento` VALUES (19, 'Ciro Gomes', '2009-06-22', '8h30 Gestão das Contas Públicas: A Visão política.Deputado Federal, Advogado e Professor Universitário, Prefeito de Fortaleza(1989-1990), Governador do Estado do Ceará (1991-1994), Ministro da Fazenda (1994-1995), Ministro da Integração Nacional (2003-2006.\n');
