INSERT INTO `nova_pagina` VALUES (3, 'Observatório da Educação do Ceará', '\n\n<div>\n	<img border="0" alt="Observatório" align="middle" src="/site/img/banco/Banner Observatório Site.jpg" /></div>\n\n<div><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 14pt; FONT-WEIGHT: bold">Missão do Observatório Social</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">A missão evidencia a finalidade ou a razão de ser da organização em um ambiente, situando o seu segmento de atuação sem miopia, de forma objetiva e atemporal. Representa os desejos ou necessidades que satisfaz quando o beneficiário final recebe ou utiliza seus serviços.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">Consiste na razão da existência da organização e na delimitação de suas atividades dentro do espaço que deseja ocupar em relação às oportunidades apresentadas pelos ambientes interno e externo.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">A missão tem a finalidade, papel ou espaço que a organização pretende ocupar na sociedade.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 12pt; FONT-WEIGHT: bold">Missão é:</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 12pt; FONT-WEIGHT: bold"></span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp; 1.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; A razão de ser da organização;<br />\n		&nbsp;&nbsp; 2.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Aquilo que justifica a sua existência;<br />\n		&nbsp;&nbsp; 3.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; É a definição sucinta e precisa da empresa; e<br />\n		&nbsp;&nbsp; 4.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; É definida com base nos fatores culturais, formada pelo conjunto de princípios da organização.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">No caso do Observatório Social, a missão instituional proposta e a ser validada pela Diretoria do CIC é a seguinte:</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt"></span>&nbsp;</div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">\n		<span style="FONT-SIZE: 12pt; FONT-WEIGHT: bold">MISSÃO DO OBSERVATÓRIO SOCIAL</span><br />\n		&nbsp;</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">Mobilizar os diversos segmentos da sociedade, em parceria com instituições públicas e privadas, para observar, sistematizar e difundir as diversas realidades, visando ao desenvolvimento econômico, social e sustentável da cidade de Fortaleza-CE.</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt"></span>&nbsp;</div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 12pt; FONT-WEIGHT: bold">Visão de Futuro do Observatório Social</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">A visão de futuro visa a busca do crescimento e perpetuação da organização através de um processo de motivação e envolvimento de todos os colaboradores.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">Deve ser tão clara e convincente que requeira pouca ou nenhuma explicação. Deve estar fora da zona de conforto. Deve exigir esforço heróico.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">Deve ser tão excitante e audaciosa que continuará a estimular o progresso, mesmo que líderes desapareçam antes que ela seja alcançada. Deve ser coerente com os princípios e a missão institucional.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">A visão de futuro representa o sonho ou os desejos pretendidos pela organização e pelas suas principais lideranças. O que se pretende alcançar em futuro estimado temporalmente.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 12pt; FONT-WEIGHT: bold">A visão de futuro:</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 12pt; FONT-WEIGHT: bold"></span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt"></span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">1.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Deve ser tão clara e convincente que requeira pouca ou nenhuma explicação;<br />\n		2.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Deve estar fora da zona de conforto. Deve exigir esforço heróico;<br />\n		3.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Deve ser tão excitante e audaciosa que continuará a estimular o progresso, mesmo que líderes desapareçam antes que ela seja alcançada; e<br />\n		4.</span> \n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Deve ser coerente com os princípios e a missão institucional.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">O futuro desejado pela(s) liderança(s) da organização sonho, valores, ideais, visão de mundo. Deve conter substancial energia, capaz de gerar compartilhamento e movimentar toda a organização em direção àquilo em que a(s) liderança(s) acredita(m).</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">Neste contexto a visão de futuro proporciona o grande delineamento do direcionamento estratégico a ser desenvolvido e implementado na organização.</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">No caso do Observatório Social, a visão de futuro proposta e a ser validada pela Diretoria do CIC é a seguinte:</span></div>\n\n<div align="justify"><br />\n	\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">\n		<span style="FONT-WEIGHT: bold">VISÃO DE FUTURO OBSERVATÓRIO SOCIAL</span><br />\n		&nbsp;</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">Ser reconhecido (*), até 2010, pela sociedade de Fortaleza-CE, como um efetivo agente de mudanças sociais, em decorrência da participação crítica e consciente dos seus cidadãos.</span></div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt"></span>&nbsp;</div>\n\n<div align="justify">\n	<span style="FONT-FAMILY: Trebuchet MS; FONT-SIZE: 10pt">(*) Ser reconhecido é atingir o nível de satisfação (8,0: escala de 1,0 a 10,0) pelos diversos segmentos da sociedade, com o seu papel de observar, sistematizar e difundir conhecimentos sobre a realidade social.<br />\n		</span></div>', 'Observatório');
