//Linha Acord
(function($){$.fn.acord=function(options){return new $.acord(options,this);};$.acord=function(options,acordion){var padrao={seletor:'.acordion',pai:'h2',filho:'div',evento:'click',sempreUm:true,autoheight:true,tempoUp:'normal',tempoDown:'normal',onAcord:null};var o=$.extend(padrao,options);if(acordion===undefined){acordion=$(o.seletor);}
return acordion.each(function(){var a=acordion;a.find(o.filho).each(function(){$(this).css("height",$(this).height()+"px").hide();});if(o.sempreUm){$(o.filho+':first',a).show();}
if(o.autoheight){var h=0;$(o.filho,this).each(function(){h=Math.max(h,$(this).outerHeight());}).height(h);a.height($(this).height()).css({overflow:'hidden'});}
if(o.evento){$(o.pai,a).bind(o.evento,function(){return showAcord($(this),a);});}
function showAcord(t,a){var n=t.next();if($.isFunction(o.onAcord)){o.onAcord.apply(t);}
$(o.filho,a).not(n).slideUp(o.tempoUp);if(o.sempreUm){n.slideDown(o.tempoDown);}else{n.slideToggle(o.tempoDown);}
return false;}});};})(jQuery);
