//Linha Focuss
(function($){$.fn.focuss=function(options){return new $.focuss(options,this);};$.focuss=function(options,elem){var padrao={seletor:'input, textarea',evento:'focus',eventoFim:'blur',cor:'red',remove:false,removeTexto:false,onFocus:null,onBlur:null};var o=$.extend(padrao,options);if(elem===undefined){elem=$(o.seletor);}
return elem.each(function(){var t=$(this),bl=t.css("border-left-color"),br=t.css("border-right-color"),bt=t.css("border-top-color"),bb=t.css("border-bottom-color"),bcor=bt+' '+br+' '+bb+' '+bl,texto=t.val();if(o.evento){t.bind(o.evento,function(){return iniciaFocuss(t);});}
if(o.eventoFim){t.bind(o.eventoFim,function(){return terminaFocuss(t,bcor);});}
function iniciaFocuss(t){if(o.remove){t.css({outline:'none'});}
if(o.removeTexto){if(t.val()==texto){t.val('');}}
t.css({borderColor:o.cor});if($.isFunction(o.onFocus)){o.onFocus.apply(t);}}
function terminaFocuss(t,cor){if(o.remove){t.css({outline:''});}
if(o.removeTexto){if(t.val()==''){t.val(texto);}}
t.css({borderColor:cor});if($.isFunction(o.onBlur)){o.onBlur.apply(t);}}});};})(jQuery);
